INSERT INTO visualization_examples (title, description, category, files, created_by) VALUES (
  '折线图示例',
  'D3.js折线图 - 展示温度趋势和股票价格走势',
  '折线图',
  '{"index.html":"<!DOCTYPE html>\\n<html lang=\\"zh-CN\\">\\n<head>\\n    <meta charset=\\"UTF-8\\">\\n    <title>D3.js 折线图</title>\\n    <script src=\\"https://d3js.org/d3.v7.min.js\\"></script>\\n    <link rel=\\"stylesheet\\" href=\\"styles.css\\">\\n</head>\\n<body>\\n    <h2>月度平均温度趋势</h2>\\n    <svg id=\\"tempChart\\" width=\\"600\\" height=\\"400\\"></svg>\\n    <h2>股票价格走势</h2>\\n    <svg id=\\"stockChart\\" width=\\"600\\" height=\\"400\\"></svg>\\n    <p>提示：鼠标悬停在数据点上可以查看详细信息。</p>\\n    <script>\\n        function initLineChart(svgId) {\\n            const svg = d3.select(`#${svgId}`);\\n            const width = +svg.attr(\\"width\\");\\n            const height = +svg.attr(\\"height\\");\\n            const margin = {top: 20, right: 30, bottom: 40, left: 50};\\n            const chartWidth = width - margin.left - margin.right;\\n            const chartHeight = height - margin.top - margin.bottom;\\n            const chartGroup = svg.append(\\"g\\").attr(\\"transform\\", `translate(${margin.left}, ${margin.top})`);\\n            const xScale = d3.scalePoint().range([0, chartWidth]).padding(0.5);\\n            const yScale = d3.scaleLinear().range([chartHeight, 0]);\\n            chartGroup.append(\\"g\\").attr(\\"class\\", \\"x-axis\\").attr(\\"transform\\", `translate(0, ${chartHeight})`);\\n            chartGroup.append(\\"g\\").attr(\\"class\\", \\"y-axis\\");\\n            const line = d3.line().x(d => xScale(d.name)).y(d => yScale(d.value)).curve(d3.curveMonotoneX);\\n            chartGroup.append(\\"path\\").attr(\\"class\\", \\"line\\");\\n            chartGroup.append(\\"g\\").attr(\\"class\\", \\"dots\\");\\n            return { svg, chartGroup, xScale, yScale, line, chartWidth, chartHeight };\\n        }\\n        function updateLineChart(chart, data, valueKey) {\\n            data.forEach(d => { d.value = +d[valueKey]; });\\n            chart.xScale.domain(data.map(d => d.name));\\n            chart.yScale.domain([d3.min(data, d => d.value) * 0.9, d3.max(data, d => d.value) * 1.1]);\\n            chart.chartGroup.select(\\".x-axis\\").call(d3.axisBottom(chart.xScale));\\n            chart.chartGroup.select(\\".y-axis\\").call(d3.axisLeft(chart.yScale));\\n            chart.chartGroup.select(\\".line\\").datum(data).attr(\\"d\\", chart.line).attr(\\"fill\\", \\"none\\").attr(\\"stroke\\", \\"steelblue\\").attr(\\"stroke-width\\", 2);\\n            const dots = chart.chartGroup.select(\\".dots\\").selectAll(\\".dot\\").data(data);\\n            dots.exit().remove();\\n            dots.enter().append(\\"circle\\").attr(\\"class\\", \\"dot\\").merge(dots).attr(\\"cx\\", d => chart.xScale(d.name)).attr(\\"cy\\", d => chart.yScale(d.value)).attr(\\"r\\", 5).attr(\\"fill\\", \\"steelblue\\");\\n        }\\n        const tempChart = initLineChart(\\"tempChart\\");\\n        const stockChart = initLineChart(\\"stockChart\\");\\n        d3.csv(\\"temperature-data.csv\\").then(data => updateLineChart(tempChart, data, \\"temperature\\"));\\n        d3.json(\\"stock-data.json\\").then(data => updateLineChart(stockChart, data, \\"price\\"));\\n    </script>\\n</body>\\n</html>","styles.css":"* { margin: 0; padding: 0; box-sizing: border-box; }\\nbody { max-width: 900px; margin: 2rem auto; padding: 0 1rem; background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%); min-height: 100vh; }\\nh2 { color: #2c3e50; margin: 2rem 0 1.5rem; text-align: center; font-weight: 600; }\\nsvg { width: 100%; max-width: 600px; height: 400px; margin: 0 auto; display: block; background: white; border-radius: 8px; box-shadow: 0 4px 6px rgba(0,0,0,0.1); }\\n.line { fill: none; stroke: steelblue; stroke-width: 2; }\\n.dot { fill: steelblue; stroke: white; stroke-width: 2; cursor: pointer; }\\np { text-align: center; margin-top: 2rem; background: white; padding: 1rem; border-radius: 8px; }","temperature-data.csv":"name,temperature\\n一月,5\\n二月,7\\n三月,12\\n四月,18\\n五月,23\\n六月,28\\n七月,32\\n八月,31\\n九月,26\\n十月,20\\n十一月,13\\n十二月,7","stock-data.json":"[{\\"name\\":\\"周一\\",\\"price\\":150},{\\"name\\":\\"周二\\",\\"price\\":155},{\\"name\\":\\"周三\\",\\"price\\":148},{\\"name\\":\\"周四\\",\\"price\\":162},{\\"name\\":\\"周五\\",\\"price\\":158},{\\"name\\":\\"周六\\",\\"price\\":165},{\\"name\\":\\"周日\\",\\"price\\":170}]"}',
  NULL
);
