  return (
    <div style={{ height: '100vh', display: 'flex', background: '#1e1e1e', color: '#d4d4d4' }}>
      {/* 侧边栏 */}
      <div style={{ width: 240, background: '#2d2d30', borderRight: '1px solid #3e3e42', padding: 16 }}>
        <div style={{ fontSize: 18, fontWeight: 'bold', marginBottom: 24 }}>教师仪表盘</div>
        <button onClick={() => setActiveTab('students')} style={{ display: 'block', width: '100%', textAlign: 'left', padding: 12, background: activeTab === 'students' ? '#3f3f46' : 'transparent', marginBottom: 8 }}>学生管理</button>
        <button onClick={() => setActiveTab('assignments')} style={{ display: 'block', width: '100%', textAlign: 'left', padding: 12, background: activeTab === 'assignments' ? '#3f3f46' : 'transparent', marginBottom: 8 }}>作业管理</button>
        <button onClick={() => setActiveTab('codes')} style={{ display: 'block', width: '100%', textAlign: 'left', padding: 12, background: activeTab === 'codes' ? '#3f3f46' : 'transparent', marginBottom: 8 }}>代码库</button>
        <button onClick={onLogout} style={{ display: 'block', width: '100%', textAlign: 'left', padding: 12, background: 'transparent', color: '#ef4444', marginTop: 32 }}>退出登录</button>
      </div>

      {/* 主内容 */}
      <div style={{ flex: 1, overflow: 'auto' }}>
        {activeTab === 'students' && <StudentManager students={data.students} setStudents={(newStudents) => setData({...data, students: newStudents})} />}
        {activeTab === 'assignments' && <AssignmentManager assignments={data.assignments} setAssignments={(newAssigns) => setData({...data, assignments: newAssigns})} codeRepository={data.codeRepository} />}
        {activeTab === 'codes' && <CodeRepo codeRepository={data.codeRepository} setCodeRepository={(newCodes) => setData({...data, codeRepository: newCodes})} />}
      </div>
    </div>
  );
}

// ==========================================
// 5. 学生仪表盘
// ==========================================
const StudentDashboard = ({ user, data, onOpenEditor, onLogout }) => {
  const assignments = data.assignments.filter(a => a.targetClass === '所有班级' || a.targetClass === user.class);

  return (
    <div style={{ height: '100vh', background: '#1e1e1e', color: '#d4d4d4', padding: 32 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 32 }}>
        <h1>欢迎, {user.name} ({user.class})</h1>
        <button onClick={onLogout} style={{ background: '#ef4444', color: '#fff', padding: '8px 16px', border: 'none', borderRadius: 4 }}>退出登录</button>
      </div>

      <h2 style={{ marginBottom: 16 }}>我的作业</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: 24 }}>
        {assignments.map(assign => {
          const sub = data.submissions.find(s => s.studentId === user.id && s.assignmentId === assign.id);
          return (
            <div key={assign.id} style={{ background: '#2d2d30', padding: 24, borderRadius: 8 }}>
               <h3>{assign.title}</h3>
               <p>{assign.requirements}</p>
               <p>截止: {assign.deadline}</p>
               <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                 <span style={{ color: sub ? '#16a34a' : '#ea580c', fontWeight: 'bold' }}>{sub ? '✅ 已提交' : '⏳ 待完成'}</span>
                 <button 
                   onClick={() => onOpenEditor({
                     mode: 'student_work',
                     initialFiles: sub ? sub.files : assign.template, // 加载提交过的 或 默认模板
                     projectName: assign.title,
                     assignmentId: assign.id // 新增：传递 assignmentId 用于提交
                   })}
                   style={{ padding: '10px 20px', background: sub ? '#0e639c' : '#ea580c', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                 >
                   {sub ? '修改作业' : '开始作业'}
                 </button>
               </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

// ==========================================
// 6. 主入口
// ==========================================
function App() {
  const [data, setData] = useState(INITIAL_DATA);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('login');
  const [editorConfig, setEditorConfig] = useState(null);
  const [username, setUsername] = useState(''); // 新增：用户名输入
  const [password, setPassword] = useState(''); // 新增：密码输入

  const handleLogin = () => {
    if (!username || !password) {
      alert('请输入用户名和密码');
      return;
    }

    // 简单验证逻辑（示例）
    // 教师: username='teacher', password='teacher'
    // 学生: username=学号 (如's1'), password=任意 (演示用)
    if (username === 'teacher' && password === 'teacher') {
      setUser({ role: 'teacher', name: '教师' });
      setView('dashboard');
    } else {
      const s = data.students.find(stu => stu.id === username);
      if (s) {
        // 学生密码验证（演示中不检查密码，实际应添加）
        setUser({ ...s, role: 'student' });
        setView('dashboard');
      } else {
        alert('用户名或密码错误');
      }
    }
  };

  // 处理提交 (从 Editor 回调)
  const handleSubmission = (selectedFiles) => {
    // 修改：现在接收 selectedFiles 而非全部 files
    const assign = data.assignments.find(a => a.id === editorConfig.assignmentId); // 使用传递的 assignmentId
    if(assign && user.role === 'student') {
       const newSub = {
         id: `sub${Date.now()}`,
         studentId: user.id,
         assignmentId: assign.id,
         files: selectedFiles, // 只保存选中的文件
         timestamp: Date.now()
       };
       const others = data.submissions.filter(s => !(s.studentId === user.id && s.assignmentId === assign.id));
       setData(prev => ({ ...prev, submissions: [...others, newSub] }));
       alert('作业已提交！');
       setView('dashboard');
    }
  };

  if (view === 'editor') {
    return (
      <FullEditor 
        {...editorConfig}
        onBack={() => setView('dashboard')}
        onSubmit={handleSubmission} // 传递修改后的 handleSubmission
      />
    );
  }

  if (view === 'dashboard') {
    return user.role === 'teacher' ? (
      <TeacherDashboard 
        data={data} 
        setData={setData} 
        onOpenEditor={(config) => { setEditorConfig(config); setView('editor'); }}
        onLogout={() => setView('login')} 
      />
    ) : (
      <StudentDashboard 
        user={user} 
        data={data} 
        onOpenEditor={(config) => { setEditorConfig(config); setView('editor'); }}
        onLogout={() => setView('login')} 
      />
    );
  }

  // 修改后的登录界面（更简约配色）
  return (
    <div style={{
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      background: 'linear-gradient(180deg, #f8f9fa 0%, #ffffff 100%)' // 浅灰白渐变背景
    }}>
      <div style={{
        background: '#ffffff', // 白色表单背景
        padding: '40px 60px',
        borderRadius: '8px',
        width: '300px',
        textAlign: 'center',
        boxShadow: '0 4px 12px rgba(0,0,0,0.1)' // 轻微阴影
      }}>
        <div style={{ color: '#495057', fontSize: '16px', marginBottom: '16px' }}>用户名</div>
        <input 
          type="text" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          placeholder="工号或学号" 
          style={{
            width: '100%',
            padding: '12px',
            marginBottom: '24px',
            background: '#ffffff',
            border: '1px solid #ced4da', // 浅灰边框
            borderRadius: '4px',
            fontSize: '16px'
          }} 
        />
        <div style={{ color: '#495057', fontSize: '16px', marginBottom: '16px' }}>密码</div>
        <input 
          type="password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          placeholder="请输入密码" 
          style={{
            width: '100%',
            padding: '12px',
            marginBottom: '32px',
            background: '#ffffff',
            border: '1px solid #ced4da', // 浅灰边框
            borderRadius: '4px',
            fontSize: '16px'
          }} 
        />
        <button 
          onClick={handleLogin} 
          style={{
            width: '100%',
            padding: '16px',
            background: '#0d6efd', // 浅蓝按钮
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            fontSize: '18px',
            fontWeight: 'bold',
            cursor: 'pointer'
          }}
        >
          登录
        </button>
      </div>
    </div>
  );
}

export default App;
