import React, { useState, useEffect, useRef } from 'react';
import { Plus, Edit, Trash2, Video, Code, BookOpen, Save, X } from 'lucide-react';

const AnchorEditor = ({ coursewareId, slides, onClose }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [anchors, setAnchors] = useState([]);
  const [showAnchorForm, setShowAnchorForm] = useState(false);
  const [editingAnchor, setEditingAnchor] = useState(null);
  const [anchorForm, setAnchorForm] = useState({
    anchor_name: '',
    description: '',
    x_position: 50,
    y_position: 50
  });
  const [showResourceForm, setShowResourceForm] = useState(false);
  const [resourceType, setResourceType] = useState('video');
  const [resourceForm, setResourceForm] = useState({
    title: '',
    description: '',
    resource_content: '',
    resource_url: ''
  });
  const [selectedAnchorId, setSelectedAnchorId] = useState(null);
  const [loading, setLoading] = useState(false);
  
  const slideRef = useRef(null);

  // 加载当前幻灯片的锚点
  useEffect(() => {
    loadAnchors();
  }, [currentSlide, coursewareId]);

  const loadAnchors = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/courseware/${coursewareId}/slide/${currentSlide + 1}/anchors`
      );
      if (response.ok) {
        const data = await response.json();
        setAnchors(data);
      }
    } catch (error) {
      console.error('加载锚点失败:', error);
    }
  };

  // 在幻灯片上点击添加锚点
  const handleSlideClick = (e) => {
    if (showAnchorForm || editingAnchor) return;
    
    const rect = slideRef.current.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;
    
    setAnchorForm({
      anchor_name: '',
      description: '',
      x_position: Math.round(x * 100) / 100,
      y_position: Math.round(y * 100) / 100
    });
    setShowAnchorForm(true);
    setEditingAnchor(null);
  };

  // 保存锚点
  const saveAnchor = async () => {
    if (!anchorForm.anchor_name.trim()) {
      alert('请输入锚点名称');
      return;
    }

    setLoading(true);
    try {
      const url = editingAnchor 
        ? `http://localhost:5000/api/anchors/${editingAnchor.id}`
        : 'http://localhost:5000/api/anchors';
      
      const method = editingAnchor ? 'PUT' : 'POST';
      const body = editingAnchor 
        ? anchorForm
        : { ...anchorForm, courseware_id: coursewareId, slide_number: currentSlide + 1 };

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
      });

      if (response.ok) {
        await loadAnchors();
        setShowAnchorForm(false);
        setEditingAnchor(null);
        setAnchorForm({
          anchor_name: '',
          description: '',
          x_position: 50,
          y_position: 50
        });
        alert(editingAnchor ? '锚点更新成功' : '锚点创建成功');
      } else {
        const error = await response.json();
        alert('保存失败: ' + error.error);
      }
    } catch (error) {
      console.error('保存锚点失败:', error);
      alert('保存失败，请重试');
    } finally {
      setLoading(false);
    }
  };

  // 编辑锚点
  const editAnchor = (anchor) => {
    setAnchorForm({
      anchor_name: anchor.anchor_name,
      description: anchor.description || '',
      x_position: anchor.x_position,
      y_position: anchor.y_position
    });
    setEditingAnchor(anchor);
    setShowAnchorForm(true);
  };

  // 删除锚点
  const deleteAnchor = async (anchorId) => {
    if (!confirm('确定要删除这个锚点吗？这将同时删除所有关联的资源。')) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/anchors/${anchorId}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        await loadAnchors();
        alert('锚点删除成功');
      } else {
        const error = await response.json();
        alert('删除失败: ' + error.error);
      }
    } catch (error) {
      console.error('删除锚点失败:', error);
      alert('删除失败，请重试');
    }
  };

  // 添加资源
  const addResource = async () => {
    if (!resourceForm.title.trim()) {
      alert('请输入资源标题');
      return;
    }

    if (resourceType === 'code' || resourceType === 'syntax') {
      if (!resourceForm.resource_content.trim()) {
        alert('请输入内容');
        return;
      }
    }

    if (resourceType === 'video' && !resourceForm.resource_url.trim()) {
      alert('请输入视频链接或上传视频文件');
      return;
    }

    setLoading(true);
    try {
      const url = `http://localhost:5000/api/anchors/${selectedAnchorId}/resources/${resourceType}`;
      
      const response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(resourceForm)
      });

      if (response.ok) {
        setShowResourceForm(false);
        setResourceForm({
          title: '',
          description: '',
          resource_content: '',
          resource_url: ''
        });
        alert('资源添加成功');
        await loadAnchors(); // 重新加载以更新资源计数
      } else {
        const error = await response.json();
        alert('添加失败: ' + error.error);
      }
    } catch (error) {
      console.error('添加资源失败:', error);
      alert('添加失败，请重试');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.8)',
      zIndex: 1000,
      display: 'flex'
    }}>
      {/* 左侧幻灯片区域 */}
      <div style={{
        flex: 1,
        padding: '20px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
      }}>
        <div style={{
          backgroundColor: 'white',
          borderRadius: '8px',
          padding: '20px',
          maxWidth: '800px',
          width: '100%'
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '20px'
          }}>
            <h3>锚点编辑器 - 第 {currentSlide + 1} 页</h3>
            <button onClick={onClose} style={{
              background: 'none',
              border: 'none',
              fontSize: '24px',
              cursor: 'pointer'
            }}>
              <X size={24} />
            </button>
          </div>

          {/* 幻灯片导航 */}
          <div style={{
            display: 'flex',
            gap: '10px',
            marginBottom: '20px',
            flexWrap: 'wrap'
          }}>
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentSlide(index)}
                style={{
                  padding: '8px 12px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  backgroundColor: currentSlide === index ? '#007bff' : 'white',
                  color: currentSlide === index ? 'white' : 'black',
                  cursor: 'pointer'
                }}
              >
                {index + 1}
              </button>
            ))}
          </div>

          {/* 幻灯片显示区域 */}
          <div style={{
            position: 'relative',
            border: '2px solid #ddd',
            borderRadius: '8px',
            overflow: 'hidden',
            cursor: showAnchorForm || editingAnchor ? 'default' : 'crosshair'
          }}>
            <img
              ref={slideRef}
              src={slides[currentSlide]}
              alt={`幻灯片 ${currentSlide + 1}`}
              style={{
                width: '100%',
                height: 'auto',
                display: 'block'
              }}
              onClick={handleSlideClick}
            />
            
            {/* 显示锚点 */}
            {anchors.map((anchor) => (
              <div
                key={anchor.id}
                style={{
                  position: 'absolute',
                  left: `${anchor.x_position}%`,
                  top: `${anchor.y_position}%`,
                  transform: 'translate(-50%, -50%)',
                  backgroundColor: '#ff4444',
                  color: 'white',
                  padding: '4px 8px',
                  borderRadius: '12px',
                  fontSize: '12px',
                  cursor: 'pointer',
                  zIndex: 10,
                  display: 'flex',
                  alignItems: 'center',
                  gap: '4px',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.3)'
                }}
                onClick={(e) => {
                  e.stopPropagation();
                  editAnchor(anchor);
                }}
                title={`${anchor.anchor_name} (${anchor.resource_count} 个资源)`}
              >
                <span>{anchor.anchor_name}</span>
                <span style={{
                  backgroundColor: 'rgba(255,255,255,0.3)',
                  borderRadius: '8px',
                  padding: '1px 4px',
                  fontSize: '10px'
                }}>
                  {anchor.resource_count}
                </span>
              </div>
            ))}
          </div>

          <div style={{ marginTop: '10px', fontSize: '14px', color: '#666' }}>
            点击幻灯片添加锚点，点击现有锚点进行编辑
          </div>
        </div>
      </div>

      {/* 右侧控制面板 */}
      <div style={{
        width: '400px',
        backgroundColor: 'white',
        padding: '20px',
        overflowY: 'auto'
      }}>
        <h4>当前幻灯片锚点</h4>
        
        {anchors.length === 0 ? (
          <p style={{ color: '#666', fontStyle: 'italic' }}>
            暂无锚点，点击幻灯片添加
          </p>
        ) : (
          <div style={{ marginBottom: '20px' }}>
            {anchors.map((anchor) => (
              <div key={anchor.id} style={{
                border: '1px solid #ddd',
                borderRadius: '8px',
                padding: '12px',
                marginBottom: '10px'
              }}>
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  marginBottom: '8px'
                }}>
                  <strong>{anchor.anchor_name}</strong>
                  <div style={{ display: 'flex', gap: '5px' }}>
                    <button
                      onClick={() => {
                        setSelectedAnchorId(anchor.id);
                        setShowResourceForm(true);
                      }}
                      style={{
                        padding: '4px 8px',
                        border: '1px solid #28a745',
                        borderRadius: '4px',
                        backgroundColor: 'white',
                        color: '#28a745',
                        cursor: 'pointer',
                        fontSize: '12px'
                      }}
                      title="添加资源"
                    >
                      <Plus size={12} />
                    </button>
                    <button
                      onClick={() => editAnchor(anchor)}
                      style={{
                        padding: '4px 8px',
                        border: '1px solid #007bff',
                        borderRadius: '4px',
                        backgroundColor: 'white',
                        color: '#007bff',
                        cursor: 'pointer',
                        fontSize: '12px'
                      }}
                      title="编辑锚点"
                    >
                      <Edit size={12} />
                    </button>
                    <button
                      onClick={() => deleteAnchor(anchor.id)}
                      style={{
                        padding: '4px 8px',
                        border: '1px solid #dc3545',
                        borderRadius: '4px',
                        backgroundColor: 'white',
                        color: '#dc3545',
                        cursor: 'pointer',
                        fontSize: '12px'
                      }}
                      title="删除锚点"
                    >
                      <Trash2 size={12} />
                    </button>
                  </div>
                </div>
                {anchor.description && (
                  <p style={{ fontSize: '12px', color: '#666', margin: '4px 0' }}>
                    {anchor.description}
                  </p>
                )}
                <div style={{ fontSize: '12px', color: '#888' }}>
                  位置: ({anchor.x_position}%, {anchor.y_position}%) | 
                  资源: {anchor.resource_count} 个
                </div>
              </div>
            ))}
          </div>
        )}

        {/* 锚点表单 */}
        {showAnchorForm && (
          <div style={{
            border: '2px solid #007bff',
            borderRadius: '8px',
            padding: '15px',
            backgroundColor: '#f8f9fa'
          }}>
            <h5>{editingAnchor ? '编辑锚点' : '新建锚点'}</h5>
            
            <div style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                锚点名称 *
              </label>
              <input
                type="text"
                value={anchorForm.anchor_name}
                onChange={(e) => setAnchorForm({
                  ...anchorForm,
                  anchor_name: e.target.value
                })}
                placeholder="例如：CSS盒模型"
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  fontSize: '14px'
                }}
              />
            </div>

            <div style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                描述
              </label>
              <textarea
                value={anchorForm.description}
                onChange={(e) => setAnchorForm({
                  ...anchorForm,
                  description: e.target.value
                })}
                placeholder="详细描述这个知识点..."
                rows={3}
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  fontSize: '14px',
                  resize: 'vertical'
                }}
              />
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: '1fr 1fr',
              gap: '10px',
              marginBottom: '15px'
            }}>
              <div>
                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                  X坐标 (%)
                </label>
                <input
                  type="number"
                  min="0"
                  max="100"
                  step="0.1"
                  value={anchorForm.x_position}
                  onChange={(e) => setAnchorForm({
                    ...anchorForm,
                    x_position: parseFloat(e.target.value)
                  })}
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    fontSize: '14px'
                  }}
                />
              </div>
              <div>
                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                  Y坐标 (%)
                </label>
                <input
                  type="number"
                  min="0"
                  max="100"
                  step="0.1"
                  value={anchorForm.y_position}
                  onChange={(e) => setAnchorForm({
                    ...anchorForm,
                    y_position: parseFloat(e.target.value)
                  })}
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    fontSize: '14px'
                  }}
                />
              </div>
            </div>

            <div style={{ display: 'flex', gap: '10px' }}>
              <button
                onClick={saveAnchor}
                disabled={loading}
                style={{
                  flex: 1,
                  padding: '10px',
                  backgroundColor: '#007bff',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: loading ? 'not-allowed' : 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '5px'
                }}
              >
                <Save size={16} />
                {loading ? '保存中...' : '保存'}
              </button>
              <button
                onClick={() => {
                  setShowAnchorForm(false);
                  setEditingAnchor(null);
                }}
                style={{
                  padding: '10px 15px',
                  backgroundColor: '#6c757d',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                取消
              </button>
            </div>
          </div>
        )}

        {/* 资源表单 */}
        {showResourceForm && (
          <div style={{
            border: '2px solid #28a745',
            borderRadius: '8px',
            padding: '15px',
            backgroundColor: '#f8fff8',
            marginTop: '20px'
          }}>
            <h5>添加资源</h5>
            
            <div style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                资源类型
              </label>
              <select
                value={resourceType}
                onChange={(e) => setResourceType(e.target.value)}
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  fontSize: '14px'
                }}
              >
                <option value="video">视频资源</option>
                <option value="code">代码示例</option>
                <option value="syntax">语法说明</option>
              </select>
            </div>

            <div style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                标题 *
              </label>
              <input
                type="text"
                value={resourceForm.title}
                onChange={(e) => setResourceForm({
                  ...resourceForm,
                  title: e.target.value
                })}
                placeholder="资源标题"
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  fontSize: '14px'
                }}
              />
            </div>

            {resourceType === 'video' && (
              <div style={{ marginBottom: '10px' }}>
                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                  视频链接 (B站等)
                </label>
                <input
                  type="url"
                  value={resourceForm.resource_url}
                  onChange={(e) => setResourceForm({
                    ...resourceForm,
                    resource_url: e.target.value
                  })}
                  placeholder="https://www.bilibili.com/video/..."
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    fontSize: '14px'
                  }}
                />
              </div>
            )}

            {(resourceType === 'code' || resourceType === 'syntax') && (
              <div style={{ marginBottom: '10px' }}>
                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                  {resourceType === 'code' ? '代码内容 *' : '语法说明 *'}
                </label>
                <textarea
                  value={resourceForm.resource_content}
                  onChange={(e) => setResourceForm({
                    ...resourceForm,
                    resource_content: e.target.value
                  })}
                  placeholder={resourceType === 'code' ? '输入代码...' : '输入语法说明...'}
                  rows={6}
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #ddd',
                    borderRadius: '4px',
                    fontSize: '14px',
                    fontFamily: resourceType === 'code' ? 'monospace' : 'inherit',
                    resize: 'vertical'
                  }}
                />
              </div>
            )}

            <div style={{ marginBottom: '15px' }}>
              <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px' }}>
                描述
              </label>
              <textarea
                value={resourceForm.description}
                onChange={(e) => setResourceForm({
                  ...resourceForm,
                  description: e.target.value
                })}
                placeholder="资源描述..."
                rows={2}
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  fontSize: '14px',
                  resize: 'vertical'
                }}
              />
            </div>

            <div style={{ display: 'flex', gap: '10px' }}>
              <button
                onClick={addResource}
                disabled={loading}
                style={{
                  flex: 1,
                  padding: '10px',
                  backgroundColor: '#28a745',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: loading ? 'not-allowed' : 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '5px'
                }}
              >
                {resourceType === 'video' && <Video size={16} />}
                {resourceType === 'code' && <Code size={16} />}
                {resourceType === 'syntax' && <BookOpen size={16} />}
                {loading ? '添加中...' : '添加资源'}
              </button>
              <button
                onClick={() => {
                  setShowResourceForm(false);
                  setResourceForm({
                    title: '',
                    description: '',
                    resource_content: '',
                    resource_url: ''
                  });
                }}
                style={{
                  padding: '10px 15px',
                  backgroundColor: '#6c757d',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                取消
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AnchorEditor;
