import React, { useState, useEffect } from 'react';
import { Play, FolderPlus, FilePlus, RefreshCw, Save, Trash2, FileCode, Folder, ChevronRight, ChevronDown, Download, Upload } from 'lucide-react';

const TEMPLATES = {
  'HTML基础': `<!DOCTYPE html>
<html lang="zh-CN">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>我的页面</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      margin: 20px;
    }
  </style>
</head>
<body>
  <h1>Hello World!</h1>
  <p>这是一个基础的HTML模板</p>
  <script>
    console.log('页面加载完成');
  </script>
</body>
</html>`,
  'React组件': `import React, { useState } from 'react';

function Counter() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <h2>计数器: {count}</h2>
      <button onClick={() => setCount(count + 1)}>
        增加
      </button>
    </div>
  );
}

export default Counter;`,
  'JavaScript函数': `// 示例函数
function greet(name) {
  return \`你好, \${name}!\`;
}

// 数组操作
const numbers = [1, 2, 3, 4, 5];
const doubled = numbers.map(n => n * 2);

console.log(greet('世界'));
console.log('翻倍后的数组:', doubled);`,
  'CSS样式': `/* 基础样式 */
body {
  margin: 0;
  padding: 0;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
}

.card {
  background: white;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 20px;
  transition: transform 0.3s;
}

.card:hover {
  transform: translateY(-5px);
}`
};

export default function OnlineJSEditor() {
  const [projects, setProjects] = useState([
    { id: '1', name: '默认项目', files: [] }
  ]);
  const [currentProject, setCurrentProject] = useState('1');
  const [files, setFiles] = useState({});
  const [currentFile, setCurrentFile] = useState(null);
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [expandedFolders, setExpandedFolders] = useState({});
  const [showTemplates, setShowTemplates] = useState(false);

  const getCurrentProject = () => projects.find(p => p.id === currentProject);

  const addProject = () => {
    const name = prompt('输入项目名称:');
    if (name) {
      const newProject = {
        id: Date.now().toString(),
        name,
        files: []
      };
      setProjects([...projects, newProject]);
      setCurrentProject(newProject.id);
    }
  };

  const deleteProject = (id) => {
    if (projects.length === 1) {
      alert('至少保留一个项目');
      return;
    }
    if (confirm('确定删除此项目吗?')) {
      setProjects(projects.filter(p => p.id !== id));
      if (currentProject === id) {
        setCurrentProject(projects[0].id);
      }
    }
  };

  const addFile = (parentPath = '') => {
    const name = prompt('输入文件名 (如: index.html, script.js):');
    if (name) {
      const project = getCurrentProject();
      const filePath = parentPath ? `${parentPath}/${name}` : name;
      const newFile = {
        id: Date.now().toString(),
        name,
        path: filePath,
        type: 'file',
        parentPath
      };

      setProjects(projects.map(p => 
        p.id === currentProject 
          ? { ...p, files: [...p.files, newFile] }
          : p
      ));
      setFiles({ ...files, [filePath]: '' });
      setCurrentFile(filePath);
      setCode('');
    }
  };

  const addFolder = (parentPath = '') => {
    const name = prompt('输入文件夹名称:');
    if (name) {
      const project = getCurrentProject();
      const folderPath = parentPath ? `${parentPath}/${name}` : name;
      const newFolder = {
        id: Date.now().toString(),
        name,
        path: folderPath,
        type: 'folder',
        parentPath
      };

      setProjects(projects.map(p => 
        p.id === currentProject 
          ? { ...p, files: [...p.files, newFolder] }
          : p
      ));
      setExpandedFolders({ ...expandedFolders, [folderPath]: true });
    }
  };

  const deleteFile = (filePath) => {
    if (confirm('确定删除吗?')) {
      const project = getCurrentProject();
      setProjects(projects.map(p => 
        p.id === currentProject 
          ? { ...p, files: p.files.filter(f => f.path !== filePath && !f.path.startsWith(filePath + '/')) }
          : p
      ));

      const newFiles = { ...files };
      delete newFiles[filePath];
      setFiles(newFiles);

      if (currentFile === filePath) {
        setCurrentFile(null);
        setCode('');
      }
    }
  };

  const saveFile = () => {
    if (currentFile) {
      setFiles({ ...files, [currentFile]: code });
      alert('文件已保存!');
    }
  };

  const openFile = (filePath) => {
    setCurrentFile(filePath);
    setCode(files[filePath] || '');
  };

  const toggleFolder = (folderPath) => {
    setExpandedFolders({
      ...expandedFolders,
      [folderPath]: !expandedFolders[folderPath]
    });
  };

  const runCode = () => {
    try {
      // 清空之前的输出
      let consoleOutput = [];

      // 重写console.log
      const originalLog = console.log;
      console.log = (...args) => {
        consoleOutput.push(args.map(arg => 
          typeof arg === 'object' ? JSON.stringify(arg, null, 2) : String(arg)
        ).join(' '));
        originalLog(...args);
      };

      // 如果是HTML文件，在iframe中运行
      if (currentFile && currentFile.endsWith('.html')) {
        setOutput(`<iframe srcdoc="${code.replace(/"/g, '&quot;')}" style="width:100%;height:100%;border:none;"></iframe>`);
      } else {
        // 执行JavaScript代码
        eval(code);
        setOutput(consoleOutput.length > 0 ? consoleOutput.join('\n') : '代码执行成功，无输出');
      }

      // 恢复console.log
      console.log = originalLog;
    } catch (error) {
      setOutput(`错误: ${error.message}`);
    }
  };

  const refresh = () => {
    setOutput('');
    alert('编辑器已刷新');
  };

  const useTemplate = (templateName) => {
    if (currentFile) {
      setCode(TEMPLATES[templateName]);
      setFiles({ ...files, [currentFile]: TEMPLATES[templateName] });
      setShowTemplates(false);
    } else {
      alert('请先创建或选择一个文件');
    }
  };

  const renderFileTree = (parentPath = '') => {
    const project = getCurrentProject();
    if (!project) return null;

    const items = project.files.filter(f => f.parentPath === parentPath);

    return items.map(item => (
      <div key={item.id} style={{ marginLeft: parentPath ? '20px' : '0' }}>
        {item.type === 'folder' ? (
          <div>
            <div
              onClick={() => toggleFolder(item.path)}
              style={{
                display: 'flex',
                alignItems: 'center',
                padding: '6px 8px',
                cursor: 'pointer',
                borderRadius: '4px',
                transition: 'background 0.2s'
              }}
              onMouseEnter={(e) => e.currentTarget.style.background = '#f0f0f0'}
              onMouseLeave={(e) => e.currentTarget.style.background = 'transparent'}
            >
              {expandedFolders[item.path] ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
              <Folder size={16} style={{ margin: '0 6px', color: '#f59e0b' }} />
              <span style={{ fontSize: '14px' }}>{item.name}</span>
              <button
                onClick={(e) => { e.stopPropagation(); deleteFile(item.path); }}
                style={{
                  marginLeft: 'auto',
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '4px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <Trash2 size={14} color="#ef4444" />
              </button>
            </div>
            {expandedFolders[item.path] && renderFileTree(item.path)}
          </div>
        ) : (
          <div
            onClick={() => openFile(item.path)}
            style={{
              display: 'flex',
              alignItems: 'center',
              padding: '6px 8px',
              cursor: 'pointer',
              borderRadius: '4px',
              background: currentFile === item.path ? '#e0f2fe' : 'transparent',
              transition: 'background 0.2s'
            }}
            onMouseEnter={(e) => {
              if (currentFile !== item.path) e.currentTarget.style.background = '#f0f0f0';
            }}
            onMouseLeave={(e) => {
              if (currentFile !== item.path) e.currentTarget.style.background = 'transparent';
            }}
          >
            <FileCode size={16} style={{ margin: '0 6px', color: '#3b82f6' }} />
            <span style={{ fontSize: '14px', flex: 1 }}>{item.name}</span>
            <button
              onClick={(e) => { e.stopPropagation(); deleteFile(item.path); }}
              style={{
                background: 'none',
                border: 'none',
                cursor: 'pointer',
                padding: '4px',
                display: 'flex',
                alignItems: 'center'
              }}
            >
              <Trash2 size={14} color="#ef4444" />
            </button>
          </div>
        )}
      </div>
    ));
  };

  return (
    <div style={{ display: 'flex', height: '100vh', background: '#1e1e1e', color: '#fff', fontFamily: 'Arial, sans-serif' }}>
      {/* 侧边栏 */}
      <div style={{ width: '280px', background: '#252526', borderRight: '1px solid #3e3e42', display: 'flex', flexDirection: 'column' }}>
        {/* 项目管理 */}
        <div style={{ padding: '12px', borderBottom: '1px solid #3e3e42' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
            <select
              value={currentProject}
              onChange={(e) => setCurrentProject(e.target.value)}
              style={{
                flex: 1,
                padding: '6px',
                background: '#3c3c3c',
                color: '#fff',
                border: '1px solid #555',
                borderRadius: '4px',
                fontSize: '13px'
              }}
            >
              {projects.map(p => (
                <option key={p.id} value={p.id}>{p.name}</option>
              ))}
            </select>
            <button
              onClick={addProject}
              style={{
                marginLeft: '8px',
                padding: '6px 10px',
                background: '#0e639c',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '12px'
              }}
            >
              新建
            </button>
          </div>
          {projects.length > 1 && (
            <button
              onClick={() => deleteProject(currentProject)}
              style={{
                width: '100%',
                padding: '6px',
                background: '#c92a2a',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '12px'
              }}
            >
              删除当前项目
            </button>
          )}
        </div>

        {/* 工具栏 */}
        <div style={{ padding: '12px', borderBottom: '1px solid #3e3e42', display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '8px' }}>
          <button onClick={() => addFile()} style={btnStyle}>
            <FilePlus size={16} /> 新建文件
          </button>
          <button onClick={() => addFolder()} style={btnStyle}>
            <FolderPlus size={16} /> 新建文件夹
          </button>
          <button onClick={refresh} style={btnStyle}>
            <RefreshCw size={16} /> 刷新
          </button>
          <button onClick={() => setShowTemplates(!showTemplates)} style={btnStyle}>
            <FileCode size={16} /> 模板
          </button>
        </div>

        {/* 模板列表 */}
        {showTemplates && (
          <div style={{ padding: '12px', borderBottom: '1px solid #3e3e42', background: '#2d2d30' }}>
            <div style={{ fontSize: '13px', fontWeight: 'bold', marginBottom: '8px' }}>选择模板:</div>
            {Object.keys(TEMPLATES).map(name => (
              <button
                key={name}
                onClick={() => useTemplate(name)}
                style={{
                  width: '100%',
                  padding: '8px',
                  marginBottom: '4px',
                  background: '#3c3c3c',
                  color: '#fff',
                  border: '1px solid #555',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  textAlign: 'left',
                  fontSize: '12px'
                }}
              >
                {name}
              </button>
            ))}
          </div>
        )}

        {/* 文件树 */}
        <div style={{ flex: 1, overflow: 'auto', padding: '12px' }}>
          <div style={{ fontSize: '13px', fontWeight: 'bold', marginBottom: '8px', color: '#ccc' }}>
            文件资源管理器
          </div>
          {renderFileTree()}
        </div>
      </div>

      {/* 编辑器区域 */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* 顶部工具栏 */}
        <div style={{ padding: '12px', background: '#2d2d30', borderBottom: '1px solid #3e3e42', display: 'flex', gap: '8px', alignItems: 'center' }}>
          <span style={{ fontSize: '14px', color: '#ccc' }}>
            {currentFile || '未选择文件'}
          </span>
          <div style={{ marginLeft: 'auto', display: 'flex', gap: '8px' }}>
            <button onClick={saveFile} disabled={!currentFile} style={{ ...actionBtnStyle, background: currentFile ? '#0e639c' : '#555' }}>
              <Save size={16} /> 保存
            </button>
            <button onClick={runCode} disabled={!currentFile} style={{ ...actionBtnStyle, background: currentFile ? '#16a34a' : '#555' }}>
              <Play size={16} /> 运行
            </button>
          </div>
        </div>

        {/* 编辑器和输出 */}
        <div style={{ flex: 1, display: 'flex' }}>
          {/* 代码编辑器 */}
          <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
            <textarea
              value={code}
              onChange={(e) => setCode(e.target.value)}
              placeholder="在左侧创建或选择文件开始编码..."
              style={{
                flex: 1,
                padding: '16px',
                background: '#1e1e1e',
                color: '#d4d4d4',
                border: 'none',
                fontFamily: 'Consolas, Monaco, "Courier New", monospace',
                fontSize: '14px',
                lineHeight: '1.6',
                resize: 'none',
                outline: 'none'
              }}
            />
          </div>

          {/* 输出面板 */}
          <div style={{ width: '40%', background: '#1e1e1e', borderLeft: '1px solid #3e3e42', display: 'flex', flexDirection: 'column' }}>
            <div style={{ padding: '12px', background: '#2d2d30', borderBottom: '1px solid #3e3e42', fontSize: '13px', fontWeight: 'bold' }}>
              输出
            </div>
            <div style={{ flex: 1, padding: '16px', overflow: 'auto', fontFamily: 'Consolas, Monaco, monospace', fontSize: '13px', whiteSpace: 'pre-wrap' }}>
              {currentFile && currentFile.endsWith('.html') ? (
                <div dangerouslySetInnerHTML={{ __html: output }} style={{ width: '100%', height: '100%' }} />
              ) : (
                output || '点击"运行"按钮执行代码'
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

const btnStyle = {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  gap: '6px',
  padding: '8px',
  background: '#0e639c',
  color: '#fff',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '12px',
  transition: 'background 0.2s'
};

const actionBtnStyle = {
  display: 'flex',
  alignItems: 'center',
  gap: '6px',
  padding: '8px 16px',
  color: '#fff',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '13px',
  fontWeight: '500',
  transition: 'opacity 0.2s'
};
