import React from 'react';
import { X, Download } from 'lucide-react';
import PPTImageViewer from './PPTImageViewer';

const UniversalFileViewer = ({ file, onClose }) => {
  const getFileType = (fileType) => {
    return fileType.toLowerCase();
  };

  const renderViewer = () => {
    const type = getFileType(file.fileType);
    
    switch (type) {
      case 'ppt':
      case 'pptx':
        return (
          <PPTImageViewer
            coursewareId={file.id}
            coursewareTitle={file.title}
            onClose={onClose}
          />
        );
      
      case 'pdf':
        return (
          <div style={{
            position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.95)', zIndex: 2000,
            display: 'flex', flexDirection: 'column'
          }}>
            <div style={{
              background: '#1a1a1a', padding: '16px 24px',
              display: 'flex', justifyContent: 'space-between', alignItems: 'center'
            }}>
              <h3 style={{ margin: 0, color: 'white', fontSize: '18px' }}>{file.title}</h3>
              <div style={{ display: 'flex', gap: '12px' }}>
                <a href={`http://localhost:5000${file.filePath}`} download={file.fileName}
                   style={{
                     padding: '8px 16px', background: '#667eea', color: 'white',
                     borderRadius: '8px', textDecoration: 'none',
                     display: 'flex', alignItems: 'center', gap: '6px'
                   }}>
                  <Download size={16} /> ‰∏ãËΩΩ
                </a>
                <button onClick={onClose} style={{
                  padding: '8px 16px', background: '#374151', color: 'white',
                  border: 'none', borderRadius: '8px', cursor: 'pointer',
                  display: 'flex', alignItems: 'center', gap: '6px'
                }}>
                  <X size={16} /> ÂÖ≥Èó≠
                </button>
              </div>
            </div>
            <div style={{ flex: 1, padding: '20px', display: 'flex', justifyContent: 'center' }}>
              <iframe
                src={`http://localhost:5000${file.filePath}`}
                style={{ width: '100%', height: '100%', border: 'none', borderRadius: '8px' }}
                title={file.title}
              />
            </div>
          </div>
        );
      
      case 'doc':
      case 'docx':
        return (
          <div style={{
            position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.95)', zIndex: 2000,
            display: 'flex', flexDirection: 'column'
          }}>
            <div style={{
              background: '#1a1a1a', padding: '16px 24px',
              display: 'flex', justifyContent: 'space-between', alignItems: 'center'
            }}>
              <h3 style={{ margin: 0, color: 'white', fontSize: '18px' }}>{file.title}</h3>
              <div style={{ display: 'flex', gap: '12px' }}>
                <a href={`http://localhost:5000${file.filePath}`} download={file.fileName}
                   style={{
                     padding: '8px 16px', background: '#667eea', color: 'white',
                     borderRadius: '8px', textDecoration: 'none',
                     display: 'flex', alignItems: 'center', gap: '6px'
                   }}>
                  <Download size={16} /> ‰∏ãËΩΩ
                </a>
                <button onClick={onClose} style={{
                  padding: '8px 16px', background: '#374151', color: 'white',
                  border: 'none', borderRadius: '8px', cursor: 'pointer',
                  display: 'flex', alignItems: 'center', gap: '6px'
                }}>
                  <X size={16} /> ÂÖ≥Èó≠
                </button>
              </div>
            </div>
            <div style={{ flex: 1, padding: '20px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
              <div style={{ textAlign: 'center', color: 'white' }}>
                <div style={{ fontSize: '48px', marginBottom: '20px' }}>üìù</div>
                <h3 style={{ marginBottom: '16px' }}>Word ÊñáÊ°£È¢ÑËßà</h3>
                <p style={{ marginBottom: '24px', color: '#9ca3af' }}>
                  ÊµèËßàÂô®Êó†Ê≥ïÁõ¥Êé•È¢ÑËßà Word ÊñáÊ°£ÔºåËØ∑ÈÄâÊã©‰ª•‰∏ãÊñπÂºèÊü•ÁúãÔºö
                </p>
                <div style={{ display: 'flex', gap: '16px', justifyContent: 'center' }}>
                  <a
                    href={`https://view.officeapps.live.com/op/view.aspx?src=${encodeURIComponent('http://localhost:5000' + file.filePath)}`}
                    target="_blank" rel="noopener noreferrer"
                    style={{
                      padding: '12px 24px', background: '#667eea', color: 'white',
                      borderRadius: '8px', textDecoration: 'none'
                    }}
                  >
                    Âú®Á∫øÈ¢ÑËßà
                  </a>
                  <a
                    href={`http://localhost:5000${file.filePath}`}
                    download={file.fileName}
                    style={{
                      padding: '12px 24px', background: '#16a34a', color: 'white',
                      borderRadius: '8px', textDecoration: 'none'
                    }}
                  >
                    ‰∏ãËΩΩÊü•Áúã
                  </a>
                </div>
              </div>
            </div>
          </div>
        );
      
      default:
        return (
          <div style={{
            position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.95)', zIndex: 2000,
            display: 'flex', justifyContent: 'center', alignItems: 'center'
          }}>
            <div style={{ textAlign: 'center', color: 'white' }}>
              <div style={{ fontSize: '48px', marginBottom: '20px' }}>üìÑ</div>
              <h3>‰∏çÊîØÊåÅÁöÑÊñá‰ª∂Á±ªÂûã</h3>
              <p style={{ marginBottom: '24px' }}>ËØ∑‰∏ãËΩΩÊñá‰ª∂ÂêéÊü•Áúã</p>
              <div style={{ display: 'flex', gap: '16px', justifyContent: 'center' }}>
                <a href={`http://localhost:5000${file.filePath}`} download={file.fileName}
                   style={{
                     padding: '12px 24px', background: '#667eea', color: 'white',
                     borderRadius: '8px', textDecoration: 'none'
                   }}>
                  ‰∏ãËΩΩÊñá‰ª∂
                </a>
                <button onClick={onClose} style={{
                  padding: '12px 24px', background: '#374151', color: 'white',
                  border: 'none', borderRadius: '8px', cursor: 'pointer'
                }}>
                  ÂÖ≥Èó≠
                </button>
              </div>
            </div>
          </div>
        );
    }
  };

  return renderViewer();
};

export default UniversalFileViewer;
