import React, { useState, useRef, useEffect } from 'react';
import { 
  Play, 
  FolderPlus, 
  FilePlus, 
  RefreshCw, 
  Save, 
  Trash2, 
  FileCode, 
  Folder, 
  ChevronRight, 
  ChevronDown,
  Upload,
  GripVertical
} from 'lucide-react';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { vscDarkPlus } from 'react-syntax-highlighter/dist/esm/styles/prism';

const TEMPLATES = {
  'HTMLÂü∫Á°Ä': `<!DOCTYPE html>
<html lang="zh-CN">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>ÊàëÁöÑÈ°µÈù¢</title>
  <style>
    body {font-family: Arial, sans-serif;margin: 20px;background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);min-height: 100vh;display: flex;align-items: center;justify-content: center;}
    .container {background: white;padding: 40px;border-radius: 10px;box-shadow: 0 10px 30px rgba(0,0,0,0.3);}
  </style>
</head>
<body>
  <div class="container">
    <h1>Hello World! üéâ</h1>
    <p>ËøôÊòØ‰∏Ä‰∏™Âü∫Á°ÄÁöÑHTMLÊ®°Êùø</p>
  </div>
</body>
</html>`,
  'ReactÁªÑ‰ª∂': `import React, { useState } from 'react';

function Counter() {
  const [count, setCount] = useState(0);
  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2>ËÆ°Êï∞Âô®: {count}</h2>
      <button onClick={() => setCount(count + 1)}>Â¢ûÂä†</button>
      <button onClick={() => setCount(0)} style={{ marginLeft: '10px' }}>ÈáçÁΩÆ</button>
    </div>
  );
}
export default Counter;`,
  // ... ÂÖ∂‰ªñÊ®°Êùø‰øùÊåÅ‰∏çÂèòÔºà‰∏∫‰∫ÜËäÇÁúÅÁØáÂπÖËøôÈáåÂè™Â±ïÁ§∫‰∏§‰∏™ÔºåÂÆåÊï¥ÁâàËØ∑‰øùÁïô‰Ω†ÂéüÊù•ÁöÑÊâÄÊúâÊ®°ÊùøÔºâ
  // ËØ∑Êää‰Ω†ÂéüÊù• TEMPLATES ÂØπË±°ÈáåÁöÑÊâÄÊúâÂÜÖÂÆπÂ§çÂà∂ËøõÊù•
};

const STORAGE_KEY = 'react-code-editor-data';

function App() {
  const saved = JSON.parse(localStorage.getItem(STORAGE_KEY) || '{}');

  const [projects, setProjects] = useState(saved.projects || [{ id: '1', name: 'ÈªòËÆ§È°πÁõÆ', files: [] }]);
  const [currentProject, setCurrentProject] = useState(saved.currentProject || '1');
  const [files, setFiles] = useState(saved.files || {});
  const [currentFile, setCurrentFile] = useState(saved.currentFile || null);
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [expandedFolders, setExpandedFolders] = useState(saved.expandedFolders || {});
  const [showTemplates, setShowTemplates] = useState(false);
  const [editorWidth, setEditorWidth] = useState(saved.editorWidth || 60);
  const [isDragging, setIsDragging] = useState(false);
  const [saveStatus, setSaveStatus] = useState('');

  const fileInputRef = useRef(null);
  const containerRef = useRef(null);

  // Ëá™Âä®‰øùÂ≠ò
  useEffect(() => {
    const data = { projects, currentProject, files, currentFile, expandedFolders, editorWidth };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
    setSaveStatus('Â∑≤Ëá™Âä®‰øùÂ≠ò');
    const t = setTimeout(() => setSaveStatus(''), 1500);
    return () => clearTimeout(t);
  }, [projects, currentProject, files, currentFile, expandedFolders, editorWidth]);

  // Êñá‰ª∂ÂàáÊç¢Êó∂ÂêåÊ≠•‰ª£Á†Å
  useEffect(() => {
    if (currentFile) setCode(files[currentFile] || '');
    else setCode('');
  }, [currentFile, files]);

  const getCurrentProject = () => projects.find(p => p.id === currentProject);

  // ============ È°πÁõÆÊìç‰Ωú ============
  const addProject = () => {
    const name = prompt('ËæìÂÖ•È°πÁõÆÂêçÁß∞:');
    if (name) {
      const newProject = { id: Date.now().toString(), name, files: [] };
      setProjects([...projects, newProject]);
      setCurrentProject(newProject.id);
    }
  };

  const deleteProject = (id) => {
    if (projects.length === 1) return alert('Ëá≥Â∞ë‰øùÁïô‰∏Ä‰∏™È°πÁõÆ');
    if (window.confirm('Á°ÆÂÆöÂà†Èô§Ê≠§È°πÁõÆÂêó?')) {
      setProjects(projects.filter(p => p.id !== id));
      if (currentProject === id) setCurrentProject(projects[0].id);
    }
  };

  // ============ Êñá‰ª∂/Êñá‰ª∂Â§πÊìç‰Ωú ============
  const addFile = (parentPath = '') => {
    const name = prompt('ËæìÂÖ•Êñá‰ª∂Âêç (Â¶Ç: index.html, script.js):');
    if (!name) return;
    const filePath = parentPath ? `${parentPath}/${name}` : name;
    const newFile = { id: Date.now().toString(), name, path: filePath, type: 'file', parentPath };
    setProjects(projects.map(p => p.id === currentProject ? { ...p, files: [...p.files, newFile] } : p));
    setFiles({ ...files, [filePath]: '' });
    setCurrentFile(filePath);
  };

  const addFolder = (parentPath = '') => {
    const name = prompt('ËæìÂÖ•Êñá‰ª∂Â§πÂêçÁß∞:');
    if (!name) return;
    const folderPath = parentPath ? `${parentPath}/${name}` : name;
    const newFolder = { id: Date.now().toString(), name, path: folderPath, type: 'folder', parentPath };
    setProjects(projects.map(p => p.id === currentProject ? { ...p, files: [...p.files, newFolder] } : p));
    setExpandedFolders({ ...expandedFolders, [folderPath]: true });
  };

  const deleteFile = (filePath) => {
    if (!window.confirm('Á°ÆÂÆöÂà†Èô§Âêó?')) return;
    setProjects(projects.map(p => p.id === currentProject
      ? { ...p, files: p.files.filter(f => f.path !== filePath && !f.path.startsWith(filePath + '/')) }
      : p));
    const newFiles = { ...files };
    delete newFiles[filePath];
    setFiles(newFiles);
    if (currentFile === filePath) { setCurrentFile(null); setCode(''); }
  };

  const openFile = (path) => setCurrentFile(path);
  const toggleFolder = (path) => setExpandedFolders({ ...expandedFolders, [path]: !expandedFolders[path] });

  // ============ ËøêË°å‰ª£Á†Å ============
  const runCode = () => {
    setOutput('Ê≠£Âú®ÊâßË°å...');
    setTimeout(() => {
      try {
        const logs = [];
        const origLog = console.log;
        console.log = (...args) => { logs.push(args.join(' ')); origLog(...args); };
        if (currentFile?.endsWith('.html')) {
          setOutput(`<iframe srcdoc="${code.replace(/"/g, '&quot;')}" style="width:100%;height:100%;border:none;"></iframe>`);
        } else {
          eval(code);
          setOutput(logs.length ? logs.join('\n') : 'ÊâßË°åÊàêÂäüÔºåÊó†ËæìÂá∫');
        }
        console.log = origLog;
      } catch (e) {
        setOutput(`ÈîôËØØ:\n${e.message}`);
      }
    }, 10);
  };

  const refresh = () => { setOutput(''); };

  const applyTemplate = (name) => {
    if (!currentFile) return alert('ËØ∑ÂÖàÈÄâÊã©Êñá‰ª∂');
    const content = TEMPLATES[name];
    setCode(content);
    setFiles({ ...files, [currentFile]: content });
    setShowTemplates(false);
  };

  const handleFileUpload = (e) => {
    Array.from(e.target.files).forEach(file => {
      const reader = new FileReader();
      reader.onload = ev => {
        const content = ev.target.result;
        const filePath = file.name;
        const newFile = { id: Date.now().toString(), name: file.name, path: filePath, type: 'file', parentPath: '' };
        setProjects(projects.map(p => p.id === currentProject ? { ...p, files: [...p.files, newFile] } : p));
        setFiles({ ...files, [filePath]: content });
        setCurrentFile(filePath);
        setCode(content);
      };
      reader.readAsText(file);
    });
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  // ============ ÊãñÊãΩÂàÜÈöîÊù° ============
  const handleMouseDown = () => setIsDragging(true);
  const handleMouseMove = (e) => {
    if (!isDragging || !containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    const w = ((e.clientX - rect.left) / rect.width) * 100;
    if (w >= 20 && w <= 80) setEditorWidth(w);
  };
  const handleMouseUp = () => setIsDragging(false);

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging]);

  // ============ Êñá‰ª∂Ê†ë ============
  const renderFileTree = (parentPath = '') => {
    const items = getCurrentProject()?.files.filter(f => f.parentPath === parentPath) || [];
    return items.map(item => (
      <div key={item.id} style={{ marginLeft: parentPath ? '20px' : '0' }}>
        {item.type === 'folder' ? (
          <div>
            <div onClick={() => toggleFolder(item.path)} className="file-tree-item" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
              <div style={{ display: 'flex', alignItems: 'center', flex: 1 }}>
                {expandedFolders[item.path] ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
                <Folder size={16} style={{ margin: '0 6px', color: '#f59e0b' }} />
                <span style={{ fontSize: '14px' }}>{item.name}</span>
              </div>
              <div style={{ display: 'flex', gap: '4px' }}>
                <button onClick={(e) => { e.stopPropagation(); addFile(item.path); }} className="mini-btn"><FilePlus size={12} /></button>
                <button onClick={(e) => { e.stopPropagation(); deleteFile(item.path); }} className="delete-btn"><Trash2 size={14} color="#ef4444" /></button>
              </div>
            </div>
            {expandedFolders[item.path] && renderFileTree(item.path)}
          </div>
        ) : (
          <div onClick={() => openFile(item.path)} className={`file-tree-item ${currentFile === item.path ? 'active' : ''}`}>
            <FileCode size={16} style={{ margin: '0 6px', color: '#3b82f6' }} />
            <span style={{ fontSize: '14px', flex: 1 }}>{item.name}</span>
            <button onClick={(e) => { e.stopPropagation(); deleteFile(item.path); }} className="delete-btn"><Trash2 size={14} color="#ef4444" /></button>
          </div>
        )}
      </div>
    ));
  };

  const getLanguage = (path) => {
    if (!path) return 'text';
    const ext = path.split('.').pop().toLowerCase();
    const map = { js: 'javascript', jsx: 'javascript', ts: 'typescript', tsx: 'typescript', html: 'html', css: 'css', json: 'json', md: 'markdown' };
    return map[ext] || 'text';
  };

  return (
    <div style={{ display: 'flex', height: '100vh', background: '#1e1e1e', color: '#fff', fontFamily: 'Arial, sans-serif' }}>
      <style>{`
        .file-tree-item{display:flex;align-items:center;padding:6px 8px;cursor:pointer;border-radius:4px;transition:background .2s}
        .file-tree-item:hover{background:#3c3c3c}
        .file-tree-item.active{background:#094771}
        .delete-btn,.mini-btn{background:none;border:none;cursor:pointer;padding:4px;border-radius:3px}
        .mini-btn:hover{background:rgba(255,255,255,.1)}
        .delete-btn:hover{background:rgba(239,68,68,.1)}
        .toolbar-btn{padding:8px;background:#0e639c;color:#fff;border:none;border-radius:4px;cursor:pointer;font-size:12px;display:flex;align-items:center;gap:6px}
        .toolbar-btn:hover{background:#0a507e}
        .action-btn{padding:8px 16px;background:#0e639c;color:#fff;border:none;border-radius:4px;cursor:pointer;font-size:13px}
        .action-btn:disabled{opacity:0.5;cursor:not-allowed}
        .divider{width:4px;background:#3e3e42;cursor:col-resize;display:flex;align-items:center;justify-content:center}
        .divider:hover,.divider.dragging{background:#0e639c}
        .save-status{position:fixed;top:10px;right:10px;background:#16a34a;color:#fff;padding:6px 12px;border-radius:4px;font-size:12px;z-index:999}
      `}</style>

      {saveStatus && <div className="save-status">{saveStatus}</div>}

      {/* ‰æßËæπÊ†è */}
      <div style={{ width: '280px', background: '#252526', borderRight: '1px solid #3e3e42', display: 'flex', flexDirection: 'column' }}>
        <div style={{ padding: '12px', borderBottom: '1px solid #3e3e42' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
            <select value={currentProject} onChange={e => setCurrentProject(e.target.value)}
              style={{ flex: 1, padding: '6px', background: '#3c3c3c', color: '#fff', border: '1px solid #555', borderRadius: '4px' }}>
              {projects.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
            </select>
            <button onClick={addProject} style={{ marginLeft: '8px', padding: '6px 10px', background: '#0e639c', color: '#fff', border: 'none', borderRadius: '4px' }}>Êñ∞Âª∫</button>
          </div>
          {projects.length > 1 && <button onClick={() => deleteProject(currentProject)} style={{ width: '100%', padding: '6px', background: '#c92a2a', color: '#fff', border: 'none', borderRadius: '4px' }}>Âà†Èô§ÂΩìÂâçÈ°πÁõÆ</button>}
        </div>

        <div style={{ padding: '12px', borderBottom: '1px solid #3e3e42', display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '8px' }}>
          <button onClick={() => addFile()} className="toolbar-btn"><FilePlus size={16} /> Êñ∞Âª∫Êñá‰ª∂</button>
          <button onClick={() => addFolder()} className="toolbar-btn"><FolderPlus size={16} /> Êñ∞Âª∫Êñá‰ª∂Â§π</button>
          <button onClick={() => fileInputRef.current?.click()} className="toolbar-btn"><Upload size={16} /> ‰∏ä‰º†Êñá‰ª∂</button>
          <button onClick={refresh} className="toolbar-btn"><RefreshCw size={16} /> Âà∑Êñ∞</button>
