                <select style={{width:'100%', padding:'8px', border:'1px solid #ddd'}} value={newAssign.linkedCodeId} onChange={e=>setNewAssign({...newAssign, linkedCodeId:e.target.value})}>
                  <option value="">(Êó† - ‰ΩøÁî®Á©∫ÁôΩÊ®°Êùø)</option>
                  {repo.map(r => <option key={r.id} value={r.id}>{r.title} ({r.category})</option>)}
                </select>
              </div>
            </div>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px', marginTop:'20px' }}>
              <button onClick={() => setShowModal(false)} style={{padding:'8px 15px', border:'1px solid #ddd', background:'white', borderRadius:'4px'}}>ÂèñÊ∂à</button>
              <button onClick={handleCreate} style={{padding:'8px 15px', background:'#0e639c', color:'white', border:'none', borderRadius:'4px'}}>ÂèëÂ∏É</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

