import React, { useState, useEffect } from 'react';
import { X, Download } from 'lucide-react';
import PPTImageViewer from './PPTImageViewer';

// WordÊñáÊ°£PDFÊü•ÁúãÂô®ÁªÑ‰ª∂
const WordPDFViewer = ({ file, onClose }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [pdfUrl, setPdfUrl] = useState(null);

  useEffect(() => {
    loadPDF();
  }, [file.id]);

  const loadPDF = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('üîÑ ÂºÄÂßãËΩ¨Êç¢WordÊñáÊ°£ÔºåËØæ‰ª∂ID:', file.id);
      
      const response = await fetch(`http://localhost:5000/api/courseware/${file.id}/doc-preview`);
      console.log('APIÂìçÂ∫îÁä∂ÊÄÅ:', response.status);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('‚úÖ ËΩ¨Êç¢ÊàêÂäü:', data);
      
      setPdfUrl(data.pdfUrl);
      setLoading(false);
    } catch (err) {
      console.error('‚ùå ËΩ¨Êç¢Â§±Ë¥•:', err);
      setError(err.message);
      setLoading(false);
    }
  };

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.95)', zIndex: 2000,
      display: 'flex', flexDirection: 'column'
    }}>
      <div style={{
        background: '#1a1a1a', padding: '16px 24px',
        display: 'flex', justifyContent: 'space-between', alignItems: 'center'
      }}>
        <h3 style={{ margin: 0, color: 'white', fontSize: '18px' }}>{file.title}</h3>
        <div style={{ display: 'flex', gap: '12px' }}>
          <a href={`http://localhost:5000${file.filePath}`} download={file.fileName}
             style={{
               padding: '8px 16px', background: '#667eea', color: 'white',
               borderRadius: '8px', textDecoration: 'none',
               display: 'flex', alignItems: 'center', gap: '6px'
             }}>
            <Download size={16} /> ‰∏ãËΩΩÂéüÊñá‰ª∂
          </a>
          <button onClick={onClose} style={{
            padding: '8px 16px', background: '#374151', color: 'white',
            border: 'none', borderRadius: '8px', cursor: 'pointer',
            display: 'flex', alignItems: 'center', gap: '6px'
          }}>
            <X size={16} /> ÂÖ≥Èó≠
          </button>
        </div>
      </div>
      <div style={{ flex: 1, padding: '20px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        {loading ? (
          <div style={{ color: 'white', fontSize: '18px', textAlign: 'center' }}>
            <div style={{ marginBottom: '16px', fontSize: '48px' }}>‚è≥</div>
            <div>Ê≠£Âú®ËΩ¨Êç¢WordÊñáÊ°£‰∏∫PDF...</div>
            <div style={{ fontSize: '14px', color: '#9ca3af', marginTop: '8px' }}>
              È¶ñÊ¨°ËΩ¨Êç¢ÈúÄË¶Å‰∏Ä‰∫õÊó∂Èó¥ÔºåËØ∑Á®çÂÄô
            </div>
          </div>
        ) : error ? (
          <div style={{ color: 'white', textAlign: 'center', maxWidth: '500px' }}>
            <div style={{ fontSize: '48px', marginBottom: '16px' }}>‚ö†Ô∏è</div>
            <div style={{ fontSize: '20px', marginBottom: '12px' }}>ËΩ¨Êç¢Â§±Ë¥•</div>
            <div style={{ fontSize: '14px', color: '#9ca3af', marginBottom: '24px' }}>
              {error}
            </div>
            <div style={{ display: 'flex', gap: '12px', justifyContent: 'center' }}>
              <button
                onClick={loadPDF}
                style={{
                  padding: '12px 24px', background: '#667eea', color: 'white',
                  border: 'none', borderRadius: '8px', cursor: 'pointer',
                  fontSize: '14px', fontWeight: '500'
                }}>
                üîÑ ÈáçËØï
              </button>
              <a
                href={`http://localhost:5000${file.filePath}`}
                download={file.fileName}
                style={{
                  padding: '12px 24px', background: '#16a34a', color: 'white',
                  borderRadius: '8px', textDecoration: 'none',
                  fontSize: '14px', fontWeight: '500'
                }}>
                ‰∏ãËΩΩÂéüÊñá‰ª∂
              </a>
            </div>
          </div>
        ) : pdfUrl ? (
          <iframe
            src={`http://localhost:5000${pdfUrl}`}
            style={{ width: '100%', height: '100%', border: 'none', borderRadius: '8px' }}
            title={file.title}
          />
        ) : null}
      </div>
    </div>
  );
};

const UniversalFileViewer = ({ file, onClose }) => {
  const getFileType = (fileType) => {
    return fileType.toLowerCase();
  };

  const renderViewer = () => {
    const type = getFileType(file.fileType);
    
    // Ë∞ÉËØï‰ø°ÊÅØ
    console.log('üîç UniversalFileViewer - Êñá‰ª∂‰ø°ÊÅØ:', {
      id: file.id,
      title: file.title,
      fileType: file.fileType,
      type: type,
      filePath: file.filePath
    });
    
    switch (type) {
      case 'ppt':
      case 'pptx':
        return (
          <PPTImageViewer
            coursewareId={file.id}
            coursewareTitle={file.title}
            onClose={onClose}
          />
        );
      
      case 'pdf':
        return (
          <div style={{
            position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.95)', zIndex: 2000,
            display: 'flex', flexDirection: 'column'
          }}>
            <div style={{
              background: '#1a1a1a', padding: '16px 24px',
              display: 'flex', justifyContent: 'space-between', alignItems: 'center'
            }}>
              <h3 style={{ margin: 0, color: 'white', fontSize: '18px' }}>{file.title}</h3>
              <div style={{ display: 'flex', gap: '12px' }}>
                <a href={`http://localhost:5000${file.filePath}`} download={file.fileName}
                   style={{
                     padding: '8px 16px', background: '#667eea', color: 'white',
                     borderRadius: '8px', textDecoration: 'none',
                     display: 'flex', alignItems: 'center', gap: '6px'
                   }}>
