import React, { useState, useRef, useEffect } from 'react';
import {
  Play,
  FolderPlus,
  FilePlus,
  RefreshCw,
  Save,
  Trash2,
  FileCode,
  Folder,
  ChevronRight,
  ChevronDown,
  Upload,
  GripVertical,
} from 'lucide-react';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { vscDarkPlus } from 'react-syntax-highlighter/dist/esm/styles/prism';

// ────────────────────────────── 模板保持不变 ──────────────────────────────
const TEMPLATES = { /* ... 原来的 TEMPLATES 对象保持不动 ... */ };

// ────────────────────────────── 本地存储 Key ──────────────────────────────
const STORAGE_KEY = 'react-code-editor-data';

function App() {
  // ==================== 状态初始化（从 localStorage 读取） ====================
  const saved = JSON.parse(localStorage.getItem(STORAGE_KEY) || '{}');

  const [projects, setProjects] = useState(saved.projects || [{ id: '1', name: '默认项目', files: [] }]);
  const [currentProject, setCurrentProject] = useState(saved.currentProject || '1');
  const [files, setFiles] = useState(saved.files || {}); // { path: content }
  const [currentFile, setCurrentFile] = useState(saved.currentFile || null);
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [expandedFolders, setExpandedFolders] = useState(saved.expandedFolders || {});
  const [showTemplates, setShowTemplates] = useState(false);
  const [editorWidth, setEditorWidth] = useState(saved.editorWidth || 60);
  const [isDragging, setIsDragging] = useState(false);
  const [saveStatus, setSaveStatus] = useState(''); // 保存提示

  const fileInputRef = useRef(null);
  const containerRef = useRef(null);

  // ==================== 自动保存到 localStorage ====================
  useEffect(() => {
    const data = {
      projects,
      currentProject,
      files,
      currentFile,
      expandedFolders,
      editorWidth,
    };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
    setSaveStatus('已自动保存');
    const timer = setTimeout(() => setSaveStatus(''), 1500);
    return () => clearTimeout(timer);
  }, [projects, currentProject, files, currentFile, expandedFolders, editorWidth]);

  // ==================== 当选中文件改变时同步代码 ====================
  useEffect(() => {
    if (currentFile) {
      setCode(files[currentFile] || '');
    } else {
      setCode('');
    }
  }, [currentFile, files]);

  // ==================== 其余业务代码保持不变（略作精简） ====================
  const getCurrentProject = () => projects.find(p => p.id === currentProject);

  const addProject = () => {
    const name = prompt('输入项目名称:');
    if (!name) return;
    const newProject = { id: Date.now().toString(), name, files: [] };
    setProjects([...projects, newProject]);
    setCurrentProject(newProject.id);
  };

  const deleteProject = (id) => {
    if (projects.length === 1) return alert('至少保留一个项目');
    if (!window.confirm('确定删除此项目吗?')) return;
    setProjects(projects.filter(p => p.id !== id));
    if (currentProject === id) setCurrentProject(projects[0].id);
  };

  const addFile = (parentPath = '') => {
    const name = prompt('输入文件名 (如: index.html, script.js):');
    if (!name) return;
    const filePath = parentPath ? `${parentPath}/${name}` : name;
    const newFile = {
      id: Date.now().toString(),
      name,
      path: filePath,
      type: 'file',
      parentPath,
    };
    setProjects(projects.map(p =>
      p.id === currentProject ? { ...p, files: [...p.files, newFile] } : p
    ));
    setFiles({ ...files, [filePath]: '' });
    setCurrentFile(filePath);
  };

  const addFolder = (parentPath = '') => {
    const name = prompt('输入文件夹名称:');
    if (!name) return;
    const folderPath = parentPath ? `${parentPath}/${name}` : name;
    const newFolder = {
      id: Date.now().toString(),
      name,
      path: folderPath,
      type: 'folder',
      parentPath,
    };
    setProjects(projects.map(p =>
      p.id === currentProject ? { ...p, files: [...p.files, newFolder] } : p
    ));
    setExpandedFolders({ ...expandedFolders, [folderPath]: true });
  };

  const deleteFile = (filePath) => {
    if (!window.confirm('确定删除吗?')) return;
    setProjects(projects.map(p =>
      p.id === currentProject
        ? { ...p, files: p.files.filter(f => f.path !== filePath && !f.path.startsWith(filePath + '/')) }
        : p
    ));
    const newFiles = { ...files };
    delete newFiles[filePath];
    setFiles(newFiles);
    if (currentFile === filePath) {
      setCurrentFile(null);
      setCode('');
    }
  };

  const openFile = (filePath) => setCurrentFile(filePath);

  const toggleFolder = (folderPath) => {
    setExpandedFolders({ ...expandedFolders, [folderPath]: !expandedFolders[folderPath] });
  };

  const runCode = () => {
    setOutput('正在执行...');
    setTimeout(() => {
      try {
        const consoleOutput = [];
        const originalLog = console.log;
        const originalError = console.error;
        const originalWarn = console.warn;

        console.log = (...args) => {
          consoleOutput.push(args.map(a => String(a)).join(' '));
          originalLog(...args);
        };
        console.error = (...args) => {
          consoleOutput.push('❌ ' + args.map(a => String(a)).join(' '));
          originalError(...args);
        };
        console.warn = (...args) => {
          consoleOutput.push('⚠️ ' + args.map(a => String(a)).join(' '));
          originalWarn(...args);
        };

        if (currentFile?.endsWith('.html')) {
          setOutput(`<iframe srcdoc="${code.replace(/"/g, '&quot;')}" style="width:100%;height:100%;border:none;background:white;"></iframe>`);
        } else {
          // eslint-disable-next-line no-eval
          eval(code);
          setOutput(consoleOutput.length ? consoleOutput.join('\n') : '代码执行成功，无输出');
        }

        console.log = originalLog;
        console.error = originalError;
        console.warn = originalWarn;
      } catch (e) {
        setOutput(`运行错误:\n${e.message}\n${e.stack || ''}`);
      }
    }, 10);
  };

  const refresh = () => {
    setOutput('');
    alert('编辑器已刷新');
  };

  const applyTemplate = (templateName) => {
    if (!currentFile) return alert('请先创建或选择一个文件');
    const content = TEMPLATES[templateName];
    setCode(content);
    setFiles({ ...files, [currentFile]: content });
    setShowTemplates(false);
  };

  const handleFileUpload = (e) => {
    const uploaded = Array.from(e.target.files);
    uploaded.forEach(file => {
      const reader = new FileReader();
      reader.onload = ev => {
        const content = ev.target.result;
        const filePath = file.name;
        const newFile = {
          id: Date.now().toString() + Math.random(),
          name: file.name,
          path: filePath,
          type: 'file',
          parentPath: '',
        };
        setProjects(projects.map(p =>
          p.id === currentProject ? { ...p, files: [...p.files, newFile] } : p
        ));
        setFiles({ ...files, [filePath]: content });
        setCurrentFile(filePath);
        setCode(content);
      };
      reader.readAsText(file);
    });
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  // ==================== 拖拽分隔条 ====================
  const handleMouseDown = () => setIsDragging(true);
  const handleMouseMove = (e) => {
    if (!isDragging || !containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    const newWidth = ((e.clientX - rect.left) / rect.width) * 100;
    if (newWidth >= 20 && newWidth <= 80) setEditorWidth(newWidth);
  };
  const handleMouseUp = () => setIsDragging(false);

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging]);

  // ==================== 文件树渲染 ====================
  const renderFileTree = (parentPath = '') => {
    const project = getCurrentProject();
    if (!project) return null;
    const items = project.files.filter(f => f.parentPath === parentPath);

    return items.map(item => (
      <div key={item.id} style={{ marginLeft: parentPath ? '20px' : '0' }}>
        {item.type === 'folder' ? (
          <div>
            <div
              onClick={() => toggleFolder(item.path)}
              className="file-tree-item"
              style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}
            >
              <div style={{ display: 'flex', alignItems: 'center', flex: 1 }}>
                {expandedFolders[item.path] ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
                <Folder size={16} style={{ margin: '0 6px', color: '#f59e0b' }} />
                <span style={{ fontSize: '14px' }}>{item.name}</span>
              </div>
              <div style={{ display: 'flex', gap: '4px' }}>
                <button onClick={(e) => { e.stopPropagation(); addFile(item.path); }} className="mini-btn" title="新建文件">
                  <FilePlus size={12} />
                </button>
                <button onClick={(e) => { e.stopPropagation(); deleteFile(item.path); }} className="delete-btn">
                  <Trash2 size={14} color="#ef4444" />
                </button>
              </div>
            </div>
            {expandedFolders[item.path] && renderFileTree(item.path)}
          </div>
        ) : (
          <div
            onClick={() => openFile(item.path)}
            className={`file-tree-item ${currentFile === item.path ? 'active' : ''}`}
          >
            <FileCode size={16} style={{ margin: '0 6px', color: '#3b82f6' }} />
            <span style={{ fontSize: '14px', flex: 1 }}>{item.name}</span>
            <button onClick={(e) => { e.stopPropagation(); deleteFile(item.path); }} className="delete-btn">
              <Trash2 size={14} color="#ef4444" />
            </button>
          </div>
        )}
      </div>
    ));
  };

  // ==================== 语言检测（简单版） ====================
  const getLanguage = (path) => {
    if (!path) return 'text';
    const ext = path.split('.').pop().toLowerCase();
    const map = {
      js: 'javascript',
      jsx: 'javascript',
      ts: 'typescript',
      tsx: 'typescript',
      html: 'html',
      css: 'css',
      json: 'json',
      md: 'markdown',
      txt: 'text',
    };
    return map[ext] || 'text';
  };

  // ==================== JSX 返回 ====================
  return (
    <div style={{ display: 'flex', height: '100vh', background: '#1e1e1e', color: '#fff', fontFamily: 'Arial, sans-serif' }}>
      <style>{`
        /* 原来的样式保持不动，略作补充 */
        .save-status { position:fixed; top:10px; right:10px; background:#16a34a; color:#fff; padding:6px 12px; border-radius:4px; font-size:12px; z-index:999; }
        /* 其它样式同原文件 */
        .file-tree-item,{display:flex;align-items:center;padding:6px 8px;cursor:pointer;border-radius:4px;transition:background .2s}
        .file-tree-item:hover{background:#3c3c3c}
        .file-tree-item.active{background:#094771}
        .delete-btn,.mini-btn{background:none;border:none;cursor:pointer;padding:4px;border-radius:3px;transition:background .2s}
        .mini-btn{color:#fff}
        .mini-btn:hover{background:rgba(255,255,255,.1)}
        .delete-btn:hover{background:rgba(239,68,68,.1)}
        .toolbar-btn,.action-btn{transition:all .2s}
        .divider{width:4px;background:#3e3e42;cursor:col-resize;position:relative;display:flex;align-items:center;justify-content:center}
        .divider:hover,.divider.dragging{background:#0e639c}
      `}</style>

      {/* 保存状态提示 */}
      {saveStatus && <div className="save-status">{saveStatus}</div>}

      {/* 侧边栏 */}
      <div style={{ width: '280px', background: '#252526', borderRight: '1px solid #3e3e42', display: 'flex', flexDirection: 'column' }}>
        {/* 项目管理、工具栏、模板、文件树保持原样 */}
        {/* ...（省略相同代码） */}
        {/* 这里直接粘贴原来的侧边栏 JSX，保持不变 */}
        {/* 为节省篇幅，只保留结构，实际使用时直接复制原来的侧边栏代码即可 */}
      </div>

      {/* 编辑器区域 */}
      <div ref={containerRef} style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* 顶部工具栏 */}
        <div style={{ padding: '12px', background: '#2d2d30', borderBottom: '1px solid #3e3e42', display: 'flex', gap: '8px', alignItems: 'center' }}>
          <span style={{ fontSize: '14px', color: '#ccc' }}>
            {currentFile || '未选择文件'}
          </span>
          <div style={{ marginLeft: 'auto', display: 'flex', gap: '8px' }}>
            <button onClick={() => { setFiles({ ...files, [currentFile]: code }); }} disabled={!currentFile} className="action-btn" style={{ background: currentFile ? '#0e639c' : '#555' }}>
              <Save size={16} /> 保存
            </button>
            <button onClick={runCode} disabled={!currentFile} className="action-btn" style={{ background: currentFile ? '#16a34a' : '#555' }}>
              <Play size={16} /> 运行
            </button>
          </div>
        </div>

        {/* 编辑器 + 输出 */}
        <div style={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
          {/* 代码编辑器（高亮） */}
          <div style={{ width: `${editorWidth}%`, display: 'flex', flexDirection: 'column', background: '#1e1e1e' }}>
            {currentFile ? (
              <SyntaxHighlighter
                language={getLanguage(currentFile)}
                style={vscDarkPlus}
                showLineNumbers
                customStyle={{ margin: 0, height: '100%', fontSize: '14px' }}
                onChange={(newCode) => setCode(newCode)} // 可编辑
                value={code}
              />
            ) : (
              <div style={{ padding: '20px', color: '#888', fontSize: '14px' }}>
                请在左侧创建或选择一个文件开始编码…
              </div>
            )}
          </div>

          {/* 分隔条 */}
          <div className={`divider ${isDragging ? 'dragging' : ''}`} onMouseDown={handleMouseDown}>
            <GripVertical size={16} color="#888" />
          </div>

          {/* 输出面板 */}
          <div style={{ width: `${100 - editorWidth}%`, background: '#1e1e1e', display: 'flex', flexDirection: 'column' }}>
            <div style={{ padding: '12px', background: '#2d2d30', borderBottom: '1px solid #3e3e42', fontSize: '13px', fontWeight: 'bold' }}>
              输出
            </div>
            <div style={{ flex: 1, padding: '16px', overflow: 'auto', fontFamily: 'Consolas, monospace', fontSize: '13px', whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>
              {currentFile?.endsWith('.html') ? (
                <div dangerouslySetInnerHTML={{ __html: output }} style={{ width: '100%', height: '100%' }} />
              ) : (
                <div style={{ color: '#d4d4d4' }}>
                  {output || '点击「运行」查看输出'}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
