// 修改后的 App.js 文件内容
import { login } from './api';
import React, { useState } from 'react';
import { 
  Play, FolderPlus, FilePlus, RefreshCw, Save, Trash2, FileCode, Folder, 
  ChevronRight, ChevronDown, Upload, GripVertical, Download, Edit2, 
  PackageOpen, PackagePlus, ArrowLeft, Send, Users, BookOpen, CheckCircle, 
  GraduationCap, Database, Plus, Calendar, Search, X
} from 'lucide-react';

import FullEditor from './FullEditor';

// ==========================================
// 1. 扩展的模拟数据库
// ==========================================
const INITIAL_DATA = {
  students: [
    { id: 's1', name: '张三', class: '三年二班' },
    { id: 's2', name: '李四', class: '三年二班' },
    { id: 's3', name: '王五', class: '三年三班' }
  ],
  codeRepository: [
    { 
      id: 'c1', 
      title: '经典 HTML 布局', 
      category: 'HTML/CSS', 
      content: '<!DOCTYPE html>\n<html>\n<body>\n  <div style="display:flex">Flex 布局示例</div>\n</body>\n</html>' 
    },
    { 
      id: 'c2', 
      title: '数组去重算法', 
      category: 'JavaScript', 
      content: '// 使用 Set 去重\nconst arr = [1, 2, 2, 3];\nconst unique = [...new Set(arr)];\nconsole.log(unique);' 
    }
  ],
  assignments: [
    { 
      id: 'a1', 
      title: '个人主页制作', 
      requirements: '包含头像、姓名和一段自我介绍，背景色需为浅灰色。',
      deadline: '2025-12-31',
      targetClass: '所有班级', 
      template: { 'index.html': '<!-- 请在此处编写代码 -->' }
    }
  ],
  submissions: [] 
};

// ==========================================
// 3. 教师端核心模块
// ==========================================

// --- 3.1 学生名单管理 ---
const StudentManager = ({ students, setStudents }) => {
  const [selectedIds, setSelectedIds] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if(!file) return;

    const reader = new FileReader();
    reader.onload = (evt) => {
      const text = evt.target.result;
      const lines = text.split('\n').filter(l => l.trim());
      const newStudents = lines.map(line => {
        const parts = line.split(/,|，|\t/);
        if(parts.length >= 2) {
          return { id: parts[0].trim(), name: parts[1].trim(), class: parts[2]?.trim() || '未分配' };
        }
        return null;
      }).filter(Boolean);

      const currentIds = new Set(students.map(s => s.id));
      const validNew = newStudents.filter(s => !currentIds.has(s.id));
      setStudents([...students, ...validNew]);
      alert(`解析完成，新增 ${validNew.length} 名学生`);
    };
    reader.readAsText(file);
    e.target.value = '';
  };

  const handleDelete = (id) => {
    if(window.confirm('确定删除该学生吗？')) {
      setStudents(students.filter(s => s.id !== id));
    }
  };

  const handleBatchDelete = () => {
    if(selectedIds.length === 0) return;
    if(window.confirm(`确定删除选中的 ${selectedIds.length} 名学生吗？`)) {
      setStudents(students.filter(s => !selectedIds.includes(s.id)));
      setSelectedIds([]);
    }
  };

  const startEditing = (student) => {
    setEditingId(student.id);
    setEditForm(student);
  };

  const saveEdit = () => {
    setStudents(students.map(s => s.id === editingId ? editForm : s));
    setEditingId(null);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ marginBottom: 16 }}>学生名单管理</h2>

      <div style={{ marginBottom: 20 }}>
        <label style={{ display: 'block', marginBottom: 8 }}>批量导入 (CSV/Excel):</label>
        <input type="file" accept=".csv,.xlsx,.xls" onChange={handleFileUpload} />
      </div>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ background: '#2d2d30', textAlign: 'left' }}>
            <th style={{ padding: 12 }}><input type="checkbox" onChange={(e) => {
              setSelectedIds(e.target.checked ? students.map(s => s.id) : []);
            }} /></th>
            <th style={{ padding: 12 }}>学号</th>
            <th style={{ padding: 12 }}>姓名</th>
            <th style={{ padding: 12 }}>班级</th>
            <th style={{ padding: 12 }}>操作</th>
          </tr>
        </thead>
        <tbody>
          {students.map(student => (
            <tr key={student.id} style={{ borderBottom: '1px solid #3e3e42' }}>
              <td style={{ padding: 12 }}><input type="checkbox" checked={selectedIds.includes(student.id)} onChange={(e) => {
                setSelectedIds(e.target.checked ? [...selectedIds, student.id] : selectedIds.filter(id => id !== student.id));
              }} /></td>
              <td style={{ padding: 12 }}>
                {editingId === student.id ? 
                  <input value={editForm.id} onChange={e => setEditForm({...editForm, id: e.target.value})} /> : 
                  student.id
                }
              </td>
              <td style={{ padding: 12 }}>
                {editingId === student.id ? 
                  <input value={editForm.name} onChange={e => setEditForm({...editForm, name: e.target.value})} /> : 
                  student.name
                }
              </td>
              <td style={{ padding: 12 }}>
                {editingId === student.id ? 
                  <input value={editForm.class} onChange={e => setEditForm({...editForm, class: e.target.value})} /> : 
                  student.class
                }
              </td>
              <td style={{ padding: 12 }}>
                {editingId === student.id ? (
                  <>
                    <button onClick={saveEdit} style={{ marginRight: 8 }}>保存</button>
                    <button onClick={() => setEditingId(null)}>取消</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => startEditing(student)} style={{ marginRight: 8 }}>编辑</button>
                    <button onClick={() => handleDelete(student.id)}>删除</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedIds.length > 0 && (
        <div style={{ marginTop: 16 }}>
          <button onClick={handleBatchDelete} style={{ background: '#dc2626', color: '#fff', padding: '8px 16px' }}>批量删除</button>
        </div>
      )}
    </div>
  );
};

// --- 3.2 作业管理 ---
const AssignmentManager = ({ assignments, setAssignments, codeRepository }) => {
  const [newAssign, setNewAssign] = useState({
    title: '',
    requirements: '',
    deadline: '',
    targetClass: '所有班级',
    template: { 'index.html': '<!-- 请在此处编写代码 -->' }
  });

