const StudentDashboard = ({ user, data, onOpenEditor, onLogout }) => {
  const assignments = data.assignments.filter(a => a.targetClass === '所有班级' || a.targetClass === user.class);

  return (
    <div style={{ height: '100vh', background: '#1e1e1e', color: '#d4d4d4', padding: 32 }}>
