      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: 24 }}>
        {assignments.map(assign => {
          const sub = data.submissions.find(s => s.studentId === user.id && s.assignmentId === assign.id);
          return (
