                 <button 
                   onClick={() => onOpenEditor({
                     mode: 'student_work',
                     initialFiles: sub ? sub.files : assign.template,
                     projectName: assign.title,
                     assignmentId: assign.id
                   })}
                   style={{ padding: '10px 20px', background: sub ? '#0e639c' : '#ea580c', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                 >
                   {sub ? '修改作业' : '开始作业'}
                 </button>
               </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

// ==========================================
// 6. 主入口
// ==========================================
function App() {
  const [data, setData] = useState(INITIAL_DATA);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('login');
  const [editorConfig, setEditorConfig] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // 处理登录逻辑（使用本地验证，注释掉API调用部分）
  const handleLogin = () => {
    if (!username || !password) return alert('请输入用户名和密码');

    // 简单验证逻辑（示例）
    // 教师: username='teacher', password='teacher'
    // 学生: username=学号 (如's1'), password=任意
    if (username === 'teacher' && password === 'teacher') {
      setUser({ role: 'teacher', name: '教师' });
      setView('dashboard');
    } else {
      const s = data.students.find(stu => stu.id === username);
      if (s) {
        // 学生密码验证（演示中不检查密码）
        setUser({ ...s, role: 'student' });
        setView('dashboard');
      } else {
        alert('用户名或密码错误');
      }
    }
  };

  // 处理提交
  const handleSubmission = (selectedFiles) => {
    const assign = data.assignments.find(a => a.id === editorConfig.assignmentId);
    if(assign && user.role === 'student') {
       const newSub = {
         id: `sub${Date.now()}`,
         studentId: user.id,
         assignmentId: assign.id,
         files: selectedFiles,
         timestamp: Date.now()
       };
       const others = data.submissions.filter(s => !(s.studentId === user.id && s.assignmentId === assign.id));
       setData(prev => ({ ...prev, submissions: [...others, newSub] }));
    }
  };

  // 渲染不同视图
  return (
    <div>
      {view === 'login' && (
        <div style={{ maxWidth: 400, margin: '50px auto', padding: 20 }}>
          <h2>登录</h2>
          <input
            type="text"
            placeholder="用户名"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ width: '100%', padding: 8, marginBottom: 12 }}
          />
          <input
            type="password"
            placeholder="密码"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ width: '100%', padding: 8, marginBottom: 12 }}
          />
          <button 
            onClick={handleLogin} 
            style={{ width: '100%', padding: 8, background: '#16a34a', color: 'white', border: 'none' }}
          >
            登录
          </button>
        </div>
      )}

      {view === 'dashboard' && (
        user.role === 'teacher' ? (
          <TeacherDashboard
            data={data}
            setData={setData}
            onOpenEditor={(config) => {
              setEditorConfig(config);
              setView('editor');
            }}
            onLogout={() => setView('login')}
          />
        ) : (
          <StudentDashboard
            user={user}
            data={data}
            onOpenEditor={(config) => {
              setEditorConfig(config);
              setView('editor');
            }}
            onLogout={() => setView('login')}
          />
        )
      )}

      {view === 'editor' && (
        <FullEditor
          {...editorConfig}
          onBack={() => setView('dashboard')}
          onSubmit={(files) => {
            handleSubmission(files);
            setView('dashboard');
          }}
        />
      )}
    </div>
  );
}

export default App;
