};

function App() {
  const [projects, setProjects] = useState([
    { id: '1', name: 'é»˜è®¤é¡¹ç›®', files: [] }
  ]);
  const [currentProject, setCurrentProject] = useState('1');
  const [files, setFiles] = useState({});
  const [currentFile, setCurrentFile] = useState(null);
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [previewHtml, setPreviewHtml] = useState('');
  const [expandedFolders, setExpandedFolders] = useState({});
  const [showTemplates, setShowTemplates] = useState(false);
  const [showBgTemplates, setShowBgTemplates] = useState(false);
  const [themeName, setThemeName] = useState(() => {
    try {
      return localStorage.getItem('my-js-editor-theme') || 'dark';
    } catch (e) {
      return 'dark';
    }
  });
  const [editorWidth, setEditorWidth] = useState(60); // ç¼–è¾‘å™¨å®½åº¦ç™¾åˆ†æ¯”
  const [isDragging, setIsDragging] = useState(false);

  const fileInputRef = useRef(null);
  const containerRef = useRef(null);
  const iframeRef = useRef(null);

  const getCurrentProject = () => projects.find(p => p.id === currentProject);

  const applyTheme = (name) => {
    setThemeName(name);
    try {
      localStorage.setItem('my-js-editor-theme', name);
    } catch (e) {}
    setShowBgTemplates(false);
  };

  // å†…éƒ¨å·¥å…·ï¼šæ·»åŠ æ–‡ä»¶ï¼ˆä¸å¼¹çª—ï¼‰
  const addFileInternal = (name, parentPath = '', content = '') => {
    const filePath = parentPath ? `${parentPath}/${name}` : name;
    const newFile = {
      id: Date.now().toString() + Math.random(),
      name,
      path: filePath,
      type: 'file',
      parentPath
    };

    setProjects(prev => prev.map(p =>
      p.id === currentProject
        ? { ...p, files: [...p.files, newFile] }
        : p
    ));
    setFiles(prev => ({ ...prev, [filePath]: content }));
    return filePath;
  };

  // åœ¨å½“å‰é¡¹ç›®ä¸­æ–°å»ºä¸€ä¸ªæ–‡ä»¶å¹¶å†™å…¥æ¨¡æ¿å†…å®¹
  const createFileFromBgTemplate = (content, suggestedName = 'background.html') => {
    let filePath = suggestedName;
    // é¿å…åŒåè¦†ç›–ï¼šè‹¥å­˜åœ¨åˆ™è¿½åŠ æ—¶é—´æˆ³
    if (files[filePath]) {
      const ts = Date.now();
      const i = suggestedName.lastIndexOf('.');
      if (i !== -1) {
        filePath = `${suggestedName.slice(0, i)}-${ts}${suggestedName.slice(i)}`;
      } else {
        filePath = `${suggestedName}-${ts}`;
      }
    }

    const newFile = {
      id: Date.now().toString() + Math.random(),
      name: filePath.split('/').pop(),
      path: filePath,
      type: 'file',
      parentPath: ''
    };

    setProjects(prev => prev.map(p => p.id === currentProject ? { ...p, files: [...p.files, newFile] } : p));
    setFiles(prev => ({ ...prev, [filePath]: content }));
    setCurrentFile(filePath);
    setCode(content);
    setShowBgTemplates(false);
    alert(`å·²åœ¨å½“å‰é¡¹ç›®ä¸­æ–°å»ºæ–‡ä»¶: ${filePath}`);
  };

  // å°† BG æ¨¡æ¿åŠ è½½åˆ°ç¼–è¾‘å™¨ï¼ˆä¸åˆ›å»ºé¡¹ç›®æ–‡ä»¶ï¼‰
  const loadBgTemplate = async (key) => {
    const map = {
      old: 'Old.html',
      simple: 'simple.html',
      sweet: 'sweet.html',
      tech: 'Tech.html'
    };
    const fileName = map[key] || map.old;
    const url = `/BG/${fileName}`;
    try {
      const res = await fetch(url);
      if (!res.ok) throw new Error(`æ— æ³•åŠ è½½æ¨¡æ¿: ${res.status} ${res.statusText}`);
      const content = await res.text();
      const suggestedName = fileName;
      setCurrentFile(suggestedName);
      setCode(content);
      setShowBgTemplates(false);
    } catch (err) {
      alert(`åŠ è½½æ¨¡æ¿å¤±è´¥: ${err.message}`);
    }
  };

  // åœ¨æ–°çª—å£ä¸­å…¨å±æ‰“å¼€å½“å‰ HTML é¢„è§ˆ
  const openFullscreen = () => {
    if (!currentFile) {
      alert('è¯·å…ˆé€‰æ‹©æˆ–åŠ è½½ä¸€ä¸ª HTML æ–‡ä»¶');
      return;
    }
    if (!currentFile.endsWith('.html')) {
      alert('å½“å‰æ–‡ä»¶ä¸æ˜¯ HTMLï¼Œæ— æ³•å…¨å±é¢„è§ˆ');
      return;
    }
    // ä¼˜å…ˆä½¿ç”¨å½“å‰ previewHtmlï¼ˆå·²æ³¨å…¥è¦†ç›–æ ·å¼ï¼‰åœ¨æ–°çª—å£å†™å…¥æ˜¾ç¤ºï¼Œä¿è¯ä¸å†…éƒ¨é¢„è§ˆä¸€è‡´
    try {
      if (previewHtml) {
        const newWin = window.open('', '_blank');
        if (newWin) {
          newWin.document.open();
          newWin.document.write(previewHtml);
          newWin.document.close();
          return;
        }
      }

      const publicFiles = ['Old.html', 'simple.html', 'sweet.html', 'Tech.html'];
      if (publicFiles.includes(currentFile)) {
        // ç›´æ¥æ‰“å¼€ public/BG ä¸­çš„æ–‡ä»¶ï¼ˆä¿æŒåŸå§‹æ¨¡æ¿è·¯å¾„ï¼‰
        const url = `${window.location.origin}/BG/${currentFile}`;
        window.open(url, '_blank');
        return;
      }

      // å¦åˆ™åŸºäºå½“å‰ç¼–è¾‘å™¨å†…å®¹åˆ›å»º blob å¹¶æ‰“å¼€ï¼ˆé€‚ç”¨äºä¸´æ—¶æœªä¿å­˜çš„ HTMLï¼‰
      const blob = new Blob([code || ''], { type: 'text/html' });
      const objectUrl = URL.createObjectURL(blob);
      const w = window.open(objectUrl, '_blank');
      // åœ¨ä¸€æ®µæ—¶é—´åé‡Šæ”¾ URL
      setTimeout(() => {
        try { URL.revokeObjectURL(objectUrl); } catch (e) {}
        try { if (w && !w.closed) w.focus(); } catch (e) {}
      }, 20000);
    } catch (err) {
      alert('æ— æ³•æ‰“å¼€å…¨å±é¢„è§ˆ: ' + (err.message || err));
    }
  };

  const addProject = () => {
    const name = prompt('è¾“å…¥é¡¹ç›®åç§°:');
    if (name) {
      const newProject = {
        id: Date.now().toString(),
        name,
        files: []
      };
      setProjects([...projects, newProject]);
      setCurrentProject(newProject.id);
    }
  };

  const deleteProject = (id) => {
    if (projects.length === 1) {
      alert('è‡³å°‘ä¿ç•™ä¸€ä¸ªé¡¹ç›®');
      return;
    }
    if (window.confirm('ç¡®å®šåˆ é™¤æ­¤é¡¹ç›®å—?')) {
      setProjects(projects.filter(p => p.id !== id));
      if (currentProject === id) {
        setCurrentProject(projects[0].id);
      }
    }
  };

  const addFile = (parentPath = '') => {
    const name = prompt('è¾“å…¥æ–‡ä»¶å (å¦‚: index.html, script.js):');
    if (name) {
      const filePath = addFileInternal(name, parentPath, '');
      setCurrentFile(filePath);
      setCode('');
    }
  };

  const addFolder = (parentPath = '') => {
    const name = prompt('è¾“å…¥æ–‡ä»¶å¤¹åç§°:');
    if (!name) return;

    const folderPath = parentPath ? `${parentPath}/${name}` : name;
    const newFolder = {
      id: Date.now().toString(),
      name,
      path: folderPath,
      type: 'folder',
      parentPath
    };

    setProjects(prev => prev.map(p =>
      p.id === currentProject
        ? { ...p, files: [...p.files, newFolder] }
        : p
    ));
    setExpandedFolders(prev => ({ ...prev, [folderPath]: true }));

    // åˆ›å»ºä¸‰ä¸ªåˆå§‹æ–‡ä»¶
    const htmlPath = addFileInternal('index.html', folderPath, `<!DOCTYPE html>
<html lang="zh-CN">
<head>
  <meta charset="UTF-8">
  <title>${name}</title>
  <link rel="stylesheet" href="style.css">
</head>
<body>
  <h1>æ¬¢è¿ä½¿ç”¨ ${name}</h1>
  <script src="script.js"></script>
</body>
</html>`);

    addFileInternal('style.css', folderPath, `/* ${name} æ ·å¼ */
body {
  font-family: Arial, sans-serif;
  margin: 0;
  padding: 20px;
  background: #f0f0f0;
}`);

    addFileInternal('script.js', folderPath, `// ${name} è„šæœ¬
console.log('${name} é¡µé¢åŠ è½½å®Œæˆ');`);

    // è‡ªåŠ¨æ‰“å¼€ index.html
    setCurrentFile(htmlPath);
    setCode(files[htmlPath] || '');
  };

  const deleteFile = (filePath) => {
    if (window.confirm('ç¡®å®šåˆ é™¤å—?')) {
      setProjects(projects.map(p => 
        p.id === currentProject 
          ? { ...p, files: p.files.filter(f => f.path !== filePath && !f.path.startsWith(filePath + '/')) }
          : p
      ));

      const newFiles = { ...files };
      delete newFiles[filePath];
      setFiles(newFiles);

      if (currentFile === filePath) {
        setCurrentFile(null);
        setCode('');
      }
    }
  };

  // é‡å‘½åæ–‡ä»¶/æ–‡ä»¶å¤¹
  const renameItem = (oldPath, isFolder) => {
    const oldName = oldPath.split('/').pop();
    const newName = prompt(`é‡å‘½å ${isFolder ? 'æ–‡ä»¶å¤¹' : 'æ–‡ä»¶'}ï¼ˆåŸåï¼š${oldName}ï¼‰:`, oldName);
    if (!newName || newName === oldName) return;
    if (newName.includes('/')) {
      alert('åç§°ä¸èƒ½åŒ…å« /');
      return;
    }

    const parent = oldPath.substring(0, oldPath.lastIndexOf('/'));
    const newPath = parent ? `${parent}/${newName}` : newName;

    const project = getCurrentProject();
    const exists = project.files.some(f => f.path === newPath);
    if (exists) {
      alert('åŒåæ–‡ä»¶/æ–‡ä»¶å¤¹å·²å­˜åœ¨');
      return;
    }

    // æ›´æ–° files å†…å®¹
    const newFiles = { ...files };
    if (files[oldPath] !== undefined) {
      newFiles[newPath] = files[oldPath];
      delete newFiles[oldPath];
    }
    setFiles(newFiles);

    // é€’å½’æ›´æ–°é¡¹ç›®ç»“æ„
    const renameRecursively = (items) => {
      return items.map(item => {
        if (item.path === oldPath) {
          return { ...item, name: newName, path: newPath };
        }
        if (item.path.startsWith(oldPath + '/')) {
          const suffix = item.path.substring(oldPath.length);
          return {
            ...item,
            path: newPath + suffix,
            parentPath: item.parentPath === oldPath ? newPath : item.parentPath
          };
        }
        return item;
      });
    };

    setProjects(prev => prev.map(p =>
      p.id === currentProject
        ? { ...p, files: renameRecursively(p.files) }
        : p
    ));

    // æ›´æ–°å½“å‰æ‰“å¼€æ–‡ä»¶
    if (currentFile === oldPath) {
      setCurrentFile(newPath);
    } else if (currentFile && currentFile.startsWith(oldPath + '/')) {
      const suffix = currentFile.substring(oldPath.length);
      setCurrentFile(newPath + suffix);
    }
  };

  const saveFile = () => {
    if (currentFile) {
      setFiles({ ...files, [currentFile]: code });
      alert('æ–‡ä»¶å·²ä¿å­˜!');
    }
  };

  const downloadFile = () => {
    if (!currentFile || !code) {
      alert('æ²¡æœ‰å¯ä¿å­˜çš„å†…å®¹');
      return;
    }

    const blob = new Blob([code], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = currentFile.split('/').pop();
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

