    setOutput('æ­£åœ¨æ‰§è¡Œ...');

    setTimeout(() => {
      try {
        const consoleOutput = [];

        const originalLog = console.log;
        const originalError = console.error;
        const originalWarn = console.warn;

        console.log = (...args) => {
          const output = args.map(arg => {
            if (typeof arg === 'object' && arg !== null) {
              try {
                return JSON.stringify(arg, null, 2);
              } catch (e) {
                return String(arg);
              }
            }
            return String(arg);
          }).join(' ');
          consoleOutput.push(output);
          originalLog(...args);
        };

        console.error = (...args) => {
          const output = 'âŒ ' + args.map(arg => String(arg)).join(' ');
          consoleOutput.push(output);
          originalError(...args);
        };

        console.warn = (...args) => {
          const output = 'âš ï¸ ' + args.map(arg => String(arg)).join(' ');
          consoleOutput.push(output);
          originalWarn(...args);
        };

        if (currentFile && currentFile.endsWith('.html')) {
          // æ³¨å…¥è¦†ç›–æ ·å¼ä»¥å…è®¸æ»šåŠ¨ï¼Œå¹¶æŠŠ HTML ä¿å­˜åˆ° previewHtml ç”¨äºçœŸå® iframe æ¸²æŸ“
          let preview = code || '';
          const injectStyle = '<style>html,body,main,section{overflow:auto !important;height:auto !important;min-height:100vh}.overflow-hidden{overflow:visible !important}</style>';
          if (/<head[^>]*>/i.test(preview)) {
            preview = preview.replace(/<head([^>]*)>/i, `<head$1>${injectStyle}`);
          } else if (/<html[^>]*>/i.test(preview)) {
            preview = preview.replace(/<html([^>]*)>/i, `<html$1><head>${injectStyle}</head>`);
          } else {
            preview = `<head>${injectStyle}</head>` + preview;
          }

          setPreviewHtml(preview);
          setOutput('');
        } else {
          // eslint-disable-next-line no-eval
          eval(code);

          console.log = originalLog;
          console.error = originalError;
          console.warn = originalWarn;

          if (consoleOutput.length > 0) {
            setOutput(consoleOutput.join('\n'));
          } else {
            setOutput('âœ… ä»£ç æ‰§è¡ŒæˆåŠŸï¼Œæ— è¾“å‡º');
          }
        }
