              {currentFile && currentFile.endsWith('.html') ? (
                previewHtml ? (
                  <iframe
                    ref={iframeRef}
                    title="preview"
                    srcDoc={previewHtml}
                    style={{ width: '100%', height: '100%', border: 'none', background: 'white' }}
                    onLoad={() => {
                      try {
                        const iframe = iframeRef.current;
                        if (!iframe) return;

                        // æ¸…ç†ä¸Šä¸€æ¬¡çš„ observer/intervalï¼ˆå¦‚æœå­˜åœ¨ï¼‰
                        if (iframe._previewCleanup) {
                          try { iframe._previewCleanup(); } catch (e) { /* ignore */ }
                          iframe._previewCleanup = null;
                        }

                        const doc = iframe.contentWindow?.document;
                        if (!doc) return;

                        const adjust = () => {
                          try {
                            const h = Math.max(doc.documentElement.scrollHeight || 0, doc.body.scrollHeight || 0);
                            if (h && iframe.style.height !== `${h}px`) {
                              iframe.style.height = `${h}px`;
                            }
                          } catch (e) {
                            // ignore
                          }
                        };

                        // åˆæ¬¡è°ƒæ•´
                        adjust();

                        // ç›‘å¬ DOM å˜åŒ–ä»¥åŠ¨æ€è°ƒæ•´é«˜åº¦ï¼ˆå¤„ç†è„šæœ¬ã€æ‡’åŠ è½½ç­‰ï¼‰
                        let observer = null;
                        try {
                          observer = new MutationObserver(() => adjust());
                          observer.observe(doc.body, { attributes: true, childList: true, subtree: true });
                        } catch (e) {
                          observer = null;
                        }

                        // ç›‘å¬å›¾ç‰‡åŠ è½½
                        const imgs = Array.from(doc.images || []);
                        imgs.forEach(img => img.addEventListener('load', adjust));

                        // å¤‡ç”¨çš„å®šæ—¶é‡è¯•ï¼ˆçŸ­æœŸå†…å¤šæ¬¡æµ‹é‡ï¼Œè¦†ç›–åŠ¨ç”»/å­—ä½“åŠ è½½å»¶è¿Ÿï¼‰
                        let checks = 0;
                        const intId = setInterval(() => {
                          adjust();
                          checks += 1;
                          if (checks > 30) clearInterval(intId);
                        }, 100);

                        // å­˜å‚¨æ¸…ç†æ–¹æ³•
                        iframe._previewCleanup = () => {
                          try { if (observer) observer.disconnect(); } catch (e) {}
                          try { imgs.forEach(img => img.removeEventListener('load', adjust)); } catch (e) {}
                          try { clearInterval(intId); } catch (e) {}
                        };
                      } catch (e) {
                        console.warn('æ— æ³•è°ƒæ•´ iframe å¤§å°', e);
                      }
                    }}
