import React from 'react';
import { Code, LogOut, Calendar, CheckCircle, Edit2 } from 'lucide-react';

const StudentDashboard = ({ user, data, onOpenEditor, onLogout }) => {
  const assignments = data.assignments.filter(a => 
    a.targetClass === 'ÊâÄÊúâÁè≠Á∫ß' || a.targetClass === user.class
  );

  const mySubmissions = data.submissions.filter(s => s.studentId === user.id);
  const completedCount = mySubmissions.length;
  const pendingCount = assignments.length - completedCount;

  return (
    <div style={{ minHeight: '100vh', background: '#f5f7fa' }}>
      {/* È°∂ÈÉ®ÂØºËà™ */}
      <header style={{
        background: '#fff', padding: '16px 40px',
        borderBottom: '1px solid #eee',
        display: 'flex', justifyContent: 'space-between', alignItems: 'center'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
          <div style={{
            width: '40px', height: '40px', borderRadius: '10px',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            display: 'flex', alignItems: 'center', justifyContent: 'center'
          }}>
            <Code size={20} color="#fff" />
          </div>
          <span style={{ fontWeight: '600', fontSize: '18px', color: '#1a1a2e' }}>ÁºñÁ®ãÊïôÂ≠¶Âπ≥Âè∞</span>
        </div>
        
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
            <span style={{ fontSize: '28px' }}>{user.avatar || 'üë§'}</span>
            <div>
              <div style={{ fontWeight: '500', color: '#1a1a2e' }}>{user.name}</div>
              <div style={{ fontSize: '12px', color: '#888' }}>{user.class}</div>
            </div>
          </div>
          <button onClick={onLogout}
            style={{
              display: 'flex', alignItems: 'center', gap: '6px',
              padding: '10px 16px', background: '#f5f5f5', border: 'none',
              borderRadius: '10px', color: '#666', cursor: 'pointer'
            }}>
            <LogOut size={16} /> ÈÄÄÂá∫
          </button>
        </div>
      </header>

      <main style={{ padding: '40px', maxWidth: '1200px', margin: '0 auto' }}>
        {/* Ê¨¢ËøéÂç°Áâá */}
        <div style={{
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          borderRadius: '20px', padding: '40px', color: '#fff', marginBottom: '32px'
        }}>
          <h1 style={{ fontSize: '28px', fontWeight: '600', marginBottom: '8px' }}>
            Ê¨¢ËøéÂõûÊù•Ôºå{user.name}ÔºÅ
          </h1>
          <p style={{ opacity: 0.9, marginBottom: '24px' }}>ÁªßÁª≠‰Ω†ÁöÑÁºñÁ®ãÂ≠¶‰π†‰πãÊóÖÂêß</p>
          <div style={{ display: 'flex', gap: '32px' }}>
            <div>
              <div style={{ fontSize: '36px', fontWeight: '700' }}>{completedCount}</div>
              <div style={{ opacity: 0.8 }}>Â∑≤ÂÆåÊàê‰Ωú‰∏ö</div>
            </div>
            <div>
              <div style={{ fontSize: '36px', fontWeight: '700' }}>{pendingCount}</div>
              <div style={{ opacity: 0.8 }}>ÂæÖÂÆåÊàê‰Ωú‰∏ö</div>
            </div>
          </div>
        </div>

        {/* ‰Ωú‰∏öÂàóË°® */}
        <h2 style={{ fontSize: '20px', fontWeight: '600', color: '#1a1a2e', marginBottom: '20px' }}>
          ÊàëÁöÑ‰Ωú‰∏ö
        </h2>
        
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(340px, 1fr))', gap: '20px' }}>
          {assignments.map(assign => {
            const submission = mySubmissions.find(s => s.assignmentId === assign.id);
            const isOverdue = new Date(assign.deadline) < new Date();
            const isCompleted = !!submission;

            return (
              <div key={assign.id} className="card-hover" style={{
                background: '#fff', borderRadius: '16px', overflow: 'hidden',
                boxShadow: '0 2px 12px rgba(0,0,0,0.04)'
              }}>
                <div style={{
                  height: '8px',
                  background: isCompleted 
                    ? 'linear-gradient(90deg, #52c41a, #73d13d)'
                    : isOverdue 
                      ? 'linear-gradient(90deg, #f5222d, #ff4d4f)'
                      : 'linear-gradient(90deg, #667eea, #764ba2)'
                }} />
                <div style={{ padding: '24px' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '12px' }}>
                    <h3 style={{ fontSize: '18px', fontWeight: '600', color: '#1a1a2e', margin: 0 }}>
                      {assign.title}
                    </h3>
                    {isCompleted ? (
                      <span style={{
                        padding: '4px 12px', background: '#f6ffed',
                        color: '#389e0d', borderRadius: '20px', fontSize: '12px',
                        display: 'flex', alignItems: 'center', gap: '4px'
                      }}>
                        <CheckCircle size={12} /> Â∑≤ÂÆåÊàê
                      </span>
                    ) : isOverdue ? (
                      <span style={{
                        padding: '4px 12px', background: '#fff1f0',
                        color: '#cf1322', borderRadius: '20px', fontSize: '12px'
                      }}>
                        Â∑≤Êà™Ê≠¢
                      </span>
                    ) : (
                      <span style={{
                        padding: '4px 12px', background: '#e6f7ff',
                        color: '#1890ff', borderRadius: '20px', fontSize: '12px'
                      }}>
                        ËøõË°å‰∏≠
                      </span>
                    )}
                  </div>
                  
                  <p style={{ color: '#666', fontSize: '14px', margin: '0 0 16px', lineHeight: 1.6 }}>
                    {assign.description}
                  </p>
                  
                  <div style={{
                    display: 'flex', alignItems: 'center', gap: '8px',
                    fontSize: '13px', color: '#888', marginBottom: '20px'
                  }}>
                    <Calendar size={14} />
                    <span>Êà™Ê≠¢Êó•ÊúüÔºö{assign.deadline}</span>
                  </div>

                  <button onClick={() => onOpenEditor({
                    mode: 'student_work',
                    initialFiles: submission ? submission.files : assign.template,
                    projectName: assign.title,
                    assignmentId: assign.id
                  })}
                    style={{
                      width: '100%', padding: '14px',
                      background: isCompleted 
                        ? '#f5f5f5' 
                        : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                      border: 'none', borderRadius: '12px',
                      color: isCompleted ? '#666' : '#fff',
                      fontWeight: '500', cursor: 'pointer',
                      display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px'
                    }}>
                    {isCompleted ? (
                      <><Edit2 size={16} /> Êü•Áúã/‰øÆÊîπ</>
                    ) : (
                      <><Code size={16} /> ÂºÄÂßã‰Ωú‰∏ö</>
                    )}
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </main>
    </div>
  );
};

export default StudentDashboard;
