import React, { useState } from 'react';
import { 
  Code, Users, FileText, CheckCircle, Clock, Plus, Edit2, Trash2,
  Calendar, LogOut, X
} from 'lucide-react';
import StudentManagement from './StudentManagement';

// ä½œä¸šåˆ›å»º/ç¼–è¾‘æ¨¡æ€æ¡†
const AssignmentModal = ({ assignment, onClose, onSave }) => {
  const [form, setForm] = useState({
    title: assignment?.title || '',
    description: assignment?.description || '',
    requirements: assignment?.requirements || '',
    deadline: assignment?.deadline || '',
    targetClass: assignment?.targetClass || 'æ‰€æœ‰ç­çº§',
    template: assignment?.template || { 'index.html': '<!-- è¯·åœ¨æ­¤å¤„ç¼–å†™ä»£ç  -->' }
  });

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.5)', display: 'flex',
      alignItems: 'center', justifyContent: 'center', zIndex: 1000
    }}>
      <div style={{
        background: '#fff', borderRadius: '20px', width: '560px',
        maxHeight: '90vh', overflow: 'auto'
      }}>
        <div style={{ padding: '24px 28px', borderBottom: '1px solid #f0f0f0' }}>
          <h2 style={{ margin: 0, fontSize: '20px', fontWeight: '600' }}>
            {assignment ? 'ç¼–è¾‘ä½œä¸š' : 'å‘å¸ƒæ–°ä½œä¸š'}
          </h2>
        </div>
        
        <div style={{ padding: '28px' }}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: '500', color: '#333' }}>ä½œä¸šæ ‡é¢˜</label>
            <input
              value={form.title}
              onChange={(e) => setForm({ ...form, title: e.target.value })}
              placeholder="ä¾‹å¦‚ï¼šä¸ªäººä¸»é¡µåˆ¶ä½œ"
              style={{
                width: '100%', padding: '12px 16px', border: '2px solid #eee',
                borderRadius: '10px', fontSize: '14px', outline: 'none', boxSizing: 'border-box'
              }}
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: '500', color: '#333' }}>ç®€è¦æè¿°</label>
            <input
              value={form.description}
              onChange={(e) => setForm({ ...form, description: e.target.value })}
              placeholder="ä¸€å¥è¯æè¿°ä½œä¸šå†…å®¹"
              style={{
                width: '100%', padding: '12px 16px', border: '2px solid #eee',
                borderRadius: '10px', fontSize: '14px', outline: 'none', boxSizing: 'border-box'
              }}
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: '500', color: '#333' }}>è¯¦ç»†è¦æ±‚</label>
            <textarea
              value={form.requirements}
              onChange={(e) => setForm({ ...form, requirements: e.target.value })}
              placeholder="è¯¦ç»†è¯´æ˜ä½œä¸šè¦æ±‚..."
              rows={4}
              style={{
                width: '100%', padding: '12px 16px', border: '2px solid #eee',
                borderRadius: '10px', fontSize: '14px', outline: 'none',
                resize: 'vertical', boxSizing: 'border-box'
              }}
            />
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px', marginBottom: '20px' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '500', color: '#333' }}>æˆªæ­¢æ—¥æœŸ</label>
              <input
                type="date"
                value={form.deadline}
                onChange={(e) => setForm({ ...form, deadline: e.target.value })}
                style={{
                  width: '100%', padding: '12px 16px', border: '2px solid #eee',
                  borderRadius: '10px', fontSize: '14px', outline: 'none', boxSizing: 'border-box'
                }}
              />
            </div>
            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '500', color: '#333' }}>ç›®æ ‡ç­çº§</label>
              <select
                value={form.targetClass}
                onChange={(e) => setForm({ ...form, targetClass: e.target.value })}
                style={{
                  width: '100%', padding: '12px 16px', border: '2px solid #eee',
                  borderRadius: '10px', fontSize: '14px', outline: 'none',
                  background: '#fff', boxSizing: 'border-box'
                }}
              >
                <option>æ‰€æœ‰ç­çº§</option>
                <option>ä¸‰å¹´äºŒç­</option>
                <option>ä¸‰å¹´ä¸‰ç­</option>
              </select>
            </div>
          </div>
        </div>

        <div style={{
          padding: '20px 28px', borderTop: '1px solid #f0f0f0',
          display: 'flex', justifyContent: 'flex-end', gap: '12px'
        }}>
          <button onClick={onClose}
            style={{
              padding: '12px 24px', background: '#f5f5f5', border: 'none',
              borderRadius: '10px', cursor: 'pointer', fontWeight: '500'
            }}>
            å–æ¶ˆ
          </button>
          <button onClick={() => onSave(form)}
            style={{
              padding: '12px 24px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              border: 'none', borderRadius: '10px', color: '#fff',
              cursor: 'pointer', fontWeight: '500'
            }}>
            {assignment ? 'ä¿å­˜ä¿®æ”¹' : 'å‘å¸ƒä½œä¸š'}
          </button>
        </div>
      </div>
    </div>
  );
};

// æ•™å¸ˆä»ªè¡¨ç›˜
const TeacherDashboard = ({ data, setData, onOpenEditor, onLogout, user }) => {
  const [activeTab, setActiveTab] = useState('assignments');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [editingAssignment, setEditingAssignment] = useState(null);

  const stats = {
    totalStudents: data.students.length,
    totalAssignments: data.assignments.length,
    totalSubmissions: data.submissions.length,
    pendingReview: data.submissions.filter(s => !s.reviewed).length
  };

  const menuItems = [
    { id: 'assignments', icon: FileText, label: 'ä½œä¸šç®¡ç†' },
    { id: 'students', icon: Users, label: 'å­¦ç”Ÿç®¡ç†' },
    { id: 'submissions', icon: CheckCircle, label: 'æäº¤è®°å½•' }
  ];

  return (
    <div style={{ display: 'flex', minHeight: '100vh', background: '#f5f7fa' }}>
      {/* ä¾§è¾¹æ  */}
      <div style={{
        width: '260px', background: '#fff', borderRight: '1px solid #eee',
        display: 'flex', flexDirection: 'column'
      }}>
        <div style={{ padding: '24px', borderBottom: '1px solid #eee' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
            <div style={{
              width: '48px', height: '48px', borderRadius: '12px',
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              display: 'flex', alignItems: 'center', justifyContent: 'center'
            }}>
              <Code size={24} color="#fff" />
            </div>
            <div>
              <div style={{ fontWeight: '600', color: '#1a1a2e' }}>ç¼–ç¨‹æ•™å­¦å¹³å°</div>
              <div style={{ fontSize: '13px', color: '#888' }}>æ•™å¸ˆç«¯</div>
            </div>
          </div>
        </div>

        <nav style={{ flex: 1, padding: '16px' }}>
          {menuItems.map(item => (
            <button key={item.id} onClick={() => setActiveTab(item.id)}
              style={{
                width: '100%', display: 'flex', alignItems: 'center', gap: '12px',
                padding: '14px 16px', marginBottom: '8px',
                background: activeTab === item.id ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' : 'transparent',
                border: 'none', borderRadius: '12px', cursor: 'pointer',
                color: activeTab === item.id ? '#fff' : '#666',
                fontWeight: activeTab === item.id ? '600' : '400',
                transition: 'all 0.3s'
              }}>
              <item.icon size={20} />
              {item.label}
            </button>
          ))}
        </nav>

        <div style={{ padding: '16px', borderTop: '1px solid #eee' }}>
          <div style={{
            display: 'flex', alignItems: 'center', gap: '12px',
            padding: '12px', background: '#f8f9ff', borderRadius: '12px', marginBottom: '12px'
          }}>
            <div style={{
              width: '40px', height: '40px', borderRadius: '10px',
              background: '#667eea', display: 'flex', alignItems: 'center', justifyContent: 'center',
              color: '#fff', fontWeight: '600'
            }}>
              {user.name[0]}
            </div>
            <div>
              <div style={{ fontWeight: '500', color: '#1a1a2e' }}>{user.name}</div>
              <div style={{ fontSize: '12px', color: '#888' }}>æ•™å¸ˆ</div>
            </div>
          </div>
          <button onClick={onLogout}
            style={{
              width: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px',
              padding: '12px', background: '#fff', border: '1px solid #eee',
              borderRadius: '10px', color: '#666', cursor: 'pointer'
            }}>
            <LogOut size={16} /> é€€å‡ºç™»å½•
          </button>
        </div>
      </div>

      {/* ä¸»å†…å®¹åŒº */}
      <div style={{ flex: 1, padding: '32px', overflow: 'auto' }}>
        {/* ç»Ÿè®¡å¡ç‰‡ */}
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '20px', marginBottom: '32px' }}>
          {[
            { label: 'å­¦ç”Ÿæ€»æ•°', value: stats.totalStudents, icon: Users, color: '#667eea' },
            { label: 'ä½œä¸šæ•°é‡', value: stats.totalAssignments, icon: FileText, color: '#52c41a' },
            { label: 'æäº¤è®°å½•', value: stats.totalSubmissions, icon: CheckCircle, color: '#faad14' },
            { label: 'å¾…æ‰¹æ”¹', value: stats.pendingReview, icon: Clock, color: '#f5222d' }
          ].map((stat, idx) => (
            <div key={idx} className="card-hover" style={{
              background: '#fff', borderRadius: '16px', padding: '24px',
              boxShadow: '0 2px 12px rgba(0,0,0,0.04)'
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
                <div>
                  <div style={{ fontSize: '14px', color: '#888', marginBottom: '8px' }}>{stat.label}</div>
                  <div style={{ fontSize: '32px', fontWeight: '700', color: '#1a1a2e' }}>{stat.value}</div>
                </div>
                <div style={{
                  width: '48px', height: '48px', borderRadius: '12px',
                  background: `${stat.color}15`, display: 'flex', alignItems: 'center', justifyContent: 'center'
                }}>
                  <stat.icon size={24} color={stat.color} />
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* ä½œä¸šç®¡ç† */}
        {activeTab === 'assignments' && (
          <div className="animate-fade-in">
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: '600', color: '#1a1a2e', margin: 0 }}>ä½œä¸šç®¡ç†</h2>
              <button onClick={() => { setEditingAssignment(null); setShowCreateModal(true); }}
                style={{
                  display: 'flex', alignItems: 'center', gap: '8px',
                  padding: '12px 24px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  border: 'none', borderRadius: '12px', color: '#fff',
                  fontWeight: '500', cursor: 'pointer'
                }}>
                <Plus size={18} /> å‘å¸ƒæ–°ä½œä¸š
              </button>
            </div>

            <div style={{ display: 'grid', gap: '16px' }}>
              {data.assignments.map(assign => {
                const submissionCount = data.submissions.filter(s => s.assignmentId === assign.id).length;
                const isOverdue = new Date(assign.deadline) < new Date();
                
                return (
                  <div key={assign.id} className="card-hover" style={{
                    background: '#fff', borderRadius: '16px', padding: '24px',
                    boxShadow: '0 2px 12px rgba(0,0,0,0.04)',
                    borderLeft: `4px solid ${isOverdue ? '#f5222d' : '#52c41a'}`
                  }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                      <div style={{ flex: 1 }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '8px' }}>
                          <h3 style={{ fontSize: '18px', fontWeight: '600', color: '#1a1a2e', margin: 0 }}>
                            {assign.title}
                          </h3>
                          <span style={{
                            padding: '4px 12px', borderRadius: '20px', fontSize: '12px',
                            background: isOverdue ? '#fff1f0' : '#f6ffed',
                            color: isOverdue ? '#cf1322' : '#389e0d'
                          }}>
                            {isOverdue ? 'å·²æˆªæ­¢' : 'è¿›è¡Œä¸­'}
                          </span>
                        </div>
                        <p style={{ color: '#666', fontSize: '14px', margin: '0 0 16px' }}>{assign.description}</p>
                        <div style={{ display: 'flex', gap: '24px', fontSize: '13px', color: '#888' }}>
                          <span style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
                            <Calendar size={14} /> æˆªæ­¢ï¼š{assign.deadline}
                          </span>
                          <span style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
                            <Users size={14} /> {assign.targetClass}
                          </span>
                          <span style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
                            <CheckCircle size={14} /> {submissionCount} äººå·²æäº¤
                          </span>
                        </div>
                      </div>
                      <div style={{ display: 'flex', gap: '8px' }}>
                        <button onClick={() => { setEditingAssignment(assign); setShowCreateModal(true); }}
                          style={{
                            padding: '10px', background: '#f5f5f5', border: 'none',
                            borderRadius: '10px', cursor: 'pointer'
                          }}>
                          <Edit2 size={16} color="#666" />
                        </button>
                        <button onClick={() => {
                          if (window.confirm('ç¡®å®šåˆ é™¤è¯¥ä½œä¸šå—ï¼Ÿ')) {
                            setData({ ...data, assignments: data.assignments.filter(a => a.id !== assign.id) });
                          }
                        }}
                          style={{
                            padding: '10px', background: '#fff1f0', border: 'none',
                            borderRadius: '10px', cursor: 'pointer'
                          }}>
                          <Trash2 size={16} color="#cf1322" />
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* å­¦ç”Ÿç®¡ç† */}
        {activeTab === 'students' && (
          <StudentManagement data={data} setData={setData} />
        )}

        {/* æäº¤è®°å½• */}
        {activeTab === 'submissions' && (
          <div className="animate-fade-in">
            <h2 style={{ fontSize: '24px', fontWeight: '600', color: '#1a1a2e', marginBottom: '24px' }}>æäº¤è®°å½•</h2>
            {data.submissions.length === 0 ? (
              <div style={{
                background: '#fff', borderRadius: '16px', padding: '60px',
                textAlign: 'center', color: '#888'
              }}>
                <FileText size={48} color="#ddd" style={{ marginBottom: '16px' }} />
                <p>æš‚æ— æäº¤è®°å½•</p>
              </div>
            ) : (
              <div style={{ display: 'grid', gap: '16px' }}>
                {data.submissions.map(sub => {
                  const student = data.students.find(s => s.id === sub.studentId);
                  const assignment = data.assignments.find(a => a.id === sub.assignmentId);
                  return (
                    <div key={sub.id} style={{
                      background: '#fff', borderRadius: '16px', padding: '20px',
                      boxShadow: '0 2px 12px rgba(0,0,0,0.04)'
                    }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
                          <span style={{ fontSize: '24px' }}>{student?.avatar || 'ğŸ‘¤'}</span>
                          <div>
                            <div style={{ fontWeight: '500' }}>{student?.name || 'æœªçŸ¥å­¦ç”Ÿ'}</div>
                            <div style={{ fontSize: '13px', color: '#888' }}>{assignment?.title || 'æœªçŸ¥ä½œä¸š'}</div>
                          </div>
                        </div>
                        <div style={{ fontSize: '13px', color: '#888' }}>
                          {new Date(sub.timestamp).toLocaleString()}
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        )}
      </div>

      {/* åˆ›å»º/ç¼–è¾‘ä½œä¸šæ¨¡æ€æ¡† */}
      {showCreateModal && (
        <AssignmentModal
          assignment={editingAssignment}
          onClose={() => setShowCreateModal(false)}
          onSave={(assignmentData) => {
            if (editingAssignment) {
              setData({
                ...data,
                assignments: data.assignments.map(a => a.id === editingAssignment.id ? { ...a, ...assignmentData } : a)
              });
            } else {
              setData({
                ...data,
                assignments: [...data.assignments, { id: `a${Date.now()}`, ...assignmentData, createdAt: new Date().toISOString().split('T')[0], status: 'active' }]
              });
            }
            setShowCreateModal(false);
          }}
        />
      )}
    </div>
  );
};

export default TeacherDashboard;
