    console.log('é¡µé¢åŠ è½½å®Œæˆ');
  </script>
</body>
</html>`,
  'Reactç»„ä»¶': `import React, { useState } from 'react';

function Counter() {
  const [count, setCount] = useState(0);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2>è®¡æ•°å™¨: {count}</h2>
      <button 
        onClick={() => setCount(count + 1)}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer'
        }}
      >
        å¢åŠ 
      </button>
      <button 
        onClick={() => setCount(0)}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          marginLeft: '10px'
        }}
      >
        é‡ç½®
      </button>
