    // 2. æ–‡ä»¶ååŒ¹é…
    else if (window.__VFS__.hasOwnProperty(fileName)) {
      content = window.__VFS__[fileName];
    }
    // 3. éå†æŸ¥æ‰¾
    else {
      for (const key in window.__VFS__) {
        if (key.endsWith('/' + cleanPath) || key.endsWith('/' + fileName) || key === cleanPath) {
          content = window.__VFS__[key];
          break;
        }
      }
    }

    if (content !== null) {
      console.log('âœ… ä» VFS åŠ è½½:', cleanPath);

      // ç¡®å®š MIME ç±»å‹
      let mimeType = 'text/plain';
      if (cleanPath.endsWith('.json')) mimeType = 'application/json';
      else if (cleanPath.endsWith('.csv')) mimeType = 'text/csv';
      else if (cleanPath.endsWith('.txt')) mimeType = 'text/plain';
      else if (cleanPath.endsWith('.js')) mimeType = 'application/javascript';
      else if (cleanPath.endsWith('.css')) mimeType = 'text/css';

      const response = new Response(content, {
        status: 200,
        statusText: 'OK',
        headers: { 'Content-Type': mimeType }
      });
      return Promise.resolve(response);
    } else {
      console.error('âŒ æ–‡ä»¶æœªæ‰¾åˆ°:', cleanPath);
      return Promise.reject(new Error('File not found: ' + cleanPath));
    }
  }

