import React, { useState } from 'react';
import { User, Lock, LogIn, Code, Eye, EyeOff, AlertCircle, UserPlus, Mail, Phone } from 'lucide-react';

const LoginPage = ({ onLogin, students }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [userType, setUserType] = useState('student');
  const [isRegisterMode, setIsRegisterMode] = useState(false);
  
  // Ê≥®ÂÜåË°®ÂçïÂ≠óÊÆµ
  const [registerData, setRegisterData] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    name: '',
    email: '',
    phone: ''
  });
  const [showRegisterPassword, setShowRegisterPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('ËØ∑ËæìÂÖ•Áî®Êà∑ÂêçÂíåÂØÜÁ†Å');
      return;
    }
    
    setIsLoading(true);
    setError('');
    
    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password, role: userType })
      });
      
      if (!response.ok) {
        const error = await response.json();
        setError(error.error || 'ÁôªÂΩïÂ§±Ë¥•');
        setIsLoading(false);
        return;
      }
      
      const userData = await response.json();
      onLogin(userData);
    } catch (error) {
      console.error('ÁôªÂΩïÈîôËØØ:', error);
      setError('ÁΩëÁªúÈîôËØØÔºåËØ∑Ê£ÄÊü•ÂêéÁ´ØÊúçÂä°ÊòØÂê¶ÂêØÂä®Ôºàhttp://localhost:5000Ôºâ');
    }
    
    setIsLoading(false);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    // Ë°®ÂçïÈ™åËØÅ
    if (!registerData.username || !registerData.password || !registerData.name) {
      setError('ËØ∑Â°´ÂÜôÂøÖÂ°´È°π');
      return;
    }
    
    if (registerData.password !== registerData.confirmPassword) {
      setError('‰∏§Ê¨°ËæìÂÖ•ÁöÑÂØÜÁ†Å‰∏ç‰∏ÄËá¥');
      return;
    }
    
    if (registerData.password.length < 6) {
      setError('ÂØÜÁ†ÅÈïøÂ∫¶Ëá≥Â∞ë‰∏∫6‰Ωç');
      return;
    }
    
    if (registerData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(registerData.email)) {
      setError('ÈÇÆÁÆ±Ê†ºÂºè‰∏çÊ≠£Á°Æ');
      return;
    }
    
    setIsLoading(true);
    
    try {
      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username: registerData.username,
          password: registerData.password,
          name: registerData.name,
          email: registerData.email,
          phone: registerData.phone,
          role: userType
        })
      });
      
      if (!response.ok) {
        const error = await response.json();
        setError(error.error || 'Ê≥®ÂÜåÂ§±Ë¥•');
        setIsLoading(false);
        return;
      }
      
      setSuccess('Ê≥®ÂÜåÊàêÂäüÔºÅÂç≥Â∞ÜË∑≥ËΩ¨Âà∞ÁôªÂΩï...');
      setTimeout(() => {
        setIsRegisterMode(false);
        setUsername(registerData.username);
        setRegisterData({
          username: '', password: '', confirmPassword: '',
          name: '', email: '', phone: ''
        });
        setSuccess('');
      }, 1500);
    } catch (error) {
      console.error('Ê≥®ÂÜåÈîôËØØ:', error);
      setError('ÁΩëÁªúÈîôËØØÔºåËØ∑Ê£ÄÊü•ÂêéÁ´ØÊúçÂä°ÊòØÂê¶ÂêØÂä®Ôºàhttp://localhost:5000Ôºâ');
    }
    
    setIsLoading(false);
  };

  const switchMode = () => {
    setIsRegisterMode(!isRegisterMode);
    setError('');
    setSuccess('');
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)'
    }}>
      {/* Â∑¶‰æßË£ÖÈ•∞Âå∫Âüü */}
      <div style={{
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '60px',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
      }}>
        <div style={{ textAlign: 'center', color: '#fff' }}>
          <div style={{
            width: '120px', height: '120px',
            background: 'rgba(255,255,255,0.2)',
            borderRadius: '30px',
            display: 'flex', alignItems: 'center', justifyContent: 'center',
            margin: '0 auto 32px',
            backdropFilter: 'blur(10px)'
