        </div>
      </div>

      {/* Âè≥‰æßÁôªÂΩïÂå∫Âüü */}
      <div style={{
        width: '500px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        padding: '60px',
        background: '#fff'
      }}>
        <div style={{ maxWidth: '360px', margin: '0 auto', width: '100%' }}>
          <h2 style={{ fontSize: '28px', fontWeight: '600', color: '#1a1a2e', marginBottom: '8px' }}>
            Ê¨¢ËøéÂõûÊù•
          </h2>
          <p style={{ color: '#666', marginBottom: '32px' }}>ËØ∑ÁôªÂΩïÊÇ®ÁöÑË¥¶Êà∑</p>

          {/* Áî®Êà∑Á±ªÂûãÂàáÊç¢ */}
          <div style={{
            display: 'flex',
            background: '#f5f5f5',
            borderRadius: '12px',
            padding: '4px',
            marginBottom: '24px'
          }}>
            <button
              onClick={() => setUserType('student')}
              style={{
                flex: 1, padding: '12px',
                background: userType === 'student' ? '#fff' : 'transparent',
                border: 'none', borderRadius: '10px',
                fontWeight: '500', cursor: 'pointer',
                color: userType === 'student' ? '#667eea' : '#666',
                boxShadow: userType === 'student' ? '0 2px 8px rgba(0,0,0,0.1)' : 'none',
                transition: 'all 0.3s'
              }}
            >
              üë®‚Äçüéì Â≠¶ÁîüÁôªÂΩï
            </button>
            <button
              onClick={() => setUserType('teacher')}
              style={{
                flex: 1, padding: '12px',
                background: userType === 'teacher' ? '#fff' : 'transparent',
                border: 'none', borderRadius: '10px',
                fontWeight: '500', cursor: 'pointer',
                color: userType === 'teacher' ? '#667eea' : '#666',
                boxShadow: userType === 'teacher' ? '0 2px 8px rgba(0,0,0,0.1)' : 'none',
                transition: 'all 0.3s'
              }}
            >
              üë®‚Äçüè´ ÊïôÂ∏àÁôªÂΩï
            </button>
          </div>

          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{ display: 'block', marginBottom: '8px', color: '#333', fontWeight: '500' }}>
                {userType === 'student' ? 'Â≠¶Âè∑' : 'Áî®Êà∑Âêç'}
              </label>
              <div style={{ position: 'relative' }}>
                <User size={18} color="#999" style={{ position: 'absolute', left: '16px', top: '50%', transform: 'translateY(-50%)' }} />
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder={userType === 'student' ? 'ËØ∑ËæìÂÖ•Â≠¶Âè∑' : 'ËØ∑ËæìÂÖ•Áî®Êà∑Âêç'}
                  style={{
                    width: '100%', padding: '14px 16px 14px 48px',
