import React, { useState, useRef, useEffect } from 'react';
import { 
  Play, FolderPlus, FilePlus, RefreshCw, Save, Trash2, FileCode, Folder, 
  ChevronRight, ChevronDown, Upload, Download, Edit2, ArrowLeft, Send,
  Code, Eye, X, Check, AlertCircle, File, Settings, Maximize2
} from 'lucide-react';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { oneDark } from 'react-syntax-highlighter/dist/esm/styles/prism';

// 简化的模板
const TEMPLATES = {
  'HTML 页面': { file: 'index.html', content: `<!DOCTYPE html>
<html lang="zh-CN">
<head>
  <meta charset="UTF-8">
  <title>我的页面</title>
  <style>
    body { font-family: Arial, sans-serif; padding: 20px; }
  </style>
</head>
<body>
  <h1>Hello World!</h1>
</body>
</html>` },
  'JavaScript': { file: 'script.js', content: `// JavaScript 代码
console.log('Hello World!');` },
  'CSS 样式': { file: 'style.css', content: `/* CSS 样式 */
body {
  margin: 0;
  padding: 20px;
}` }
};

// ==========================================
// 文件选择提交模态框
// ==========================================
const SubmitModal = ({ show, files, projectFiles, onClose, onSubmit }) => {
  const [selectedFiles, setSelectedFiles] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const availableFiles = projectFiles.filter(f => f.type === 'file');

  useEffect(() => {
    // 默认全选
    const initial = {};
    availableFiles.forEach(f => { initial[f.path] = true; });
    setSelectedFiles(initial);
  }, []);

  const toggleFile = (path) => {
    setSelectedFiles(prev => ({ ...prev, [path]: !prev[path] }));
  };

  const handleSubmit = async () => {
    const selected = Object.entries(selectedFiles).filter(([_, v]) => v).map(([k]) => k);
    if (selected.length === 0) { alert('请至少选择一个文件'); return; }
    
    setIsSubmitting(true);
    const submitFiles = {};
    selected.forEach(path => { submitFiles[path] = files[path] || ''; });
    await onSubmit(submitFiles);
    setIsSubmitting(false);
    onClose();
  };

  if (!show) return null;

  const selectedCount = Object.values(selectedFiles).filter(Boolean).length;

  return (
    <div style={{
      position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.6)',
      display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000
    }}>
      <div style={{
        background: '#fff', borderRadius: '20px', width: '480px',
        maxHeight: '80vh', display: 'flex', flexDirection: 'column',
        boxShadow: '0 25px 60px rgba(0,0,0,0.3)'
      }}>
        <div style={{
          padding: '24px 28px', borderBottom: '1px solid #f0f0f0',
          display: 'flex', justifyContent: 'space-between', alignItems: 'center'
        }}>
          <div>
            <h2 style={{ margin: 0, fontSize: '20px', fontWeight: '600', color: '#1a1a2e' }}>提交作业</h2>
            <p style={{ margin: '4px 0 0', fontSize: '14px', color: '#888' }}>选择要提交的文件</p>
          </div>
          <button onClick={onClose} style={{
            width: '36px', height: '36px', borderRadius: '10px',
            border: 'none', background: '#f5f5f5', cursor: 'pointer',
            display: 'flex', alignItems: 'center', justifyContent: 'center'
          }}>
            <X size={18} color="#666" />
          </button>
        </div>

        <div style={{ flex: 1, overflow: 'auto', padding: '20px 28px' }}>
          {availableFiles.length === 0 ? (
            <div style={{ textAlign: 'center', padding: '40px', color: '#888' }}>
              <File size={40} color="#ddd" style={{ marginBottom: '12px' }} />
              <p>暂无可提交的文件</p>
            </div>
          ) : (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
              {availableFiles.map(file => {
                const ext = file.name.split('.').pop().toLowerCase();
                const colors = { html: '#e34c26', css: '#264de4', js: '#f7df1e', json: '#000' };
                const color = colors[ext] || '#888';
                
                return (
                  <label key={file.path} style={{
                    display: 'flex', alignItems: 'center', gap: '14px',
                    padding: '14px 16px', borderRadius: '12px', cursor: 'pointer',
                    background: selectedFiles[file.path] ? '#f0f5ff' : '#f8f9fa',
                    border: `2px solid ${selectedFiles[file.path] ? '#667eea' : 'transparent'}`,
                    transition: 'all 0.2s'
                  }}>
                    <input type="checkbox" checked={!!selectedFiles[file.path]}
                      onChange={() => toggleFile(file.path)}
                      style={{ width: '18px', height: '18px', accentColor: '#667eea' }} />
                    <div style={{
                      width: '40px', height: '40px', borderRadius: '10px',
                      background: `${color}15`, display: 'flex',
                      alignItems: 'center', justifyContent: 'center'
                    }}>
                      <span style={{ fontSize: '11px', fontWeight: '700', color }}>{ext.toUpperCase()}</span>
                    </div>
                    <div style={{ flex: 1 }}>
                      <div style={{ fontWeight: '500', color: '#1a1a2e' }}>{file.name}</div>
                      <div style={{ fontSize: '12px', color: '#888' }}>
                        {files[file.path] ? `${(files[file.path].length / 1024).toFixed(1)} KB` : '空文件'}
                      </div>
                    </div>
                    {selectedFiles[file.path] && <Check size={18} color="#667eea" />}
                  </label>
                );
              })}
            </div>
          )}
        </div>

        <div style={{
          padding: '20px 28px', borderTop: '1px solid #f0f0f0',
          display: 'flex', justifyContent: 'space-between', alignItems: 'center'
        }}>
          <span style={{ fontSize: '14px', color: '#888' }}>
            已选 <strong style={{ color: '#667eea' }}>{selectedCount}</strong> 个文件
          </span>
          <div style={{ display: 'flex', gap: '12px' }}>
            <button onClick={onClose} style={{
              padding: '12px 24px', background: '#f5f5f5', border: 'none',
              borderRadius: '10px', cursor: 'pointer', fontWeight: '500', color: '#666'
            }}>取消</button>
            <button onClick={handleSubmit} disabled={selectedCount === 0 || isSubmitting}
              style={{
                padding: '12px 28px',
                background: selectedCount === 0 ? '#ccc' : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                border: 'none', borderRadius: '10px', color: '#fff',
                fontWeight: '500', cursor: selectedCount === 0 ? 'not-allowed' : 'pointer',
                display: 'flex', alignItems: 'center', gap: '8px'
              }}>
              {isSubmitting ? '提交中...' : <><Send size={16} /> 确认提交</>}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// ==========================================
// 主编辑器组件
// ==========================================
function FullEditor({ initialFiles = {}, projectName = '默认项目', onBack, onSave, onSubmit, mode = 'playground' }) {
  const [files, setFiles] = useState({});
  const [fileList, setFileList] = useState([]);
  const [currentFile, setCurrentFile] = useState(null);
  const [code, setCode] = useState('');
  const [previewHtml, setPreviewHtml] = useState('');
  const [showSubmitModal, setShowSubmitModal] = useState(false);
  const [showTemplates, setShowTemplates] = useState(false);
  const [activePanel, setActivePanel] = useState('preview'); // 'preview' | 'console'
  const [consoleOutput, setConsoleOutput] = useState([]);
  
  const fileInputRef = useRef(null);

  useEffect(() => {
    if (initialFiles && Object.keys(initialFiles).length > 0) {
      const list = Object.keys(initialFiles).map(path => ({
        id: path, name: path.split('/').pop(), path, type: 'file', parentPath: ''
      }));
      setFileList(list);
      setFiles(initialFiles);
      if (list.length > 0) {
        setCurrentFile(list[0].path);
        setCode(initialFiles[list[0].path] || '');
      }
    }
  }, [initialFiles]);

  const addFile = (name, content = '') => {
    if (!name) return;
    const newFile = { id: Date.now().toString(), name, path: name, type: 'file', parentPath: '' };
    setFileList(prev => [...prev, newFile]);
    setFiles(prev => ({ ...prev, [name]: content }));
    setCurrentFile(name);
    setCode(content);
  };

  const deleteFile = (path) => {
    if (!window.confirm('确定删除此文件吗？')) return;
    setFileList(prev => prev.filter(f => f.path !== path));
    const newFiles = { ...files };
    delete newFiles[path];
    setFiles(newFiles);
    if (currentFile === path) {
      const remaining = fileList.filter(f => f.path !== path);
      if (remaining.length > 0) {
        setCurrentFile(remaining[0].path);
        setCode(newFiles[remaining[0].path] || '');
      } else {
        setCurrentFile(null);
        setCode('');
      }
    }
  };

  const openFile = (path) => {
    // 保存当前文件
    if (currentFile) {
      setFiles(prev => ({ ...prev, [currentFile]: code }));
    }
    setCurrentFile(path);
    setCode(files[path] || '');
  };

  const saveFile = () => {
    if (currentFile) {
      const updated = { ...files, [currentFile]: code };
      setFiles(updated);
      if (onSave) onSave(updated);
    }
  };

  const runCode = () => {
    // 先保存当前文件
    const updatedFiles = { ...files, [currentFile]: code };
    setFiles(updatedFiles);

    if (!currentFile?.endsWith('.html')) {
      // 运行 JS
      const output = [];
      const originalLog = console.log;
      console.log = (...args) => {
        output.push({ type: 'log', content: args.map(a => typeof a === 'object' ? JSON.stringify(a, null, 2) : String(a)).join(' ') });
        originalLog(...args);
      };
      try {
        // eslint-disable-next-line no-eval
        eval(code);
        setConsoleOutput(output.length > 0 ? output : [{ type: 'success', content: '✅ 代码执行成功，无输出' }]);
      } catch (e) {
        setConsoleOutput([{ type: 'error', content: `❌ ${e.message}` }]);
      }
      console.log = originalLog;
      setActivePanel('console');
      return;
    }

    // 运行 HTML
    let html = code;
    
    // 内联 CSS
    html = html.replace(/<link[^>]+href=["']([^"']+\.css)["'][^>]*>/gi, (match, path) => {
      const cssContent = updatedFiles[path] || updatedFiles[path.replace('./', '')];
      return cssContent ? `<style>\n${cssContent}\n</style>` : match;
    });

    // 内联 JS
    html = html.replace(/<script[^>]+src=["']([^"']+\.js)["'][^>]*><\/script>/gi, (match, path) => {
      const jsContent = updatedFiles[path] || updatedFiles[path.replace('./', '')];
      return jsContent ? `<script>\n${jsContent}\n</script>` : match;
    });

    setPreviewHtml(html);
    setActivePanel('preview');
  };

  const openFullscreen = () => {
    if (!previewHtml) { alert('请先运行代码'); return; }
    const win = window.open('', '_blank');
    if (win) {
      win.document.open();
      win.document.write(previewHtml);
      win.document.close();
    }
  };

  const getLanguage = (filename) => {
    if (!filename) return 'javascript';
    const ext = filename.split('.').pop().toLowerCase();
    return { js: 'javascript', html: 'html', css: 'css', json: 'json' }[ext] || 'javascript';
  };

  const getFileIcon = (name) => {
    const ext = name.split('.').pop().toLowerCase();
    const colors = { html: '#e34c26', css: '#264de4', js: '#f7df1e' };
    return colors[ext] || '#888';
  };

  return (
    <div style={{ display: 'flex', height: '100vh', background: '#1e1e2e', fontFamily: "'Inter', -apple-system, sans-serif" }}>
      {/* 侧边栏 */}
      <div style={{ width: '260px', background: '#252536', borderRight: '1px solid #333', display: 'flex', flexDirection: 'column' }}>
        {/* 头部 */}
        <div style={{ padding: '16px 20px', borderBottom: '1px solid #333', display: 'flex', alignItems: 'center', gap: '12px' }}>
          {onBack && (
            <button onClick={onBack} style={{
              width: '36px', height: '36px', borderRadius: '10px',
              background: 'rgba(255,255,255,0.1)', border: 'none',
              cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center'
            }}>
              <ArrowLeft size={18} color="#fff" />
            </button>
          )}
          <div style={{ flex: 1, overflow: 'hidden' }}>
            <div style={{ fontWeight: '600', color: '#fff', fontSize: '15px', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
              {projectName}
            </div>
            <div style={{ fontSize: '12px', color: '#888' }}>{fileList.length} 个文件</div>
          </div>
        </div>

        {/* 工具按钮 */}
        <div style={{ padding: '12px', display: 'flex', gap: '8px', borderBottom: '1px solid #333' }}>
          <button onClick={() => {
            const name = prompt('输入文件名 (如: index.html)');
            if (name) addFile(name);
          }} style={{
            flex: 1, padding: '10px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            border: 'none', borderRadius: '10px', color: '#fff',
            fontWeight: '500', cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '6px'
          }}>
            <FilePlus size={16} /> 新建
          </button>
          <button onClick={() => setShowTemplates(!showTemplates)} style={{
            padding: '10px 14px', background: 'rgba(255,255,255,0.1)',
            border: 'none', borderRadius: '10px', color: '#fff', cursor: 'pointer'
          }}>
            <Code size={16} />
          </button>
          <button onClick={() => fileInputRef.current?.click()} style={{
            padding: '10px 14px', background: 'rgba(255,255,255,0.1)',
            border: 'none', borderRadius: '10px', color: '#fff', cursor: 'pointer'
          }}>
            <Upload size={16} />
          </button>
        </div>

        <input ref={fileInputRef} type="file" multiple style={{ display: 'none' }}
          accept=".html,.css,.js,.json,.txt"
          onChange={(e) => {
            Array.from(e.target.files).forEach(file => {
              const reader = new FileReader();
              reader.onload = (ev) => addFile(file.name, ev.target.result);
              reader.readAsText(file);
            });
            e.target.value = '';
          }} />

        {/* 模板列表 */}
        {showTemplates && (
          <div style={{ padding: '12px', borderBottom: '1px solid #333' }}>
            {Object.entries(TEMPLATES).map(([name, { file, content }]) => (
              <button key={name} onClick={() => { addFile(file, content); setShowTemplates(false); }}
                style={{
                  width: '100%', padding: '10px 12px', marginBottom: '6px',
                  background: 'rgba(255,255,255,0.05)', border: '1px solid #444',
                  borderRadius: '8px', color: '#ccc', cursor: 'pointer',
                  textAlign: 'left', fontSize: '13px'
                }}>
                {name}
              </button>
            ))}
          </div>
        )}

        {/* 文件列表 */}
        <div style={{ flex: 1, overflow: 'auto', padding: '12px' }}>
          <div style={{ fontSize: '11px', color: '#666', fontWeight: '600', marginBottom: '12px', letterSpacing: '0.5px' }}>
            文件列表
          </div>
          {fileList.filter(f => f.type === 'file').map(file => (
            <div key={file.path} onClick={() => openFile(file.path)}
              style={{
                display: 'flex', alignItems: 'center', gap: '10px',
                padding: '10px 12px', marginBottom: '4px', borderRadius: '8px',
                background: currentFile === file.path ? 'rgba(102, 126, 234, 0.2)' : 'transparent',
                border: currentFile === file.path ? '1px solid rgba(102, 126, 234, 0.4)' : '1px solid transparent',
                cursor: 'pointer', transition: 'all 0.2s'
              }}>
              <div style={{
                width: '8px', height: '8px', borderRadius: '50%',
                background: getFileIcon(file.name)
              }} />
              <span style={{ flex: 1, color: currentFile === file.path ? '#fff' : '#aaa', fontSize: '13px' }}>
                {file.name}
              </span>
              <button onClick={(e) => { e.stopPropagation(); deleteFile(file.path); }}
                style={{ background: 'none', border: 'none', cursor: 'pointer', padding: '4px', opacity: 0.5 }}>
                <Trash2 size={14} color="#f66" />
              </button>
            </div>
          ))}
          {fileList.length === 0 && (
            <div style={{ textAlign: 'center', padding: '40px 20px', color: '#666' }}>
              <FileCode size={32} style={{ marginBottom: '12px', opacity: 0.5 }} />
              <p style={{ fontSize: '13px', margin: 0 }}>点击上方按钮<br />新建或导入文件</p>
            </div>
          )}
        </div>
      </div>

      {/* 主区域 */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* 顶部工具栏 */}
        <div style={{
          padding: '12px 20px', background: '#252536', borderBottom: '1px solid #333',
          display: 'flex', alignItems: 'center', justifyContent: 'space-between'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
            {currentFile && (
              <div style={{
                padding: '6px 14px', background: 'rgba(255,255,255,0.1)',
                borderRadius: '8px', color: '#fff', fontSize: '13px', fontWeight: '500'
              }}>
                {currentFile}
              </div>
            )}
          </div>
          
          <div style={{ display: 'flex', gap: '10px' }}>
            <button onClick={saveFile} disabled={!currentFile}
              style={{
                padding: '10px 18px', background: 'rgba(255,255,255,0.1)',
                border: 'none', borderRadius: '10px', color: '#fff',
                cursor: currentFile ? 'pointer' : 'not-allowed', opacity: currentFile ? 1 : 0.5,
                display: 'flex', alignItems: 'center', gap: '6px', fontWeight: '500'
              }}>
              <Save size={16} /> 保存
            </button>
            <button onClick={runCode} disabled={!currentFile}
              style={{
                padding: '10px 20px',
                background: currentFile ? 'linear-gradient(135deg, #52c41a 0%, #73d13d 100%)' : '#555',
                border: 'none', borderRadius: '10px', color: '#fff',
                cursor: currentFile ? 'pointer' : 'not-allowed',
                display: 'flex', alignItems: 'center', gap: '6px', fontWeight: '500'
              }}>
              <Play size={16} /> 运行
            </button>
            {mode === 'student_work' && onSubmit && (
              <button onClick={() => setShowSubmitModal(true)}
                style={{
                  padding: '10px 20px',
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  border: 'none', borderRadius: '10px', color: '#fff',
                  cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '6px', fontWeight: '500'
                }}>
                <Send size={16} /> 提交
              </button>
            )}
          </div>
        </div>

        {/* 编辑器和预览 */}
        <div style={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
          {/* 代码编辑器 */}
          <div style={{ flex: 1, display: 'flex', flexDirection: 'column', borderRight: '1px solid #333' }}>
            <div style={{ flex: 1, position: 'relative', overflow: 'hidden' }}>
              <textarea
                value={code}
                onChange={(e) => setCode(e.target.value)}
                placeholder="选择文件开始编辑..."
                spellCheck="false"
                style={{
                  position: 'absolute', inset: 0, width: '100%', height: '100%',
                  padding: '16px', paddingLeft: '56px',
                  background: 'transparent', color: 'transparent', caretColor: '#fff',
                  border: 'none', outline: 'none', resize: 'none',
                  fontFamily: "'Fira Code', 'Monaco', monospace", fontSize: '14px', lineHeight: 1.7,
                  zIndex: 2, WebkitTextFillColor: 'transparent'
                }}
              />
              <div style={{ position: 'absolute', inset: 0, overflow: 'auto', zIndex: 1 }}>
                <SyntaxHighlighter
                  language={getLanguage(currentFile)}
                  style={oneDark}
                  showLineNumbers
                  customStyle={{
                    margin: 0, padding: '16px', background: '#1e1e2e',
                    fontSize: '14px', lineHeight: 1.7,
                    fontFamily: "'Fira Code', 'Monaco', monospace",
                    minHeight: '100%'
                  }}
                  lineNumberStyle={{ color: '#555', minWidth: '40px' }}
                >
                  {code || ' '}
                </SyntaxHighlighter>
              </div>
            </div>
          </div>

          {/* 预览/控制台 */}
          <div style={{ width: '45%', display: 'flex', flexDirection: 'column', background: '#1a1a2e' }}>
            {/* 面板切换 */}
            <div style={{ display: 'flex', borderBottom: '1px solid #333' }}>
              <button onClick={() => setActivePanel('preview')}
                style={{
                  flex: 1, padding: '12px', background: activePanel === 'preview' ? '#252536' : 'transparent',
                  border: 'none', borderBottom: activePanel === 'preview' ? '2px solid #667eea' : 'none',
                  color: activePanel === 'preview' ? '#fff' : '#888',
                  cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '6px'
                }}>
                <Eye size={16} /> 预览
              </button>
              <button onClick={() => setActivePanel('console')}
                style={{
                  flex: 1, padding: '12px', background: activePanel === 'console' ? '#252536' : 'transparent',
                  border: 'none', borderBottom: activePanel === 'console' ? '2px solid #667eea' : 'none',
                  color: activePanel === 'console' ? '#fff' : '#888',
                  cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '6px'
                }}>
                <Code size={16} /> 控制台
              </button>
              {previewHtml && (
                <button onClick={openFullscreen} style={{
                  padding: '12px', background: 'transparent', border: 'none',
                  color: '#888', cursor: 'pointer'
                }}>
                  <Maximize2 size={16} />
                </button>
              )}
            </div>

            {/* 面板内容 */}
            <div style={{ flex: 1, overflow: 'auto' }}>
              {activePanel === 'preview' ? (
                previewHtml ? (
                  <iframe srcDoc={previewHtml} title="preview"
                    style={{ width: '100%', height: '100%', border: 'none', background: '#fff' }} />
                ) : (
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%', color: '#666' }}>
                    <div style={{ textAlign: 'center' }}>
                      <Eye size={48} style={{ marginBottom: '16px', opacity: 0.3 }} />
                      <p>点击「运行」查看预览</p>
                    </div>
                  </div>
                )
              ) : (
                <div style={{ padding: '16px', fontFamily: "'Fira Code', monospace", fontSize: '13px' }}>
                  {consoleOutput.length === 0 ? (
                    <div style={{ color: '#666' }}>控制台输出将显示在这里</div>
                  ) : (
                    consoleOutput.map((item, idx) => (
                      <div key={idx} style={{
                        padding: '8px 12px', marginBottom: '6px', borderRadius: '6px',
                        background: item.type === 'error' ? 'rgba(255,77,79,0.1)' : 'rgba(255,255,255,0.05)',
                        color: item.type === 'error' ? '#ff4d4f' : item.type === 'success' ? '#52c41a' : '#ddd'
                      }}>
                        {item.content}
                      </div>
                    ))
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* 提交模态框 */}
      <SubmitModal
        show={showSubmitModal}
        files={files}
        projectFiles={fileList}
        onClose={() => setShowSubmitModal(false)}
        onSubmit={onSubmit}
      />
    </div>
  );
}

export default FullEditor;
