      // Âä†ËΩΩÊâÄÊúâÈîöÁÇπ
      console.log('üîó Ê≠£Âú®Ëé∑ÂèñÈîöÁÇπÊï∞ÊçÆ...');
      const anchorResponse = await fetch(`http://localhost:5000/api/courseware/${coursewareId}/anchors`);
