  .card-hover {
    transition: all 0.3s ease;
  }
  .card-hover:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 40px rgba(0,0,0,0.15);
  }
`;

// ==========================================
// æ¨¡æ‹Ÿæ•°æ®
// ==========================================
const INITIAL_DATA = {
  students: [
    { id: 's1', name: 'å¼ ä¸‰', class: 'ä¸‰å¹´äºŒç­', avatar: 'ğŸ‘¨â€ğŸ“' },
    { id: 's2', name: 'æå››', class: 'ä¸‰å¹´äºŒç­', avatar: 'ğŸ‘©â€ğŸ“' },
    { id: 's3', name: 'ç‹äº”', class: 'ä¸‰å¹´ä¸‰ç­', avatar: 'ğŸ‘¨â€ğŸ“' }
  ],
  assignments: [
    { 
      id: 'a1', 
      title: 'ä¸ªäººä¸»é¡µåˆ¶ä½œ', 
      description: 'ä½¿ç”¨HTMLå’ŒCSSåˆ¶ä½œä¸€ä¸ªä¸ªäººä»‹ç»é¡µé¢',
      requirements: 'åŒ…å«å¤´åƒã€å§“åå’Œä¸€æ®µè‡ªæˆ‘ä»‹ç»ï¼ŒèƒŒæ™¯è‰²éœ€ä¸ºæµ…ç°è‰²ã€‚',
      deadline: '2025-12-31',
      targetClass: 'æ‰€æœ‰ç­çº§',
      status: 'active',
      createdAt: '2025-01-15',
      template: { 'index.html': '<!DOCTYPE html>\n<html>\n<head>\n  <title>æˆ‘çš„ä¸»é¡µ</title>\n</head>\n<body>\n  <!-- åœ¨è¿™é‡Œç¼–å†™ä½ çš„ä»£ç  -->\n</body>\n</html>' }
    },
    { 
      id: 'a2', 
      title: 'JavaScriptè®¡ç®—å™¨', 
      description: 'å®ç°ä¸€ä¸ªç®€å•çš„å››åˆ™è¿ç®—è®¡ç®—å™¨',
      requirements: 'æ”¯æŒåŠ å‡ä¹˜é™¤è¿ç®—ï¼Œæœ‰æ¸…é™¤åŠŸèƒ½ã€‚',
      deadline: '2025-12-25',
      targetClass: 'ä¸‰å¹´äºŒç­',
      status: 'active',
      createdAt: '2025-01-10',
      template: { 'index.html': '<!-- è®¡ç®—å™¨æ¨¡æ¿ -->', 'script.js': '// åœ¨è¿™é‡Œç¼–å†™JSä»£ç ' }
    }
  ],
  submissions: [],
  codeRepository: []
};

// ==========================================
// ç¾åŒ–çš„ç™»å½•é¡µé¢
// ==========================================
const LoginPage = ({ onLogin, students }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [userType, setUserType] = useState('student');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('è¯·è¾“å…¥ç”¨æˆ·åå’Œå¯†ç ');
      return;
    }

    setIsLoading(true);
    setError('');
    await new Promise(resolve => setTimeout(resolve, 600));

    if (userType === 'teacher' && username === 'teacher' && password === 'teacher') {
      onLogin({ role: 'teacher', name: 'æ•™å¸ˆ', id: 'teacher' });
    } else if (userType === 'student') {
      const s = students.find(stu => stu.id === username);
      if (s) {
        onLogin({ ...s, role: 'student' });
      } else {
        setError('å­¦å·ä¸å­˜åœ¨');
      }
    } else {
      setError('ç”¨æˆ·åæˆ–å¯†ç é”™è¯¯');
    }
    setIsLoading(false);
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)'
    }}>
      <style>{globalStyles}</style>

      {/* å·¦ä¾§è£…é¥°åŒºåŸŸ */}
      <div style={{
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '60px',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
      }}>
        <div style={{ textAlign: 'center', color: '#fff' }}>
          <div style={{
            width: '120px', height: '120px',
            background: 'rgba(255,255,255,0.2)',
            borderRadius: '30px',
            display: 'flex', alignItems: 'center', justifyContent: 'center',
            margin: '0 auto 32px',
            backdropFilter: 'blur(10px)'
          }}>
            <Code size={60} color="#fff" />
          </div>
          <h1 style={{ fontSize: '36px', fontWeight: '700', marginBottom: '16px' }}>
            ç¼–ç¨‹æ•™å­¦å¹³å°
          </h1>
          <p style={{ fontSize: '18px', opacity: 0.9, maxWidth: '400px', lineHeight: 1.6 }}>
            åœ¨çº¿ç¼–å†™ã€è¿è¡Œä»£ç <br />
            è½»æ¾å®Œæˆç¼–ç¨‹ä½œä¸š
          </p>
        </div>
      </div>

      {/* å³ä¾§ç™»å½•åŒºåŸŸ */}
      <div style={{
        width: '500px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        padding: '60px',
        background: '#fff'
      }}>
        <div style={{ maxWidth: '360px', margin: '0 auto', width: '100%' }}>
          <h2 style={{ fontSize: '28px', fontWeight: '600', color: '#1a1a2e', marginBottom: '8px' }}>
            æ¬¢è¿å›æ¥
          </h2>
          <p style={{ color: '#666', marginBottom: '32px' }}>è¯·ç™»å½•æ‚¨çš„è´¦æˆ·</p>

          {/* ç”¨æˆ·ç±»å‹åˆ‡æ¢ */}
          <div style={{
            display: 'flex',
            background: '#f5f5f5',
            borderRadius: '12px',
            padding: '4px',
            marginBottom: '24px'
          }}>
            <button
              onClick={() => setUserType('student')}
              style={{
                flex: 1, padding: '12px',
                background: userType === 'student' ? '#fff' : 'transparent',
                border: 'none', borderRadius: '10px',
                fontWeight: '500', cursor: 'pointer',
                color: userType === 'student' ? '#667eea' : '#666',
                boxShadow: userType === 'student' ? '0 2px 8px rgba(0,0,0,0.1)' : 'none',
                transition: 'all 0.3s'
              }}
            >
              ğŸ‘¨â€ğŸ“ å­¦ç”Ÿç™»å½•
            </button>
            <button
              onClick={() => setUserType('teacher')}
              style={{
                flex: 1, padding: '12px',
                background: userType === 'teacher' ? '#fff' : 'transparent',
                border: 'none', borderRadius: '10px',
                fontWeight: '500', cursor: 'pointer',
                color: userType === 'teacher' ? '#667eea' : '#666',
                boxShadow: userType === 'teacher' ? '0 2px 8px rgba(0,0,0,0.1)' : 'none',
                transition: 'all 0.3s'
              }}
            >
              ğŸ‘¨â€ğŸ« æ•™å¸ˆç™»å½•
            </button>
          </div>

          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{ display: 'block', marginBottom: '8px', color: '#333', fontWeight: '500' }}>
                {userType === 'student' ? 'å­¦å·' : 'ç”¨æˆ·å'}
              </label>
              <div style={{ position: 'relative' }}>
                <User size={18} color="#999" style={{ position: 'absolute', left: '16px', top: '50%', transform: 'translateY(-50%)' }} />
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder={userType === 'student' ? 'è¯·è¾“å…¥å­¦å·' : 'è¯·è¾“å…¥ç”¨æˆ·å'}
                  style={{
                    width: '100%', padding: '14px 16px 14px 48px',
                    border: '2px solid #eee', borderRadius: '12px',
                    fontSize: '15px', outline: 'none',
                    transition: 'border-color 0.3s', boxSizing: 'border-box'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#667eea'}
                  onBlur={(e) => e.target.style.borderColor = '#eee'}
                />
              </div>
            </div>

            <div style={{ marginBottom: '24px' }}>
              <label style={{ display: 'block', marginBottom: '8px', color: '#333', fontWeight: '500' }}>å¯†ç </label>
              <div style={{ position: 'relative' }}>
                <Lock size={18} color="#999" style={{ position: 'absolute', left: '16px', top: '50%', transform: 'translateY(-50%)' }} />
                <input
                  type={showPassword ? 'text' : 'password'}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="è¯·è¾“å…¥å¯†ç "
                  style={{
                    width: '100%', padding: '14px 48px 14px 48px',
                    border: '2px solid #eee', borderRadius: '12px',
                    fontSize: '15px', outline: 'none',
                    transition: 'border-color 0.3s', boxSizing: 'border-box'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#667eea'}
                  onBlur={(e) => e.target.style.borderColor = '#eee'}
                />
                <button type="button" onClick={() => setShowPassword(!showPassword)}
                  style={{ position: 'absolute', right: '16px', top: '50%', transform: 'translateY(-50%)', background: 'none', border: 'none', cursor: 'pointer' }}>
                  {showPassword ? <Eye size={18} color="#999" /> : <EyeOff size={18} color="#999" />}
                </button>
              </div>
            </div>

            {error && (
              <div style={{
                padding: '12px 16px', background: '#fff5f5', border: '1px solid #ffccc7',
                borderRadius: '10px', marginBottom: '20px', color: '#cf1322',
                display: 'flex', alignItems: 'center', gap: '8px'
              }}>
                <AlertCircle size={16} /> {error}
              </div>
            )}

            <button type="submit" disabled={isLoading}
              style={{
                width: '100%', padding: '14px',
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                border: 'none', borderRadius: '12px',
                color: '#fff', fontSize: '16px', fontWeight: '600',
                cursor: isLoading ? 'not-allowed' : 'pointer',
                opacity: isLoading ? 0.7 : 1,
                display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px'
              }}>
              {isLoading ? (
                <><div style={{ width: '20px', height: '20px', border: '2px solid rgba(255,255,255,0.3)', borderTopColor: '#fff', borderRadius: '50%', animation: 'spin 0.8s linear infinite' }} /> ç™»å½•ä¸­...</>
              ) : (
                <><LogIn size={18} /> ç™» å½•</>
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

// ==========================================
// æ•™å¸ˆä»ªè¡¨ç›˜
// ==========================================
const TeacherDashboard = ({ data, setData, onOpenEditor, onLogout, user }) => {
  const [activeTab, setActiveTab] = useState('assignments');
  const [showCreateModal, setShowCreateModal] = useState(false);
