    { id: 'submissions', icon: CheckCircle, label: 'æäº¤è®°å½•' }
  ];

  return (
    <div style={{ display: 'flex', minHeight: '100vh', background: '#f5f7fa' }}>
      <style>{globalStyles}</style>

      {/* ä¾§è¾¹æ  */}
