function App() {
  const [data, setData] = useState(INITIAL_DATA);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('login');
  const [editorConfig, setEditorConfig] = useState(null);

  const handleSubmission = async (selectedFiles) => {
    const assign = data.assignments.find(a => a.id === editorConfig.assignmentId);
    if (assign && user.role === 'student') {
      const newSub = {
        id: `sub${Date.now()}`,
        studentId: user.id,
        studentName: user.name,
        assignmentId: assign.id,
        assignmentTitle: assign.title,
        files: selectedFiles,
        timestamp: Date.now()
      };

      try {
        await fetch('http://localhost:5000/api/submissions', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newSub)
        });
      } catch (e) {
        console.log('APIè°ƒç”¨å¤±è´¥ï¼Œä½¿ç”¨æœ¬åœ°å­˜å‚¨');
      }

      const others = data.submissions.filter(s => !(s.studentId === user.id && s.assignmentId === assign.id));
      setData(prev => ({ ...prev, submissions: [...others, newSub] }));
      alert('âœ… ä½œä¸šæäº¤æˆåŠŸï¼');
    }
  };

  return (
    <>
      {view === 'login' && (
        <LoginPage 
          onLogin={(userData) => { setUser(userData); setView('dashboard'); }}
          students={data.students}
        />
      )}

      {view === 'dashboard' && user?.role === 'teacher' && (
        <TeacherDashboard
          data={data}
          setData={setData}
          user={user}
          onOpenEditor={(config) => { setEditorConfig(config); setView('editor'); }}
          onLogout={() => { setUser(null); setView('login'); }}
        />
      )}

      {view === 'dashboard' && user?.role === 'student' && (
        <StudentDashboard
          user={user}
          data={data}
          onOpenEditor={(config) => { setEditorConfig(config); setView('editor'); }}
          onLogout={() => { setUser(null); setView('login'); }}
        />
      )}

      {view === 'editor' && (
        <FullEditor
          {...editorConfig}
          onBack={() => setView('dashboard')}
          onSubmit={(files) => { handleSubmission(files); setView('dashboard'); }}
        />
      )}
    </>
  );
}

export default App;
