    const fileName = nameMap[templateName] || `${templateName}.txt`;
    const filePath = addFileInternal(fileName, '', TEMPLATES[templateName]);

    setCurrentFile(filePath);
    setCode(TEMPLATES[templateName]);
    setShowTemplates(false);
  };

  // ä¸Šä¼ æ–‡ä»¶ï¼ˆæ”¯æŒå›¾ç‰‡ç­‰äºŒè¿›åˆ¶æ–‡ä»¶ï¼‰
  const handleFileUpload = (e) => {
    const uploadedFiles = Array.from(e.target.files);
    
    uploadedFiles.forEach(file => {
      const filePath = file.name;
      const newFile = {
        id: Date.now().toString() + Math.random(),
        name: file.name,
        path: filePath,
        type: 'file',
        parentPath: ''
      };
      
      // åˆ¤æ–­æ˜¯å¦ä¸ºå›¾ç‰‡æˆ–äºŒè¿›åˆ¶æ–‡ä»¶
      const isImage = file.type.startsWith('image/');
      const isBinary = !file.type.startsWith('text/') && !file.name.match(/\.(html|css|js|jsx|json|txt|md)$/i);
      
      if (isImage || isBinary) {
        // å›¾ç‰‡å’ŒäºŒè¿›åˆ¶æ–‡ä»¶è½¬ä¸º base64
        const reader = new FileReader();
        reader.onload = (event) => {
          const content = event.target.result; // data URL
          
          setProjects(projects.map(p => 
            p.id === currentProject 
              ? { ...p, files: [...p.files, newFile] }
