          ));
          setFiles({ ...files, [filePath]: content });
          setCurrentFile(filePath);
          setCode(`/* äºŒè¿›åˆ¶æ–‡ä»¶: ${file.name} */\n/* ç±»å‹: ${file.type} */\n/* å¤§å°: ${(file.size / 1024).toFixed(2)} KB */\n\n/* æ­¤æ–‡ä»¶å·²è½¬æ¢ä¸º base64 æ ¼å¼ï¼Œå¯åœ¨ HTML ä¸­ä½¿ç”¨ */\n/* ç¤ºä¾‹: <img src="${filePath}" alt="${file.name}"> */`);
        };
        reader.readAsDataURL(file);
      } else {
        // æ–‡æœ¬æ–‡ä»¶æ­£å¸¸è¯»å–
        const reader = new FileReader();
        reader.onload = (event) => {
          const content = event.target.result;
          
          setProjects(projects.map(p => 
            p.id === currentProject 
              ? { ...p, files: [...p.files, newFile] }
              : p
          ));
          setFiles({ ...files, [filePath]: content });
          setCurrentFile(filePath);
          setCode(content);
        };
        reader.readAsText(file);
      }
    });
    
    // é‡ç½® input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // åŠ è½½ç¤ºä¾‹ CSV æ–‡ä»¶ï¼ˆReact åº”ç”¨å±‚æ•°æ®åŠ è½½ï¼‰
  const loadSampleCsv = async () => {
    try {
      // ä½¿ç”¨ d3.csv() ä» public æ–‡ä»¶å¤¹åŠ è½½
      const data = await d3.csv('/data/sample.csv');
      
      // å­˜å‚¨åˆ° React state
      setD3Data(data);
      
      alert('âœ… ç¤ºä¾‹æ•°æ®åŠ è½½æˆåŠŸï¼\nå…± ' + data.length + ' æ¡æ•°æ®');
    } catch (err) {
      alert('âŒ åŠ è½½ç¤ºä¾‹ CSV å¤±è´¥ï¼š' + (err.message || err));
    }
  };

  // å¤„ç†ç”¨æˆ·ä»æœ¬åœ°ä¸Šä¼  CSV
  const handleCsvUpload = (e) => {
    const file = e.target.files && e.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    reader.onload = (ev) => {
      try {
        const text = ev.target.result;
        
        // æ·»åŠ æ–‡ä»¶åˆ°å½“å‰é¡¹ç›®
        const filePath = file.name;
        const newFile = {
          id: Date.now().toString() + Math.random(),
          name: file.name,
          path: filePath,
          type: 'file',
          parentPath: ''
        };
        
        setProjects(prev => prev.map(p => 
          p.id === currentProject 
            ? { ...p, files: [...p.files, newFile] }
            : p
        ));
        setFiles(prev => ({ ...prev, [filePath]: text }));
        
        // æ‰“å¼€æ–‡ä»¶åˆ°ç¼–è¾‘å™¨
        setCurrentFile(filePath);
        setCode(text);
        
        // åŒæ—¶è§£ææ•°æ®ç”¨äºå¯è§†åŒ–
        const parsed = d3.csvParse(text);
        setD3Data(parsed);
        
        alert('âœ… CSV æ–‡ä»¶å·²æ·»åŠ åˆ°é¡¹ç›®ï¼\næ–‡ä»¶å: ' + file.name + '\nå…± ' + parsed.length + ' æ¡æ•°æ®');
      } catch (err) {
        alert('âŒ CSV å¤„ç†å¤±è´¥ï¼š' + (err.message || err));
      }
    };
    reader.readAsText(file, 'utf-8');
    
    // é‡ç½® input å€¼ä»¥ä¾¿ä¸‹æ¬¡å¯å¤ç”¨åŒä¸€æ–‡ä»¶
    e.target.value = '';
  };

  // æ‹–åŠ¨åˆ†éš”æ¡
  const handleMouseDown = () => {
