const TestAnchorDemo = () => {
  const [testResult, setTestResult] = useState('');
  const [loading, setLoading] = useState(false);

  const testAPI = async () => {
    setLoading(true);
    setTestResult('ÂºÄÂßãÊµãËØï...\n');
    
