      // ÊµãËØïËØæ‰ª∂È¢ÑËßàAPI
      console.log('üß™ ÊµãËØïËØæ‰ª∂È¢ÑËßàAPI...');
      setTestResult(prev => prev + 'üß™ ÊµãËØïËØæ‰ª∂È¢ÑËßàAPI...\n');
      
      const response = await fetch('http://localhost:5000/api/courseware/4/preview');
