        setTestResult(prev => prev + `üìä ËøîÂõûÊï∞ÊçÆ:\n${JSON.stringify(data, null, 2)}\n`);
        
        // ÊµãËØïÁ¨¨‰∏ÄÂº†ÂõæÁâáÊòØÂê¶ÂèØ‰ª•ËÆøÈóÆ
        if (data.slides && data.slides.length > 0) {
          const firstSlide = data.slides[0];
          const imageUrl = typeof firstSlide === 'object' ? 
            `http://localhost:5000${firstSlide.imageUrl}` : 
            firstSlide;
          
          setTestResult(prev => prev + `üñºÔ∏è ÊµãËØïÁ¨¨‰∏ÄÂº†ÂõæÁâá: ${imageUrl}\n`);
          
          // ÂàõÂª∫ÂõæÁâáÂÖÉÁ¥†ÊµãËØïÂä†ËΩΩ
