          img.src = imageUrl;
        }
        
      } else {
        const error = await response.text();
        console.error('‚ùå ÈîôËØØÂìçÂ∫î:', error);
        setTestResult(prev => prev + `‚ùå ÈîôËØØÂìçÂ∫î: ${error}\n`);
      }
      
    } catch (error) {
      console.error('‚ùå ËØ∑Ê±ÇÂ§±Ë¥•:', error);
      setTestResult(prev => prev + `‚ùå ËØ∑Ê±ÇÂ§±Ë¥•: ${error.message}\n`);
    } finally {
