      <button onclick="addTodo()">æ·»åŠ </button>
    </div>
    <ul id="todoList"></ul>
  </div>
  <script>
    let todos = [];
    function addTodo() {
      const input = document.getElementById('todoInput');
      const text = input.value.trim();
      if (text) {
        todos.push(text);
        input.value = '';
        render();
      }
    }
    function render() {
      const list = document.getElementById('todoList');
      list.innerHTML = todos.map(t => '<li>' + t + '</li>').join('');
    }
  </script>
</body>
