};

function App() {
  const [projects, setProjects] = useState([
    { id: '1', name: 'é»˜è®¤é¡¹ç›®', files: [] }
  ]);
  const [currentProject, setCurrentProject] = useState('1');
  const [files, setFiles] = useState({});
  const [currentFile, setCurrentFile] = useState(null);
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [expandedFolders, setExpandedFolders] = useState({});
  const [showTemplates, setShowTemplates] = useState(false);
  const [editorWidth, setEditorWidth] = useState(60); // ç¼–è¾‘å™¨å®½åº¦ç™¾åˆ†æ¯”
  const [isDragging, setIsDragging] = useState(false);

  const fileInputRef = useRef(null);
  const containerRef = useRef(null);

  const getCurrentProject = () => projects.find(p => p.id === currentProject);

  const addProject = () => {
    const name = prompt('è¾“å…¥é¡¹ç›®åç§°:');
    if (name) {
      const newProject = {
        id: Date.now().toString(),
        name,
        files: []
      };
      setProjects([...projects, newProject]);
      setCurrentProject(newProject.id);
    }
  };

  const deleteProject = (id) => {
    if (projects.length === 1) {
      alert('è‡³å°‘ä¿ç•™ä¸€ä¸ªé¡¹ç›®');
      return;
    }
    if (window.confirm('ç¡®å®šåˆ é™¤æ­¤é¡¹ç›®å—?')) {
      setProjects(projects.filter(p => p.id !== id));
      if (currentProject === id) {
        setCurrentProject(projects[0].id);
      }
    }
  };

  const addFile = (parentPath = '') => {
    const name = prompt('è¾“å…¥æ–‡ä»¶å (å¦‚: index.html, script.js):');
    if (name) {
      const filePath = parentPath ? `${parentPath}/${name}` : name;
      const newFile = {
        id: Date.now().toString(),
        name,
        path: filePath,
        type: 'file',
        parentPath
      };

      setProjects(projects.map(p => 
        p.id === currentProject 
          ? { ...p, files: [...p.files, newFile] }
          : p
      ));
      setFiles({ ...files, [filePath]: '' });
      setCurrentFile(filePath);
      setCode('');
    }
  };

  const addFolder = (parentPath = '') => {
    const name = prompt('è¾“å…¥æ–‡ä»¶å¤¹åç§°:');
    if (name) {
      const folderPath = parentPath ? `${parentPath}/${name}` : name;
      const newFolder = {
        id: Date.now().toString(),
        name,
        path: folderPath,
        type: 'folder',
        parentPath
      };

      setProjects(projects.map(p => 
        p.id === currentProject 
          ? { ...p, files: [...p.files, newFolder] }
          : p
      ));
      setExpandedFolders({ ...expandedFolders, [folderPath]: true });
    }
  };

  const deleteFile = (filePath) => {
    if (window.confirm('ç¡®å®šåˆ é™¤å—?')) {
      setProjects(projects.map(p => 
        p.id === currentProject 
          ? { ...p, files: p.files.filter(f => f.path !== filePath && !f.path.startsWith(filePath + '/')) }
          : p
      ));

      const newFiles = { ...files };
      delete newFiles[filePath];
      setFiles(newFiles);

      if (currentFile === filePath) {
        setCurrentFile(null);
        setCode('');
      }
    }
  };

  const saveFile = () => {
    if (currentFile) {
      setFiles({ ...files, [currentFile]: code });
      alert('æ–‡ä»¶å·²ä¿å­˜!');
    }
  };

  // ä¿å­˜æ–‡ä»¶åˆ°æœ¬åœ°
  const downloadFile = () => {
    if (!currentFile || !code) {
      alert('æ²¡æœ‰å¯ä¿å­˜çš„å†…å®¹');
      return;
    }

    const blob = new Blob([code], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = currentFile.split('/').pop(); // è·å–æ–‡ä»¶å
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  // è·å–æ–‡ä»¶è¯­è¨€ç±»å‹
  const getLanguage = (filename) => {
    if (!filename) return 'javascript';
    const ext = filename.split('.').pop().toLowerCase();
    const langMap = {
      'js': 'javascript',
      'jsx': 'jsx',
      'ts': 'typescript',
      'tsx': 'tsx',
      'html': 'html',
      'css': 'css',
      'json': 'json',
      'md': 'markdown',
      'py': 'python',
      'java': 'java',
      'cpp': 'cpp',
      'c': 'c',
      'php': 'php',
      'rb': 'ruby',
      'go': 'go',
      'rs': 'rust',
      'sql': 'sql',
      'xml': 'xml',
      'yaml': 'yaml',
      'yml': 'yaml'
    };
    return langMap[ext] || 'javascript';
  };

  const openFile = (filePath) => {
    setCurrentFile(filePath);
    setCode(files[filePath] || '');
  };

  const toggleFolder = (folderPath) => {
    setExpandedFolders({
      ...expandedFolders,
      [folderPath]: !expandedFolders[folderPath]
    });
  };

  const runCode = () => {
    setOutput('æ­£åœ¨æ‰§è¡Œ...');

    setTimeout(() => {
      try {
        const consoleOutput = [];

        const originalLog = console.log;
        const originalError = console.error;
        const originalWarn = console.warn;

        console.log = (...args) => {
          const output = args.map(arg => {
            if (typeof arg === 'object' && arg !== null) {
              try {
                return JSON.stringify(arg, null, 2);
              } catch (e) {
                return String(arg);
              }
            }
            return String(arg);
          }).join(' ');
          consoleOutput.push(output);
          originalLog(...args);
        };

        console.error = (...args) => {
          const output = 'âŒ ' + args.map(arg => String(arg)).join(' ');
          consoleOutput.push(output);
          originalError(...args);
        };

        console.warn = (...args) => {
          const output = 'âš ï¸ ' + args.map(arg => String(arg)).join(' ');
          consoleOutput.push(output);
          originalWarn(...args);
        };

        if (currentFile && currentFile.endsWith('.html')) {
          setOutput(`<iframe srcdoc="${code.replace(/"/g, '&quot;')}" style="width:100%;height:100%;border:none;background:white;"></iframe>`);
        } else {
          // eslint-disable-next-line no-eval
          eval(code);

          console.log = originalLog;
          console.error = originalError;
          console.warn = originalWarn;

          if (consoleOutput.length > 0) {
            setOutput(consoleOutput.join('\n'));
          } else {
            setOutput('âœ… ä»£ç æ‰§è¡ŒæˆåŠŸï¼Œæ— è¾“å‡º');
          }
        }
      } catch (error) {
        setOutput(`âŒ è¿è¡Œé”™è¯¯:\n${error.message}\n\nå †æ ˆä¿¡æ¯:\n${error.stack || 'æ— å †æ ˆä¿¡æ¯'}`);
      }
    }, 10);
  };

  const refresh = () => {
    setOutput('');
    alert('âœ… ç¼–è¾‘å™¨å·²åˆ·æ–°');
  };

  const applyTemplate = (templateName) => {
    if (currentFile) {
      setCode(TEMPLATES[templateName]);
      setFiles({ ...files, [currentFile]: TEMPLATES[templateName] });
      setShowTemplates(false);
    } else {
      alert('è¯·å…ˆåˆ›å»ºæˆ–é€‰æ‹©ä¸€ä¸ªæ–‡ä»¶');
    }
  };

  // ä¸Šä¼ æ–‡ä»¶
  const handleFileUpload = (e) => {
    const uploadedFiles = Array.from(e.target.files);

    uploadedFiles.forEach(file => {
      const reader = new FileReader();
      reader.onload = (event) => {
        const content = event.target.result;
        const filePath = file.name;

        const newFile = {
          id: Date.now().toString() + Math.random(),
          name: file.name,
          path: filePath,
          type: 'file',
          parentPath: ''
        };

        setProjects(projects.map(p => 
          p.id === currentProject 
            ? { ...p, files: [...p.files, newFile] }
            : p
        ));
        setFiles({ ...files, [filePath]: content });
        setCurrentFile(filePath);
        setCode(content);
      };
      reader.readAsText(file);
    });

    // é‡ç½® input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // æ‹–åŠ¨åˆ†éš”æ¡
  const handleMouseDown = () => {
    setIsDragging(true);
  };

  const handleMouseMove = (e) => {
    if (isDragging && containerRef.current) {
      const container = containerRef.current;
      const containerRect = container.getBoundingClientRect();
      const newWidth = ((e.clientX - containerRect.left) / containerRect.width) * 100;

      // é™åˆ¶åœ¨ 20% åˆ° 80% ä¹‹é—´
      if (newWidth >= 20 && newWidth <= 80) {
        setEditorWidth(newWidth);
      }
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  // æ·»åŠ å…¨å±€é¼ æ ‡äº‹ä»¶ç›‘å¬
  React.useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, editorWidth]);

  const renderFileTree = (parentPath = '') => {
    const project = getCurrentProject();
    if (!project) return null;

    const items = project.files.filter(f => f.parentPath === parentPath);

    return items.map(item => (
      <div key={item.id} style={{ marginLeft: parentPath ? '20px' : '0' }}>
        {item.type === 'folder' ? (
          <div>
            <div
              onClick={() => toggleFolder(item.path)}
              className="file-tree-item"
              style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}
            >
              <div style={{ display: 'flex', alignItems: 'center', flex: 1 }}>
                {expandedFolders[item.path] ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
                <Folder size={16} style={{ margin: '0 6px', color: '#f59e0b' }} />
                <span style={{ fontSize: '14px' }}>{item.name}</span>
              </div>
              <div style={{ display: 'flex', gap: '4px' }}>
                <button
                  onClick={(e) => { e.stopPropagation(); addFile(item.path); }}
                  className="mini-btn"
                  title="åœ¨æ­¤æ–‡ä»¶å¤¹ä¸­æ–°å»ºæ–‡ä»¶"
                >
                  <FilePlus size={12} />
                </button>
                <button
                  onClick={(e) => { e.stopPropagation(); deleteFile(item.path); }}
                  className="delete-btn"
                >
                  <Trash2 size={14} color="#ef4444" />
                </button>
              </div>
            </div>
            {expandedFolders[item.path] && renderFileTree(item.path)}
          </div>
        ) : (
          <div
            onClick={() => openFile(item.path)}
            className={`file-tree-item ${currentFile === item.path ? 'active' : ''}`}
          >
            <FileCode size={16} style={{ margin: '0 6px', color: '#3b82f6' }} />
            <span style={{ fontSize: '14px', flex: 1 }}>{item.name}</span>
            <button
              onClick={(e) => { e.stopPropagation(); deleteFile(item.path); }}
              className="delete-btn"
            >
              <Trash2 size={14} color="#ef4444" />
            </button>
          </div>
        )}
      </div>
    ));
  };

  return (
    <div style={{ display: 'flex', height: '100vh', background: '#1e1e1e', color: '#fff', fontFamily: 'Arial, sans-serif' }}>
      <style>{`
        .file-tree-item {
          display: flex;
          align-items: center;
          padding: 6px 8px;
          cursor: pointer;
          border-radius: 4px;
          transition: background 0.2s;
        }
        .file-tree-item:hover {
          background: #3c3c3c;
        }
        .file-tree-item.active {
          background: #094771;
        }
        .delete-btn, .mini-btn {
          background: none;
          border: none;
          cursor: pointer;
          padding: 4px;
          display: flex;
          align-items: center;
          border-radius: 3px;
          transition: background 0.2s;
        }
        .mini-btn {
          color: #fff;
        }
        .mini-btn:hover {
          background: rgba(255,255,255,0.1);
        }
        .delete-btn:hover {
          background: rgba(239, 68, 68, 0.1);
        }
        .toolbar-btn {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 6px;
          padding: 8px;
          background: #0e639c;
          color: #fff;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          fontSize: 12px;
          transition: background 0.2s;
        }
        .toolbar-btn:hover {
          background: #0a507e;
        }
        .action-btn {
          display: flex;
          align-items: center;
          gap: 6px;
          padding: 8px 16px;
          color: #fff;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          fontSize: 13px;
          font-weight: 500;
          transition: opacity 0.2s;
        }
        .action-btn:disabled {
          opacity: 0.5;
          cursor: not-allowed;
        }
        .action-btn:not(:disabled):hover {
          opacity: 0.9;
        }
        .divider {
          width: 4px;
          background: #3e3e42;
          cursor: col-resize;
          position: relative;
          display: flex;
          align-items: center;
          justify-content: center;
          transition: background 0.2s;
        }
        .divider:hover {
          background: #0e639c;
        }
        .divider.dragging {
          background: #0e639c;
        }
      `}</style>

      {/* ä¾§è¾¹æ  */}
      <div style={{ width: '280px', background: '#252526', borderRight: '1px solid #3e3e42', display: 'flex', flexDirection: 'column' }}>
        {/* é¡¹ç›®ç®¡ç† */}
        <div style={{ padding: '12px', borderBottom: '1px solid #3e3e42' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
            <select
              value={currentProject}
              onChange={(e) => setCurrentProject(e.target.value)}
              style={{
                flex: 1,
                padding: '6px',
                background: '#3c3c3c',
                color: '#fff',
                border: '1px solid #555',
                borderRadius: '4px',
                fontSize: '13px'
              }}
            >
              {projects.map(p => (
                <option key={p.id} value={p.id}>{p.name}</option>
              ))}
            </select>
            <button
              onClick={addProject}
              style={{
                marginLeft: '8px',
                padding: '6px 10px',
                background: '#0e639c',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '12px'
              }}
            >
              æ–°å»º
            </button>
          </div>
          {projects.length > 1 && (
            <button
              onClick={() => deleteProject(currentProject)}
              style={{
                width: '100%',
                padding: '6px',
                background: '#c92a2a',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '12px'
              }}
            >
              åˆ é™¤å½“å‰é¡¹ç›®
            </button>
          )}
        </div>

        {/* å·¥å…·æ  */}
        <div style={{ padding: '12px', borderBottom: '1px solid #3e3e42', display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '8px' }}>
          <button onClick={() => addFile()} className="toolbar-btn">
            <FilePlus size={16} /> æ–°å»ºæ–‡ä»¶
          </button>
          <button onClick={() => addFolder()} className="toolbar-btn">
            <FolderPlus size={16} /> æ–°å»ºæ–‡ä»¶å¤¹
          </button>
          <button onClick={() => fileInputRef.current?.click()} className="toolbar-btn">
            <Upload size={16} /> ä¸Šä¼ æ–‡ä»¶
          </button>
          <button onClick={refresh} className="toolbar-btn">
            <RefreshCw size={16} /> åˆ·æ–°
          </button>
          <button onClick={() => setShowTemplates(!showTemplates)} className="toolbar-btn" style={{ gridColumn: 'span 2' }}>
            <FileCode size={16} /> {showTemplates ? 'éšè—æ¨¡æ¿' : 'æ˜¾ç¤ºæ¨¡æ¿'}
          </button>
        </div>

        {/* éšè—çš„æ–‡ä»¶ä¸Šä¼ input */}
        <input
          ref={fileInputRef}
          type="file"
          multiple
          onChange={handleFileUpload}
          style={{ display: 'none' }}
          accept=".html,.css,.js,.jsx,.json,.txt,.md"
        />

        {/* æ¨¡æ¿åˆ—è¡¨ */}
        {showTemplates && (
          <div style={{ padding: '12px', borderBottom: '1px solid #3e3e42', background: '#2d2d30' }}>
            <div style={{ fontSize: '13px', fontWeight: 'bold', marginBottom: '8px' }}>é€‰æ‹©æ¨¡æ¿:</div>
            {Object.keys(TEMPLATES).map(name => (
              <button
                key={name}
                onClick={() => applyTemplate(name)}
                style={{
                  width: '100%',
                  padding: '8px',
                  marginBottom: '4px',
                  background: '#3c3c3c',
                  color: '#fff',
                  border: '1px solid #555',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  textAlign: 'left',
                  fontSize: '12px'
                }}
              >
                {name}
              </button>
            ))}
          </div>
        )}

        {/* æ–‡ä»¶æ ‘ */}
        <div style={{ flex: 1, overflow: 'auto', padding: '12px' }}>
          <div style={{ fontSize: '13px', fontWeight: 'bold', marginBottom: '8px', color: '#ccc' }}>
            æ–‡ä»¶èµ„æºç®¡ç†å™¨
          </div>
          {renderFileTree()}
        </div>
      </div>

      {/* ç¼–è¾‘å™¨åŒºåŸŸ */}
      <div ref={containerRef} style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* é¡¶éƒ¨å·¥å…·æ  */}
        <div style={{ padding: '12px', background: '#2d2d30', borderBottom: '1px solid #3e3e42', display: 'flex', gap: '8px', alignItems: 'center' }}>
          <span style={{ fontSize: '14px', color: '#ccc' }}>
            {currentFile || 'æœªé€‰æ‹©æ–‡ä»¶'}
          </span>
          <div style={{ marginLeft: 'auto', display: 'flex', gap: '8px' }}>
            <button 
              onClick={saveFile} 
              disabled={!currentFile} 
              className="action-btn"
              style={{ background: currentFile ? '#0e639c' : '#555' }}
            >
              <Save size={16} /> ä¿å­˜
            </button>
            <button 
              onClick={downloadFile} 
              disabled={!currentFile} 
              className="action-btn"
              style={{ background: currentFile ? '#7c3aed' : '#555' }}
            >
              <Download size={16} /> ä¿å­˜åˆ°æœ¬åœ°
            </button>
            <button 
              onClick={runCode} 
              disabled={!currentFile} 
              className="action-btn"
              style={{ background: currentFile ? '#16a34a' : '#555' }}
            >
              <Play size={16} /> è¿è¡Œ
            </button>
          </div>
        </div>

        {/* ç¼–è¾‘å™¨å’Œè¾“å‡ºï¼ˆå¯æ‹–åŠ¨åˆ†éš”ï¼‰ */}
        <div style={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
          {/* ä»£ç ç¼–è¾‘å™¨ */}
          <div style={{ width: `${editorWidth}%`, display: 'flex', flexDirection: 'column', position: 'relative' }}>
            {/* ä»£ç ç¼–è¾‘åŒº */}
            <textarea
              value={code}
              onChange={(e) => setCode(e.target.value)}
              placeholder="åœ¨å·¦ä¾§åˆ›å»ºæˆ–é€‰æ‹©æ–‡ä»¶å¼€å§‹ç¼–ç ...&#10;&#10;ğŸ’¡ æç¤ºï¼š&#10;â€¢ ç‚¹å‡»ã€Œæ¨¡æ¿ã€æŒ‰é’®é€‰æ‹©é¢„è®¾æ¨¡æ¿&#10;â€¢ ç‚¹å‡»ã€Œä¸Šä¼ æ–‡ä»¶ã€å¯¼å…¥æœ¬åœ°æ–‡ä»¶&#10;â€¢ åœ¨æ–‡ä»¶å¤¹ä¸Šå¯ä»¥ç›´æ¥æ–°å»ºæ–‡ä»¶&#10;â€¢ æ‹–åŠ¨ä¸­é—´åˆ†éš”æ¡è°ƒæ•´å¤§å°"
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                padding: '16px',
                background: 'transparent',
                color: 'transparent',
                caretColor: '#fff',
                border: 'none',
                fontFamily: 'Consolas, Monaco, "Courier New", monospace',
                fontSize: '14px',
                lineHeight: '1.6',
                resize: 'none',
                outline: 'none',
                zIndex: 2,
                whiteSpace: 'pre',
                overflowWrap: 'normal',
                overflow: 'auto'
              }}
              spellCheck="false"
            />
            {/* ä»£ç é«˜äº®æ˜¾ç¤ºå±‚ */}
            <div style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              overflow: 'auto',
              pointerEvents: 'none',
              zIndex: 1
            }}>
              <SyntaxHighlighter
                language={getLanguage(currentFile)}
                style={vscDarkPlus}
                customStyle={{
                  margin: 0,
                  padding: '16px',
                  background: '#1e1e1e',
                  fontSize: '14px',
                  lineHeight: '1.6',
                  fontFamily: 'Consolas, Monaco, "Courier New", monospace'
                }}
                showLineNumbers={true}
                wrapLines={true}
              >
                {code || ''}
              </SyntaxHighlighter>
            </div>
          </div>

          {/* å¯æ‹–åŠ¨çš„åˆ†éš”æ¡ */}
          <div 
            className={`divider ${isDragging ? 'dragging' : ''}`}
            onMouseDown={handleMouseDown}
          >
            <GripVertical size={16} color="#888" />
          </div>

          {/* è¾“å‡ºé¢æ¿ */}
          <div style={{ width: `${100 - editorWidth}%`, background: '#1e1e1e', display: 'flex', flexDirection: 'column' }}>
            <div style={{ padding: '12px', background: '#2d2d30', borderBottom: '1px solid #3e3e42', fontSize: '13px', fontWeight: 'bold' }}>
              è¾“å‡º
            </div>
            <div style={{ flex: 1, padding: '16px', overflow: 'auto', fontFamily: 'Consolas, Monaco, monospace', fontSize: '13px', whiteSpace: 'pre-wrap', wordBreak: 'break-word', lineHeight: '1.5' }}>
              {currentFile && currentFile.endsWith('.html') ? (
                <div dangerouslySetInnerHTML={{ __html: output }} style={{ width: '100%', height: '100%' }} />
              ) : (
                <div style={{ color: '#d4d4d4' }}>
