// --- 3.1 学生名单管理 ---
const StudentManager = ({ students, setStudents }) => {
  const [importText, setImportText] = useState('');
  const [selectedIds, setSelectedIds] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});

  // Excel/CSV 解析模拟
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if(!file) return;

    /* 真实环境使用 xlsx 库代码示例:
    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, {type:'binary'});
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const data = XLSX.utils.sheet_to_json(ws, {header:1});
      // data 此时为二维数组，第一行通常是表头 [学号, 姓名, 班级]
      // ... 解析逻辑 ...
    };
    reader.readAsBinaryString(file);
    */

    // 此处使用 CSV/文本简单解析兜底
    const reader = new FileReader();
    reader.onload = (evt) => {
      const text = evt.target.result;
      const lines = text.split('\n').filter(l => l.trim());
      const newStudents = lines.map(line => {
        // 假设格式: 学号,姓名,班级
        const parts = line.split(/,|，|\t/);
        if(parts.length >= 2) {
          return { id: parts[0].trim(), name: parts[1].trim(), class: parts[2]?.trim() || '未分配' };
        }
        return null;
      }).filter(Boolean);

      // 去重合并
      const currentIds = new Set(students.map(s => s.id));
      const validNew = newStudents.filter(s => !currentIds.has(s.id));
      setStudents([...students, ...validNew]);
      alert(`解析完成，新增 ${validNew.length} 名学生`);
    };
    reader.readAsText(file);
    e.target.value = '';
  };

  const handleDelete = (id) => {
    if(window.confirm('确定删除该学生吗？')) {
      setStudents(students.filter(s => s.id !== id));
    }
  };

  const handleBatchDelete = () => {
    if(selectedIds.length === 0) return;
    if(window.confirm(`确定删除选中的 ${selectedIds.length} 名学生吗？`)) {
      setStudents(students.filter(s => !selectedIds.includes(s.id)));
      setSelectedIds([]);
    }
  };

  const startEdit = (student) => {
    setEditingId(student.id);
    setEditForm({...student});
  };

  const saveEdit = () => {
    setStudents(students.map(s => s.id === editingId ? editForm : s));
    setEditingId(null);
  };

  return (
    <div style={{ background: 'white', padding: '20px', borderRadius: '8px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <div style={{ display: 'flex', gap: '10px' }}>
          <label style={{ padding: '8px 12px', background: '#16a34a', color: 'white', borderRadius: '4px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px' }}>
            <Upload size={16}/> 导入名单 (Excel/CSV)
            <input type="file" accept=".csv,.xlsx,.xls,.txt" style={{display:'none'}} onChange={handleFileUpload}/>
          </label>
          <button onClick={handleBatchDelete} disabled={selectedIds.length===0} style={{ padding: '8px 12px', background: selectedIds.length?'#ef4444':'#ccc', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
            批量删除
          </button>
        </div>
        <div style={{color:'#666', fontSize:'13px'}}>总人数: {students.length}</div>
      </div>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ background: '#f3f4f6', textAlign: 'left' }}>
            <th style={{padding:'10px'}}><input type="checkbox" onChange={e => setSelectedIds(e.target.checked ? students.map(s=>s.id) : [])} checked={selectedIds.length === students.length && students.length > 0}/></th>
            <th style={{padding:'10px'}}>学号</th>
            <th style={{padding:'10px'}}>姓名</th>
            <th style={{padding:'10px'}}>班级</th>
            <th style={{padding:'10px'}}>操作</th>
          </tr>
        </thead>
        <tbody>
          {students.map(s => (
            <tr key={s.id} style={{ borderBottom: '1px solid #eee' }}>
              <td style={{padding:'10px'}}><input type="checkbox" checked={selectedIds.includes(s.id)} onChange={e => {
                if(e.target.checked) setSelectedIds([...selectedIds, s.id]);
                else setSelectedIds(selectedIds.filter(id => id !== s.id));
              }}/></td>
              <td style={{padding:'10px'}}>{s.id}</td>
              <td style={{padding:'10px'}}>
                {editingId === s.id ? <input value={editForm.name} onChange={e=>setEditForm({...editForm, name:e.target.value})} style={{width:'80px'}}/> : s.name}
              </td>
              <td style={{padding:'10px'}}>
                {editingId === s.id ? <input value={editForm.class} onChange={e=>setEditForm({...editForm, class:e.target.value})} style={{width:'100px'}}/> : s.class}
              </td>
              <td style={{padding:'10px'}}>
                {editingId === s.id ? (
                  <button onClick={saveEdit} style={{color:'#16a34a', marginRight:'10px', background:'none', border:'none', cursor:'pointer'}}>保存</button>
                ) : (
                  <button onClick={() => startEdit(s)} style={{color:'#0e639c', marginRight:'10px', background:'none', border:'none', cursor:'pointer'}}>编辑</button>
                )}
                <button onClick={() => handleDelete(s.id)} style={{color:'#ef4444', background:'none', border:'none', cursor:'pointer'}}>删除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// --- 3.2 代码库管理 ---
const CodeRepository = ({ repo, setRepo, onOpenEditor }) => {
  const [showModal, setShowModal] = useState(false);
  const [editingSnippet, setEditingSnippet] = useState(null);
  const [newSnippet, setNewSnippet] = useState({ title: '', category: 'HTML/CSS', content: '' });
  const [filter, setFilter] = useState('');

  const addSnippet = () => {
    if(!newSnippet.title) return alert('请输入标题');
    if(!newSnippet.content) return alert('请输入代码内容');

    if(editingSnippet) {
      // 编辑模式
      setRepo(repo.map(r => r.id === editingSnippet.id ? { ...newSnippet, id: editingSnippet.id } : r));
    } else {
      // 新增模式
      setRepo([...repo, { ...newSnippet, id: `c${Date.now()}` }]);
    }

    setShowModal(false);
    setEditingSnippet(null);
    setNewSnippet({ title: '', category: 'HTML/CSS', content: '' });
  };

  const deleteSnippet = (id) => {
    if(window.confirm('删除此代码片段？')) setRepo(repo.filter(r => r.id !== id));
  };

  const startEdit = (item) => {
    setEditingSnippet(item);
    setNewSnippet({ title: item.title, category: item.category, content: item.content });
    setShowModal(true);
  };

  const openInEditor = (item) => {
    // 根据分类确定文件名
    let fileName = 'index.html';
    if(item.category.includes('JavaScript') || item.category.includes('算法')) {
      fileName = 'script.js';
    } else if(item.category.includes('CSS')) {
      fileName = 'style.css';
    } else if(item.category.includes('React')) {
      fileName = 'App.jsx';
    }

    // 打开编辑器
    onOpenEditor({
      mode: 'repo_view',
      initialFiles: { [fileName]: item.content },
      projectName: `代码库 - ${item.title}`,
      onSave: (updatedFiles) => {
        // 保存回代码库
        const updatedContent = updatedFiles[fileName] || item.content;
        setRepo(repo.map(r => r.id === item.id ? { ...r, content: updatedContent } : r));
        alert('✅ 代码已保存到代码库');
      }
    });
  };

  return (
    <div style={{ background: 'white', padding: '20px', borderRadius: '8px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <div style={{display:'flex', gap:'10px', alignItems:'center'}}>
           <input placeholder="搜索代码..." value={filter} onChange={e=>setFilter(e.target.value)} style={{padding:'8px', borderRadius:'4px', border:'1px solid #ddd', width:'250px'}}/>
        </div>
        <button onClick={() => { setEditingSnippet(null); setNewSnippet({ title: '', category: 'HTML/CSS', content: '' }); setShowModal(true); }} style={{ padding: '8px 15px', background: '#0e639c', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', display:'flex', alignItems:'center', gap:'5px' }}>
          <Plus size={16}/> 添加代码片段
        </button>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(320px, 1fr))', gap: '20px' }}>
        {repo.filter(r => r.title.includes(filter) || r.category.includes(filter)).map(item => (
          <div key={item.id} style={{ border: '1px solid #eee', borderRadius: '8px', padding: '15px', background: '#f9fafb', display: 'flex', flexDirection: 'column' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
              <strong style={{fontSize:'16px', flex: 1}}>{item.title}</strong>
              <span style={{ fontSize: '12px', padding: '2px 8px', background: '#e5e7eb', borderRadius: '10px', whiteSpace: 'nowrap' }}>{item.category}</span>
            </div>
            <div style={{ background: '#1f2937', color: '#d1d5db', padding: '10px', borderRadius: '4px', height: '100px', overflow: 'hidden', fontSize: '12px', fontFamily: 'monospace', marginBottom:'10px', lineHeight: '1.5' }}>
              {item.content.slice(0, 200)}...
            </div>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px', marginTop: 'auto' }}>
               <button onClick={() => openInEditor(item)} style={{color:'#16a34a', background:'none', border:'none', cursor:'pointer', fontSize:'13px', display:'flex', alignItems:'center', gap:'4px', fontWeight:'500'}}>
                 <Edit2 size={14}/> 编辑代码
               </button>
               <button onClick={() => startEdit(item)} style={{color:'#0e639c', background:'none', border:'none', cursor:'pointer', fontSize:'13px', display:'flex', alignItems:'center', gap:'4px'}}>
                 <FileCode size={14}/> 修改
               </button>
               <button onClick={() => deleteSnippet(item.id)} style={{color:'#ef4444', background:'none', border:'none', cursor:'pointer', fontSize:'13px'}}>删除</button>
            </div>
          </div>
        ))}
      </div>

      {repo.filter(r => r.title.includes(filter) || r.category.includes(filter)).length === 0 && (
        <div style={{ textAlign: 'center', padding: '40px', color: '#999' }}>
          {filter ? '没有找到匹配的代码片段' : '暂无代码片段，点击右上角添加'}
        </div>
      )}

      {/* 添加/编辑模态框 */}
      {showModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000 }}>
          <div style={{ background: 'white', padding: '20px', borderRadius: '8px', width: '600px', maxHeight: '80vh', overflow: 'auto' }}>
            <h3>{editingSnippet ? '编辑代码片段' : '添加代码片段'}</h3>
            <div style={{ marginBottom: '10px' }}>
              <label style={{display:'block', marginBottom:'5px', fontWeight:'500'}}>标题</label>
              <input style={{width:'100%', padding:'8px', border:'1px solid #ddd', borderRadius:'4px'}} value={newSnippet.title} onChange={e=>setNewSnippet({...newSnippet, title:e.target.value})} placeholder="例如：响应式导航栏"/>
            </div>
            <div style={{ marginBottom: '10px' }}>
              <label style={{display:'block', marginBottom:'5px', fontWeight:'500'}}>分类</label>
              <select style={{width:'100%', padding:'8px', border:'1px solid #ddd', borderRadius:'4px'}} value={newSnippet.category} onChange={e=>setNewSnippet({...newSnippet, category:e.target.value})}>
                <option>HTML/CSS</option>
                <option>JavaScript</option>
                <option>React</option>
                <option>算法</option>
                <option>其他</option>
              </select>
            </div>
            <div style={{ marginBottom: '10px' }}>
              <label style={{display:'block', marginBottom:'5px', fontWeight:'500'}}>代码内容</label>
              <textarea 
                style={{width:'100%', height:'250px', padding:'8px', fontFamily:'monospace', fontSize:'13px', border:'1px solid #ddd', borderRadius:'4px', lineHeight:'1.5'}} 
                value={newSnippet.content} 
                onChange={e=>setNewSnippet({...newSnippet, content:e.target.value})}
                placeholder="在此输入代码..."
              />
            </div>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
              <button onClick={() => { setShowModal(false); setEditingSnippet(null); }} style={{padding:'8px 15px', border:'1px solid #ddd', background:'white', borderRadius:'4px', cursor:'pointer'}}>取消</button>
              <button onClick={addSnippet} style={{padding:'8px 15px', background:'#0e639c', color:'white', border:'none', borderRadius:'4px', cursor:'pointer'}}>{editingSnippet ? '保存修改' : '添加'}</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// --- 3.3 作业布置 ---
const AssignmentManager = ({ assignments, setAssignments, repo, students }) => {
  const [showModal, setShowModal] = useState(false);
  const [newAssign, setNewAssign] = useState({ title: '', requirements: '', deadline: '', linkedCodeId: '', targetClass: '所有班级' });

  const handleCreate = () => {
    if(!newAssign.title) return alert('请输入标题');

    let template = { 'index.html': '<!-- 默认模板 -->' };
    // 如果关联了代码库，使用代码库内容作为模板
    if (newAssign.linkedCodeId) {
      const code = repo.find(c => c.id === newAssign.linkedCodeId);
      if (code) {
         const fileName = code.category.includes('Java') ? 'script.js' : 'index.html';
         template = { [fileName]: code.content };
      }
    }

    setAssignments([...assignments, { ...newAssign, id: `a${Date.now()}`, template }]);
    setShowModal(false);
    setNewAssign({ title: '', requirements: '', deadline: '', linkedCodeId: '', targetClass: '所有班级' });
  };

  // 提取所有班级
  const classes = [...new Set(students.map(s => s.class))];

  return (
    <div style={{ background: 'white', padding: '20px', borderRadius: '8px' }}>
      <button onClick={() => setShowModal(true)} style={{ marginBottom: '20px', padding: '10px 15px', background: '#16a34a', color: 'white', border: 'none', borderRadius: '4px', display:'flex', alignItems:'center', gap:'5px' }}>
        <Plus size={16}/> 发布新作业
      </button>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
           <tr style={{ background: '#f3f4f6', textAlign: 'left' }}><th style={{padding:'10px'}}>作业标题</th><th style={{padding:'10px'}}>截止时间</th><th style={{padding:'10px'}}>面向对象</th><th style={{padding:'10px'}}>关联代码</th><th style={{padding:'10px'}}>操作</th></tr>
        </thead>
        <tbody>
          {assignments.map(a => {
            const linkedCode = repo.find(r => r.id === a.linkedCodeId);
            return (
              <tr key={a.id} style={{ borderBottom: '1px solid #eee' }}>
                <td style={{padding:'10px'}}>
                  <div>{a.title}</div>
                  <div style={{fontSize:'12px', color:'#666'}}>{a.requirements}</div>
                </td>
                <td style={{padding:'10px'}}>{a.deadline}</td>
                <td style={{padding:'10px'}}>{a.targetClass}</td>
                <td style={{padding:'10px'}}>{linkedCode ? <span style={{color:'#0e639c', fontSize:'12px'}}>{linkedCode.title}</span> : '-'}</td>
                <td style={{padding:'10px'}}>
                  <button onClick={() => {if(window.confirm('删除?')) setAssignments(assignments.filter(x=>x.id!==a.id))}} style={{color:'#ef4444', background:'none', border:'none', cursor:'pointer'}}>删除</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      {/* 发布作业弹窗 */}
      {showModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <div style={{ background: 'white', padding: '20px', borderRadius: '8px', width: '500px' }}>
            <h3>发布新作业</h3>
            <div style={{ display: 'grid', gap: '15px' }}>
              <div>
                <label style={{display:'block', fontSize:'13px', marginBottom:'5px'}}>作业标题</label>
                <input style={{width:'100%', padding:'8px', border:'1px solid #ddd'}} value={newAssign.title} onChange={e=>setNewAssign({...newAssign, title:e.target.value})}/>
              </div>
              <div>
                <label style={{display:'block', fontSize:'13px', marginBottom:'5px'}}>作业要求</label>
                <textarea style={{width:'100%', height:'60px', padding:'8px', border:'1px solid #ddd'}} value={newAssign.requirements} onChange={e=>setNewAssign({...newAssign, requirements:e.target.value})}/>
              </div>
              <div style={{display:'flex', gap:'15px'}}>
                <div style={{flex:1}}>
                  <label style={{display:'block', fontSize:'13px', marginBottom:'5px'}}>截止时间</label>
                  <input type="date" style={{width:'100%', padding:'8px', border:'1px solid #ddd'}} value={newAssign.deadline} onChange={e=>setNewAssign({...newAssign, deadline:e.target.value})}/>
                </div>
                <div style={{flex:1}}>
                  <label style={{display:'block', fontSize:'13px', marginBottom:'5px'}}>发布给(班级)</label>
                  <select style={{width:'100%', padding:'8px', border:'1px solid #ddd'}} value={newAssign.targetClass} onChange={e=>setNewAssign({...newAssign, targetClass:e.target.value})}>
                    <option>所有班级</option>
                    {classes.map(c => <option key={c}>{c}</option>)}
                  </select>
                </div>
              </div>
              <div>
                <label style={{display:'block', fontSize:'13px', marginBottom:'5px'}}>关联代码案例 (学生打开作业时默认加载)</label>
                <select style={{width:'100%', padding:'8px', border:'1px solid #ddd'}} value={newAssign.linkedCodeId} onChange={e=>setNewAssign({...newAssign, linkedCodeId:e.target.value})}>
