              style={{ 
                padding: '12px', borderRadius: '6px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '10px',
                background: activeTab === item.id ? '#374151' : 'transparent',
                color: activeTab === item.id ? '#60a5fa' : '#9ca3af'
              }}
            >
              {item.icon} {item.label}
            </div>
          ))}
        </div>
        <button onClick={onLogout} style={{ marginTop: 'auto', display: 'flex', alignItems: 'center', gap: '10px', background: 'none', border: 'none', color: '#9ca3af', cursor: 'pointer', padding:'10px' }}>
          退出登录
        </button>
      </div>

      {/* 主内容区 */}
      <div style={{ flex: 1, padding: '30px', overflow: 'auto' }}>
        <h2 style={{ marginBottom: '20px', color: '#111827' }}>
          {activeTab === 'students' && '学生名单管理'}
          {activeTab === 'repository' && '代码库管理'}
          {activeTab === 'assignments' && '作业布置'}
          {activeTab === 'submissions' && '学生作业批阅'}
        </h2>

        {activeTab === 'students' && (
          <StudentManager 
            students={data.students} 
            setStudents={newS => setData({...data, students: newS})} 
          />
        )}

        {activeTab === 'repository' && (
          <CodeRepository 
            repo={data.codeRepository} 
            setRepo={newR => setData({...data, codeRepository: newR})}
            onOpenEditor={onOpenEditor}
          />
        )}

        {activeTab === 'assignments' && (
          <AssignmentManager 
            assignments={data.assignments} 
            setAssignments={newA => setData({...data, assignments: newA})}
            repo={data.codeRepository}
            students={data.students}
          />
        )}

        {activeTab === 'submissions' && (
          <div style={{ background: 'white', padding: '20px', borderRadius: '8px' }}>
             {data.submissions.length === 0 ? <div style={{color:'#666', textAlign:'center', padding:'20px'}}>暂无学生提交</div> : (
               <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                 <thead><tr style={{background:'#f3f4f6', textAlign:'left'}}><th style={{padding:'10px'}}>学生</th><th style={{padding:'10px'}}>班级</th><th style={{padding:'10px'}}>作业</th><th style={{padding:'10px'}}>提交时间</th><th style={{padding:'10px'}}>操作</th></tr></thead>
                 <tbody>
                   {data.submissions.map(sub => {
                     const student = data.students.find(s => s.id === sub.studentId);
                     const assignment = data.assignments.find(a => a.id === sub.assignmentId);
                     return (
                       <tr key={sub.id} style={{borderBottom:'1px solid #eee'}}>
                         <td style={{padding:'10px'}}>{student?.name} <span style={{color:'#999', fontSize:'12px'}}>({student?.id})</span></td>
                         <td style={{padding:'10px'}}>{student?.class}</td>
                         <td style={{padding:'10px'}}>{assignment?.title}</td>
                         <td style={{padding:'10px'}}>{new Date(sub.timestamp).toLocaleString()}</td>
                         <td style={{padding:'10px'}}>
                           <button 
                             onClick={() => onOpenEditor({
                               mode: 'teacher_review',
                               initialFiles: sub.files,
                               projectName: `${student?.name} - ${assignment?.title}`,
                             })}
                             style={{padding:'5px 10px', background:'#0e639c', color:'white', border:'none', borderRadius:'4px', cursor:'pointer', display:'flex', alignItems:'center', gap:'5px'}}
                           >
                             <Play size={14}/> 批阅
                           </button>
                         </td>
                       </tr>
                     )
                   })}
                 </tbody>
               </table>
             )}
          </div>
        )}
      </div>
    </div>
  );
}

// ==========================================
// 5. 学生端仪表盘 (StudentDashboard - 简化版)
// ==========================================
function StudentDashboard({ user, data, onOpenEditor, onLogout }) {
  return (
    <div style={{ padding: '30px', maxWidth: '1000px', margin: '0 auto', fontFamily: 'Arial' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>🎓 同学你好，{user.name}</h1>
        <button onClick={onLogout} style={{padding:'8px 16px'}}>退出</button>
      </div>
      <div style={{ display: 'grid', gap: '20px' }}>
        {data.assignments.map(assign => {
          if(assign.targetClass !== '所有班级' && assign.targetClass !== user.class) return null;
          const sub = data.submissions.find(s => s.studentId === user.id && s.assignmentId === assign.id);
          return (
            <div key={assign.id} style={{ background: 'white', padding: '25px', borderRadius: '8px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
               <h2>{assign.title}</h2>
               <div style={{margin:'10px 0', color:'#4b5563'}}><strong>要求：</strong>{assign.requirements}</div>
               <div style={{fontSize:'14px', color:'#6b7280', marginBottom:'15px'}}>截止日期：{assign.deadline}</div>
               <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                 <span style={{ color: sub ? '#16a34a' : '#ea580c', fontWeight: 'bold' }}>{sub ? '✅ 已提交' : '⏳ 待完成'}</span>
                 <button 
                   onClick={() => onOpenEditor({
                     mode: 'student_work',
                     initialFiles: sub ? sub.files : assign.template, // 加载提交过的 或 默认模板
                     projectName: assign.title,
                     onSubmit: (files) => {
                       // 提交回调
                       // 这里简单模拟，实际应调用 App 的 updateData
                       alert('提交成功！(演示)');
                     }
                   })}
                   style={{ padding: '10px 20px', background: sub ? '#0e639c' : '#ea580c', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                 >
                   {sub ? '修改作业' : '开始作业'}
                 </button>
               </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

// ==========================================
// 6. 主入口
// ==========================================
function App() {
  const [data, setData] = useState(INITIAL_DATA);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('login');
  const [editorConfig, setEditorConfig] = useState(null);

  const handleLogin = (role, id) => {
    if (role === 'teacher') {
      setUser({ role: 'teacher', name: '教师' });
      setView('dashboard');
    } else {
      const s = data.students.find(stu => stu.id === id);
      if (s) { setUser({ ...s, role: 'student' }); setView('dashboard'); }
      else alert('学号不存在 (试用 s1, s2)');
    }
  };

  // 处理提交 (从 Editor 回调)
  const handleSubmission = (files) => {
    // 简单的模拟：找到当前用户在当前项目的提交并更新，或新增
    // 在真实 App 中，editorConfig 应该包含 assignmentId
    // 这里为了简化 Demo，我们假设 editorConfig.projectName 即 assignment.title
    // 实际应用请传递精确 ID
    const assign = data.assignments.find(a => a.title === editorConfig.projectName);
    if(assign && user.role === 'student') {
       const newSub = {
         id: `sub${Date.now()}`,
         studentId: user.id,
         assignmentId: assign.id,
         files: files,
         timestamp: Date.now()
       };
       const others = data.submissions.filter(s => !(s.studentId === user.id && s.assignmentId === assign.id));
       setData(prev => ({ ...prev, submissions: [...others, newSub] }));
       alert('作业已提交！');
       setView('dashboard');
    }
  };

  if (view === 'editor') {
    return (
      <FullEditor 
        {...editorConfig}
        onBack={() => setView('dashboard')}
        onSubmit={handleSubmission}
      />
    );
  }

  if (view === 'dashboard') {
    return user.role === 'teacher' ? (
      <TeacherDashboard 
        data={data} 
        setData={setData} 
        onOpenEditor={(config) => { setEditorConfig(config); setView('editor'); }}
        onLogout={() => setView('login')} 
      />
    ) : (
      <StudentDashboard 
        user={user} 
        data={data} 
        onOpenEditor={(config) => { setEditorConfig(config); setView('editor'); }}
        onLogout={() => setView('login')} 
      />
    );
  }

  return (
    <div style={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
       <div style={{ background: 'white', padding: '40px', borderRadius: '10px', width: '400px', textAlign: 'center' }}>
         <h1 style={{marginBottom:'30px'}}>JS 教学平台</h1>
         <div style={{display:'flex', gap:'20px'}}>
            <button onClick={() => handleLogin('teacher')} style={{flex:1, padding:'15px', background:'#0e639c', color:'white', border:'none', borderRadius:'5px', cursor:'pointer'}}>我是老师</button>
            <button onClick={() => {const id=prompt('输入学号(s1/s2):','s1'); if(id) handleLogin('student', id)}} style={{flex:1, padding:'15px', background:'#ea580c', color:'white', border:'none', borderRadius:'5px', cursor:'pointer'}}>我是学生</button>
         </div>
       </div>
    </div>
  );
}

export default App;
