// 修改后的 App.js 文件内容
import { login } from './api';
import React, { useState } from 'react';
import { 
  Play, FolderPlus, FilePlus, RefreshCw, Save, Trash2, FileCode, Folder, 
  ChevronRight, ChevronDown, Upload, GripVertical, Download, Edit2, 
  PackageOpen, PackagePlus, ArrowLeft, Send, Users, BookOpen, CheckCircle, 
  GraduationCap, Database, Plus, Calendar, Search, X, User, Lock, LogIn,
  Code, Sparkles, Eye, EyeOff
} from 'lucide-react';

import FullEditor from './FullEditor';

// ==========================================
// 1. 扩展的模拟数据库
// ==========================================
const INITIAL_DATA = {
  students: [
    { id: 's1', name: '张三', class: '三年二班' },
    { id: 's2', name: '李四', class: '三年二班' },
    { id: 's3', name: '王五', class: '三年三班' }
  ],
  codeRepository: [
    { 
      id: 'c1', 
      title: '经典 HTML 布局', 
      category: 'HTML/CSS', 
      content: '<!DOCTYPE html>\n<html>\n<body>\n  <div style="display:flex">Flex 布局示例</div>\n</body>\n</html>' 
    },
    { 
      id: 'c2', 
      title: '数组去重算法', 
      category: 'JavaScript', 
      content: '// 使用 Set 去重\nconst arr = [1, 2, 2, 3];\nconst unique = [...new Set(arr)];\nconsole.log(unique);' 
    }
  ],
  assignments: [
    { 
      id: 'a1', 
      title: '个人主页制作', 
      requirements: '包含头像、姓名和一段自我介绍，背景色需为浅灰色。',
      deadline: '2025-12-31',
      targetClass: '所有班级', 
      template: { 'index.html': '<!-- 请在此处编写代码 -->' }
    }
  ],
  submissions: [] 
};

// ==========================================
// 2. 美化的登录界面组件
// ==========================================
const LoginPage = ({ onLogin, students }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('请输入用户名和密码');
      return;
    }

    setIsLoading(true);
    setError('');

    // 模拟登录延迟
    await new Promise(resolve => setTimeout(resolve, 800));

    // 验证逻辑
    if (username === 'teacher' && password === 'teacher') {
      onLogin({ role: 'teacher', name: '教师' });
    } else {
      const s = students.find(stu => stu.id === username);
      if (s) {
        onLogin({ ...s, role: 'student' });
      } else {
        setError('用户名或密码错误');
      }
    }
    setIsLoading(false);
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)',
      padding: '20px'
    }}>
      {/* 背景装饰 */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        overflow: 'hidden',
        pointerEvents: 'none'
      }}>
        <div style={{
          position: 'absolute',
          top: '10%',
          left: '10%',
          width: '300px',
          height: '300px',
          background: 'radial-gradient(circle, rgba(99,102,241,0.15) 0%, transparent 70%)',
          borderRadius: '50%'
        }} />
        <div style={{
          position: 'absolute',
          bottom: '10%',
          right: '10%',
          width: '400px',
          height: '400px',
          background: 'radial-gradient(circle, rgba(16,185,129,0.1) 0%, transparent 70%)',
          borderRadius: '50%'
        }} />
      </div>

      <div style={{
        width: '100%',
        maxWidth: '420px',
        position: 'relative',
        zIndex: 1
      }}>
        {/* Logo 和标题 */}
        <div style={{ textAlign: 'center', marginBottom: '40px' }}>
          <div style={{
            display: 'inline-flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '80px',
            height: '80px',
            background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)',
            borderRadius: '20px',
            marginBottom: '24px',
            boxShadow: '0 20px 40px rgba(99,102,241,0.3)'
          }}>
            <Code size={40} color="#fff" />
          </div>
          <h1 style={{
            fontSize: '28px',
            fontWeight: '700',
            color: '#fff',
            margin: '0 0 8px 0',
            letterSpacing: '1px'
          }}>
            在线编程平台
          </h1>
          <p style={{
            fontSize: '14px',
            color: 'rgba(255,255,255,0.6)',
            margin: 0
          }}>
            Code • Learn • Create
          </p>
        </div>

        {/* 登录卡片 */}
        <div style={{
          background: 'rgba(255,255,255,0.05)',
          backdropFilter: 'blur(20px)',
          borderRadius: '24px',
          padding: '40px',
          border: '1px solid rgba(255,255,255,0.1)',
          boxShadow: '0 25px 50px rgba(0,0,0,0.25)'
        }}>
          <form onSubmit={handleSubmit}>
            {/* 用户名输入 */}
            <div style={{ marginBottom: '24px' }}>
              <label style={{
                display: 'block',
                fontSize: '13px',
                fontWeight: '500',
                color: 'rgba(255,255,255,0.7)',
                marginBottom: '8px'
              }}>
                用户名 / 学号
              </label>
              <div style={{
                position: 'relative',
                display: 'flex',
                alignItems: 'center'
              }}>
                <User size={18} color="rgba(255,255,255,0.4)" style={{
                  position: 'absolute',
                  left: '16px'
                }} />
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="请输入用户名"
                  style={{
                    width: '100%',
                    padding: '14px 16px 14px 48px',
                    background: 'rgba(255,255,255,0.05)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                    fontSize: '15px',
                    outline: 'none',
                    transition: 'all 0.3s ease',
                    boxSizing: 'border-box'
                  }}
                  onFocus={(e) => {
                    e.target.style.borderColor = '#6366f1';
                    e.target.style.boxShadow = '0 0 0 3px rgba(99,102,241,0.2)';
                  }}
                  onBlur={(e) => {
                    e.target.style.borderColor = 'rgba(255,255,255,0.1)';
                    e.target.style.boxShadow = 'none';
                  }}
                />
              </div>
            </div>

            {/* 密码输入 */}
            <div style={{ marginBottom: '24px' }}>
              <label style={{
                display: 'block',
                fontSize: '13px',
                fontWeight: '500',
                color: 'rgba(255,255,255,0.7)',
                marginBottom: '8px'
              }}>
                密码
              </label>
              <div style={{
                position: 'relative',
                display: 'flex',
                alignItems: 'center'
              }}>
                <Lock size={18} color="rgba(255,255,255,0.4)" style={{
                  position: 'absolute',
                  left: '16px'
                }} />
                <input
                  type={showPassword ? 'text' : 'password'}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="请输入密码"
                  style={{
                    width: '100%',
                    padding: '14px 48px 14px 48px',
                    background: 'rgba(255,255,255,0.05)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                    fontSize: '15px',
                    outline: 'none',
                    transition: 'all 0.3s ease',
                    boxSizing: 'border-box'
                  }}
                  onFocus={(e) => {
                    e.target.style.borderColor = '#6366f1';
                    e.target.style.boxShadow = '0 0 0 3px rgba(99,102,241,0.2)';
                  }}
                  onBlur={(e) => {
                    e.target.style.borderColor = 'rgba(255,255,255,0.1)';
                    e.target.style.boxShadow = 'none';
                  }}
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  style={{
                    position: 'absolute',
                    right: '16px',
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer',
                    padding: 0
                  }}
                >
                  {showPassword ? 
                    <EyeOff size={18} color="rgba(255,255,255,0.4)" /> : 
                    <Eye size={18} color="rgba(255,255,255,0.4)" />
                  }
                </button>
              </div>
            </div>

            {/* 错误提示 */}
            {error && (
              <div style={{
                padding: '12px 16px',
                background: 'rgba(239,68,68,0.1)',
                border: '1px solid rgba(239,68,68,0.3)',
                borderRadius: '10px',
                marginBottom: '24px',
                color: '#f87171',
                fontSize: '14px',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}>
                <X size={16} />
                {error}
              </div>
            )}

            {/* 登录按钮 */}
            <button
              type="submit"
              disabled={isLoading}
              style={{
                width: '100%',
                padding: '14px',
                background: isLoading 
                  ? 'rgba(99,102,241,0.5)' 
                  : 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)',
                border: 'none',
                borderRadius: '12px',
                color: '#fff',
                fontSize: '16px',
                fontWeight: '600',
                cursor: isLoading ? 'not-allowed' : 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
                transition: 'all 0.3s ease',
                boxShadow: '0 10px 30px rgba(99,102,241,0.3)'
              }}
            >
              {isLoading ? (
                <>
                  <div style={{
                    width: '20px',
                    height: '20px',
                    border: '2px solid rgba(255,255,255,0.3)',
                    borderTopColor: '#fff',
                    borderRadius: '50%',
                    animation: 'spin 0.8s linear infinite'
                  }} />
                  登录中...
                </>
              ) : (
                <>
                  <LogIn size={18} />
                  登 录
                </>
              )}
            </button>
          </form>

          {/* 提示信息 */}
          <div style={{
            marginTop: '32px',
            padding: '16px',
            background: 'rgba(16,185,129,0.1)',
            borderRadius: '12px',
            border: '1px solid rgba(16,185,129,0.2)'
          }}>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
              marginBottom: '8px'
            }}>
              <Sparkles size={16} color="#10b981" />
              <span style={{ color: '#10b981', fontSize: '13px', fontWeight: '600' }}>
                快速体验
              </span>
            </div>
            <p style={{
              margin: 0,
              fontSize: '12px',
              color: 'rgba(255,255,255,0.5)',
              lineHeight: 1.6
            }}>
              教师账号: teacher / teacher<br />
              学生账号: s1, s2, s3 / 任意密码
            </p>
          </div>
        </div>

        {/* 底部版权 */}
        <p style={{
          textAlign: 'center',
          marginTop: '32px',
          fontSize: '12px',
          color: 'rgba(255,255,255,0.3)'
        }}>
          © 2025 在线编程平台 · 助力编程教育
        </p>
      </div>

      {/* 加载动画样式 */}
      <style>{`
        @keyframes spin {
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

// ==========================================
// 3. 教师端核心模块
// ==========================================

// --- 3.1 学生名单管理 ---
const StudentManager = ({ students, setStudents }) => {
  const [selectedIds, setSelectedIds] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if(!file) return;

    const reader = new FileReader();
    reader.onload = (evt) => {
      const text = evt.target.result;
      const lines = text.split('\n').filter(l => l.trim());
      const newStudents = lines.map(line => {
        const parts = line.split(/,|，|\t/);
        if(parts.length >= 2) {
          return { id: parts[0].trim(), name: parts[1].trim(), class: parts[2]?.trim() || '未分配' };
        }
        return null;
      }).filter(Boolean);

      const currentIds = new Set(students.map(s => s.id));
      const validNew = newStudents.filter(s => !currentIds.has(s.id));
      setStudents([...students, ...validNew]);
      alert(`解析完成，新增 ${validNew.length} 名学生`);
    };
    reader.readAsText(file);
    e.target.value = '';
  };

  const handleDelete = (id) => {
    if(window.confirm('确定删除该学生吗？')) {
      setStudents(students.filter(s => s.id !== id));
    }
  };

  const handleBatchDelete = () => {
    if(selectedIds.length === 0) return;
    if(window.confirm(`确定删除选中的 ${selectedIds.length} 名学生吗？`)) {
      setStudents(students.filter(s => !selectedIds.includes(s.id)));
      setSelectedIds([]);
    }
  };

  const startEditing = (student) => {
    setEditingId(student.id);
    setEditForm(student);
  };

  const saveEdit = () => {
    setStudents(students.map(s => s.id === editingId ? editForm : s));
    setEditingId(null);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ marginBottom: 16 }}>学生名单管理</h2>

      <div style={{ marginBottom: 20 }}>
        <label style={{ display: 'block', marginBottom: 8 }}>批量导入 (CSV/Excel):</label>
        <input type="file" accept=".csv,.xlsx,.xls" onChange={handleFileUpload} />
      </div>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ background: '#2d2d30', textAlign: 'left' }}>
            <th style={{ padding: 12 }}><input type="checkbox" onChange={(e) => {
              setSelectedIds(e.target.checked ? students.map(s => s.id) : []);
            }} /></th>
            <th style={{ padding: 12 }}>学号</th>
            <th style={{ padding: 12 }}>姓名</th>
            <th style={{ padding: 12 }}>班级</th>
            <th style={{ padding: 12 }}>操作</th>
          </tr>
        </thead>
        <tbody>
          {students.map(student => (
            <tr key={student.id} style={{ borderBottom: '1px solid #3e3e42' }}>
              <td style={{ padding: 12 }}><input type="checkbox" checked={selectedIds.includes(student.id)} onChange={(e) => {
                setSelectedIds(e.target.checked ? [...selectedIds, student.id] : selectedIds.filter(id => id !== student.id));
              }} /></td>
              <td style={{ padding: 12 }}>{editingId === student.id ? <input value={editForm.id} onChange={e => setEditForm({...editForm, id: e.target.value})} /> : student.id}</td>
              <td style={{ padding: 12 }}>{editingId === student.id ? <input value={editForm.name} onChange={e => setEditForm({...editForm, name: e.target.value})} /> : student.name}</td>
              <td style={{ padding: 12 }}>{editingId === student.id ? <input value={editForm.class} onChange={e => setEditForm({...editForm, class: e.target.value})} /> : student.class}</td>
              <td style={{ padding: 12 }}>
                {editingId === student.id ? (
                  <>
                    <button onClick={saveEdit} style={{ marginRight: 8 }}>保存</button>
                    <button onClick={() => setEditingId(null)}>取消</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => startEditing(student)} style={{ marginRight: 8 }}>编辑</button>
                    <button onClick={() => handleDelete(student.id)}>删除</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedIds.length > 0 && (
        <div style={{ marginTop: 16 }}>
          <button onClick={handleBatchDelete} style={{ background: '#dc2626', color: '#fff', padding: '8px 16px' }}>批量删除</button>
        </div>
      )}
    </div>
  );
};

// --- 3.2 作业管理 ---
const AssignmentManager = ({ assignments, setAssignments }) => {
  const [newAssign, setNewAssign] = useState({
    title: '', requirements: '', deadline: '', targetClass: '所有班级',
    template: { 'index.html': '<!-- 请在此处编写代码 -->' }
  });

  const handleCreate = () => {
    if (!newAssign.title) return alert('请输入作业标题');
    setAssignments([...assignments, { id: `a${Date.now()}`, ...newAssign }]);
    setNewAssign({ title: '', requirements: '', deadline: '', targetClass: '所有班级', template: { 'index.html': '<!-- 请在此处编写代码 -->' } });
  };

  const handleDelete = (id) => {
    if (window.confirm('确定删除该作业吗？')) {
      setAssignments(assignments.filter(a => a.id !== id));
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ marginBottom: 16 }}>作业管理</h2>
      <div style={{ background: '#2d2d30', padding: 16, borderRadius: 8, marginBottom: 24 }}>
        <h3 style={{ marginBottom: 12 }}>创建新作业</h3>
        <input placeholder="作业标题" value={newAssign.title} onChange={e => setNewAssign({...newAssign, title: e.target.value})} style={{ width: '100%', marginBottom: 12, padding: 8 }} />
        <textarea placeholder="作业要求" value={newAssign.requirements} onChange={e => setNewAssign({...newAssign, requirements: e.target.value})} style={{ width: '100%', height: 80, marginBottom: 12, padding: 8 }} />
        <input type="date" value={newAssign.deadline} onChange={e => setNewAssign({...newAssign, deadline: e.target.value})} style={{ width: '100%', marginBottom: 12, padding: 8 }} />
        <select value={newAssign.targetClass} onChange={e => setNewAssign({...newAssign, targetClass: e.target.value})} style={{ width: '100%', marginBottom: 12, padding: 8 }}>
          <option>所有班级</option>
          <option>三年二班</option>
          <option>三年三班</option>
        </select>
        <button onClick={handleCreate} style={{ background: '#16a34a', color: '#fff', padding: '8px 16px' }}>创建作业</button>
      </div>
      <div>
        {assignments.map(assign => (
          <div key={assign.id} style={{ background: '#2d2d30', padding: 16, borderRadius: 8, marginBottom: 16 }}>
            <h3>{assign.title}</h3>
            <p>要求: {assign.requirements}</p>
            <p>截止时间: {assign.deadline}</p>
            <p>目标班级: {assign.targetClass}</p>
            <button onClick={() => handleDelete(assign.id)} style={{ background: '#dc2626', color: '#fff', padding: '4px 8px' }}>删除</button>
          </div>
        ))}
      </div>
    </div>
  );
};

// --- 3.3 代码库管理 ---
const CodeRepo = ({ codeRepository, setCodeRepository }) => {
  const [newCode, setNewCode] = useState({ title: '', category: 'JavaScript', content: '' });

  const handleCreate = () => {
    if (!newCode.title) return alert('请输入代码标题');
    setCodeRepository([...codeRepository, { id: `c${Date.now()}`, ...newCode }]);
    setNewCode({ title: '', category: 'JavaScript', content: '' });
  };

  const handleDelete = (id) => {
    if (window.confirm('确定删除该代码示例吗？')) {
      setCodeRepository(codeRepository.filter(c => c.id !== id));
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ marginBottom: 16 }}>代码库管理</h2>
      <div style={{ background: '#2d2d30', padding: 16, borderRadius: 8, marginBottom: 24 }}>
        <h3 style={{ marginBottom: 12 }}>添加新代码示例</h3>
        <input placeholder="代码标题" value={newCode.title} onChange={e => setNewCode({...newCode, title: e.target.value})} style={{ width: '100%', marginBottom: 12, padding: 8 }} />
        <select value={newCode.category} onChange={e => setNewCode({...newCode, category: e.target.value})} style={{ width: '100%', marginBottom: 12, padding: 8 }}>
          <option>HTML/CSS</option>
          <option>JavaScript</option>
          <option>React</option>
          <option>算法</option>
        </select>
        <textarea placeholder="代码内容" value={newCode.content} onChange={e => setNewCode({...newCode, content: e.target.value})} style={{ width: '100%', height: 150, marginBottom: 12, padding: 8 }} />
        <button onClick={handleCreate} style={{ background: '#16a34a', color: '#fff', padding: '8px 16px' }}>添加</button>
      </div>
      <div>
        {codeRepository.map(code => (
          <div key={code.id} style={{ background: '#2d2d30', padding: 16, borderRadius: 8, marginBottom: 16 }}>
            <h3>{code.title} ({code.category})</h3>
            <pre style={{ background: '#1e1e1e', padding: 12, borderRadius: 4, overflow: 'auto' }}><code>{code.content}</code></pre>
            <button onClick={() => handleDelete(code.id)} style={{ background: '#dc2626', color: '#fff', padding: '4px 8px' }}>删除</button>
          </div>
        ))}
      </div>
    </div>
  );
};

