// ==========================================
// 5. 学生仪表盘
// ==========================================
const StudentDashboard = ({ user, data, onOpenEditor, onLogout }) => {
  const assignments = data.assignments.filter(a => a.targetClass === '所有班级' || a.targetClass === user.class);

  return (
    <div style={{ height: '100vh', background: '#1e1e1e', color: '#d4d4d4', padding: 32 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 32 }}>
        <h1>欢迎, {user.name} ({user.class})</h1>
        <button onClick={onLogout} style={{ background: '#ef4444', color: '#fff', padding: '8px 16px', border: 'none', borderRadius: 4, cursor: 'pointer' }}>退出登录</button>
      </div>

      <h2 style={{ marginBottom: 16 }}>我的作业</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: 24 }}>
        {assignments.map(assign => {
          const sub = data.submissions.find(s => s.studentId === user.id && s.assignmentId === assign.id);
          return (
            <div key={assign.id} style={{ background: '#2d2d30', padding: 24, borderRadius: 8 }}>
               <h3>{assign.title}</h3>
               <p>{assign.requirements}</p>
               <p>截止: {assign.deadline}</p>
               <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                 <span style={{ color: sub ? '#16a34a' : '#ea580c', fontWeight: 'bold' }}>{sub ? '✅ 已提交' : '⏳ 待完成'}</span>
                 <button 
                   onClick={() => onOpenEditor({
                     mode: 'student_work',
                     initialFiles: sub ? sub.files : assign.template,
                     projectName: assign.title,
                     assignmentId: assign.id
                   })}
                   style={{ padding: '10px 20px', background: sub ? '#0e639c' : '#ea580c', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                 >
                   {sub ? '修改作业' : '开始作业'}
                 </button>
               </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

// ==========================================
// 6. 主入口
// ==========================================
function App() {
  const [data, setData] = useState(INITIAL_DATA);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('login');
  const [editorConfig, setEditorConfig] = useState(null);

  // 处理提交到数据库
  const handleSubmission = async (selectedFiles) => {
    const assign = data.assignments.find(a => a.id === editorConfig.assignmentId);
    if(assign && user.role === 'student') {
      const newSub = {
        id: `sub${Date.now()}`,
        studentId: user.id,
        studentName: user.name,
        assignmentId: assign.id,
        assignmentTitle: assign.title,
        files: selectedFiles,
        timestamp: Date.now()
      };

      // 调用后端API提交到数据库
      try {
        const response = await fetch('/api/submissions', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newSub)
        });

        if (response.ok) {
          // 更新本地状态
          const others = data.submissions.filter(s => !(s.studentId === user.id && s.assignmentId === assign.id));
          setData(prev => ({ ...prev, submissions: [...others, newSub] }));
          alert('✅ 作业提交成功！');
        } else {
          throw new Error('提交失败');
        }
      } catch (error) {
        console.error('提交错误:', error);
        // 即使API失败，也更新本地状态（用于演示）
        const others = data.submissions.filter(s => !(s.studentId === user.id && s.assignmentId === assign.id));
        setData(prev => ({ ...prev, submissions: [...others, newSub] }));
        alert('✅ 作业已保存（本地）');
      }
    }
  };

  return (
    <div>
      {view === 'login' && (
        <LoginPage 
          onLogin={(userData) => {
            setUser(userData);
            setView('dashboard');
          }}
          students={data.students}
        />
      )}

      {view === 'dashboard' && (
        user.role === 'teacher' ? (
          <TeacherDashboard
            data={data}
            setData={setData}
            onLogout={() => { setUser(null); setView('login'); }}
          />
        ) : (
          <StudentDashboard
            user={user}
            data={data}
            onOpenEditor={(config) => { setEditorConfig(config); setView('editor'); }}
            onLogout={() => { setUser(null); setView('login'); }}
          />
        )
      )}

      {view === 'editor' && (
        <FullEditor
          {...editorConfig}
          onBack={() => setView('dashboard')}
          onSubmit={(files) => {
            handleSubmission(files);
            setView('dashboard');
          }}
        />
      )}
    </div>
  );
}

export default App;
