                  {user?.name || 'ÊïôÂ∏à'}
                </div>
                <div style={{ fontSize: '12px', color: '#9ca3af' }}>ÊïôÂ∏àË¥¶Âè∑</div>
              </div>
              <ChevronDown size={16} color="#9ca3af" style={{
                transition: 'transform 0.3s',
                transform: showUserMenu ? 'rotate(180deg)' : 'rotate(0deg)'
              }} />
            </div>

            {/* ‰∏ãÊãâËèúÂçï */}
            {showUserMenu && (
              <div style={{
                position: 'absolute',
                top: '100%',
                right: 0,
                marginTop: '8px',
                background: 'white',
                borderRadius: '8px',
                boxShadow: '0 10px 15px -3px rgba(0,0,0,0.1)',
                minWidth: '200px',
                zIndex: 1000,
                border: '1px solid #e5e7eb'
              }}>
                <div style={{ padding: '8px' }}>
                  <button
                    style={{
                      width: '100%',
                      padding: '10px 12px',
                      background: 'none',
                      border: 'none',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      fontSize: '14px',
                      color: '#374151',
                      transition: 'background 0.3s'
                    }}
                    onMouseEnter={(e) => e.currentTarget.style.background = '#f3f4f6'}
                    onMouseLeave={(e) => e.currentTarget.style.background = 'none'}>
                    <User size={16} /> ‰∏™‰∫∫ËµÑÊñô
                  </button>
                  <button
                    style={{
                      width: '100%',
                      padding: '10px 12px',
                      background: 'none',
                      border: 'none',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      fontSize: '14px',
                      color: '#374151',
                      transition: 'background 0.3s'
                    }}
                    onMouseEnter={(e) => e.currentTarget.style.background = '#f3f4f6'}
                    onMouseLeave={(e) => e.currentTarget.style.background = 'none'}>
                    <Settings size={16} /> Á≥ªÁªüËÆæÁΩÆ
                  </button>
                  <div style={{ height: '1px', background: '#e5e7eb', margin: '8px 0' }} />
                  <button
                    onClick={onLogout}
                    style={{
                      width: '100%',
                      padding: '10px 12px',
                      background: 'none',
                      border: 'none',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      fontSize: '14px',
                      color: '#dc2626',
                      transition: 'background 0.3s'
                    }}
                    onMouseEnter={(e) => e.currentTarget.style.background = '#fee2e2'}
                    onMouseLeave={(e) => e.currentTarget.style.background = 'none'}>
                    <LogOutIcon size={16} /> ÈÄÄÂá∫ÁôªÂΩï
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* ÂÜÖÂÆπÂå∫Âüü */}
        <div style={{ flex: 1, padding: '30px', overflow: 'auto' }}>

        {activeTab === 'students' && (
          <StudentManager 
            students={data.students} 
            setStudents={newS => setData({...data, students: newS})} 
          />
        )}

        {activeTab === 'repository' && (
          <CodeRepository 
            repo={data.codeRepository || []} 
            setRepo={newR => setData({...data, codeRepository: newR})}
            onOpenEditor={onOpenEditor}
          />
        )}

        {activeTab === 'assignments' && (
          <AssignmentManager 
            assignments={data.assignments} 
            setAssignments={newA => setData({...data, assignments: newA})}
            repo={data.codeRepository || []}
            students={data.students}
          />
        )}

        {activeTab === 'submissions' && (
          <SubmissionReview 
            submissions={data.submissions}
            students={data.students}
            assignments={data.assignments}
            onOpenEditor={onOpenEditor}
            onScoreUpdate={(submissionId, score, comment) => {
              setData({
                ...data,
                submissions: data.submissions.map(s => 
                  s.id === submissionId 
                    ? { ...s, score, comment, reviewed: true, reviewedAt: new Date() }
                    : s
                )
              });
            }}
          />
        )}

        {activeTab === 'qa' && (
          <QAManagement 
            students={data.students}
          />
        )}
        </div>
      </div>
    </div>
  );
}

export default TeacherDashboard;
