  Upload, Download, X
} from 'lucide-react';
import * as XLSX from 'xlsx';
import FullEditor from './FullEditor';

// ==========================================
// å…¨å±€æ ·å¼
// ==========================================
const globalStyles = `
  @keyframes spin {
    to { transform: rotate(360deg); }
  }
  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
  }
  @keyframes slideIn {
    from { opacity: 0; transform: translateX(-20px); }
    to { opacity: 1; transform: translateX(0); }
  }
  .animate-fade-in {
    animation: fadeIn 0.3s ease-out forwards;
  }
  .card-hover {
    transition: all 0.3s ease;
  }
  .card-hover:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 40px rgba(0,0,0,0.15);
  }
`;

// ==========================================
// æ¨¡æ‹Ÿæ•°æ®
// ==========================================
const INITIAL_DATA = {
  students: [],
  assignments: [
    { 
      id: 'a1', 
      title: 'ä¸ªäººä¸»é¡µåˆ¶ä½œ', 
      description: 'ä½¿ç”¨HTMLå’ŒCSSåˆ¶ä½œä¸€ä¸ªä¸ªäººä»‹ç»é¡µé¢',
      requirements: 'åŒ…å«å¤´åƒã€å§“åå’Œä¸€æ®µè‡ªæˆ‘ä»‹ç»ï¼ŒèƒŒæ™¯è‰²éœ€ä¸ºæµ…ç°è‰²ã€‚',
      deadline: '2025-12-31',
