      template: { 'index.html': '<!DOCTYPE html>\n<html>\n<head>\n  <title>æˆ‘çš„ä¸»é¡µ</title>\n</head>\n<body>\n  <!-- åœ¨è¿™é‡Œç¼–å†™ä½ çš„ä»£ç  -->\n</body>\n</html>' }
    },
    { 
      id: 'a2', 
      title: 'JavaScriptè®¡ç®—å™¨', 
      description: 'å®ç°ä¸€ä¸ªç®€å•çš„å››åˆ™è¿ç®—è®¡ç®—å™¨',
      requirements: 'æ”¯æŒåŠ å‡ä¹˜é™¤è¿ç®—ï¼Œæœ‰æ¸…é™¤åŠŸèƒ½ã€‚',
      deadline: '2025-12-25',
      targetClass: 'ä¸‰å¹´äºŒç­',
      status: 'active',
      createdAt: '2025-01-10',
      template: { 'index.html': '<!-- è®¡ç®—å™¨æ¨¡æ¿ -->', 'script.js': '// åœ¨è¿™é‡Œç¼–å†™JSä»£ç ' }
    }
  ],
  submissions: [],
