              <div key={assign.id} className="card-hover" style={{
                background: '#fff', borderRadius: '16px', overflow: 'hidden',
                boxShadow: '0 2px 12px rgba(0,0,0,0.04)'
              }}>
                <div style={{
                  height: '8px',
                  background: isCompleted 
                    ? 'linear-gradient(90deg, #52c41a, #73d13d)'
                    : isOverdue 
                      ? 'linear-gradient(90deg, #f5222d, #ff4d4f)'
                      : 'linear-gradient(90deg, #667eea, #764ba2)'
                }} />
                <div style={{ padding: '24px' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '12px' }}>
                    <h3 style={{ fontSize: '18px', fontWeight: '600', color: '#1a1a2e', margin: 0 }}>
                      {assign.title}
                    </h3>
                    {isCompleted ? (
                      <span style={{
                        padding: '4px 12px', background: '#f6ffed',
                        color: '#389e0d', borderRadius: '20px', fontSize: '12px',
                        display: 'flex', alignItems: 'center', gap: '4px'
                      }}>
                        <CheckCircle size={12} /> å·²å®Œæˆ
                      </span>
                    ) : isOverdue ? (
                      <span style={{
                        padding: '4px 12px', background: '#fff1f0',
                        color: '#cf1322', borderRadius: '20px', fontSize: '12px'
                      }}>
                        å·²æˆªæ­¢
                      </span>
                    ) : (
                      <span style={{
                        padding: '4px 12px', background: '#e6f7ff',
                        color: '#1890ff', borderRadius: '20px', fontSize: '12px'
                      }}>
                        è¿›è¡Œä¸­
                      </span>
                    )}
                  </div>

                  <p style={{ color: '#666', fontSize: '14px', margin: '0 0 16px', lineHeight: 1.6 }}>
                    {assign.description}
                  </p>

                  <div style={{
                    display: 'flex', alignItems: 'center', gap: '8px',
                    fontSize: '13px', color: '#888', marginBottom: '20px'
                  }}>
                    <Calendar size={14} />
                    <span>æˆªæ­¢æ—¥æœŸï¼š{assign.deadline}</span>
                  </div>

                  <button onClick={() => onOpenEditor({
                    mode: 'student_work',
                    initialFiles: submission ? submission.files : assign.template,
                    projectName: assign.title,
                    assignmentId: assign.id
                  })}
                    style={{
                      width: '100%', padding: '14px',
                      background: isCompleted 
                        ? '#f5f5f5' 
                        : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                      border: 'none', borderRadius: '12px',
                      color: isCompleted ? '#666' : '#fff',
                      fontWeight: '500', cursor: 'pointer',
                      display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px'
                    }}>
                    {isCompleted ? (
                      <><Edit2 size={16} /> æŸ¥çœ‹/ä¿®æ”¹</>
                    ) : (
                      <><Code size={16} /> å¼€å§‹ä½œä¸š</>
                    )}
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </main>
    </div>
  );
};

// ==========================================
// ä¸»åº”ç”¨å…¥å£
// ==========================================
function App() {
  const [data, setData] = useState(INITIAL_DATA);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('login');
  const [editorConfig, setEditorConfig] = useState(null);

  const handleSubmission = async (selectedFiles) => {
    const assign = data.assignments.find(a => a.id === editorConfig?.assignmentId);
    if (assign && user?.role === 'student') {
      const newSub = {
        id: `sub${Date.now()}`,
        studentId: user.id,
        studentName: user.name,
        assignmentId: assign.id,
        assignmentTitle: assign.title,
        files: selectedFiles,
        timestamp: Date.now()
      };

      try {
        await fetch('http://localhost:5000/api/submissions', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newSub)
        });
      } catch (e) {
        console.log('APIè°ƒç”¨å¤±è´¥ï¼Œä½¿ç”¨æœ¬åœ°å­˜å‚¨');
      }

      const others = data.submissions.filter(s => !(s.studentId === user.id && s.assignmentId === assign.id));
      setData(prev => ({ ...prev, submissions: [...others, newSub] }));
      alert('âœ… ä½œä¸šæäº¤æˆåŠŸï¼');
    }
  };

  const handleLogin = (userData) => {
    setUser(userData);
    setView('dashboard');
  };

  const handleLogout = () => {
    setUser(null);
    setView('login');
  };

  const handleOpenEditor = (config) => {
    setEditorConfig(config);
    setView('editor');
  };

  const handleBackFromEditor = () => {
    setView('dashboard');
  };

  const handleSubmitFromEditor = (files) => {
    handleSubmission(files);
    setView('dashboard');
  };

  // æ ¹æ®å½“å‰è§†å›¾æ¸²æŸ“å¯¹åº”ç»„ä»¶
  const renderView = () => {
    if (view === 'login') {
      return (
        <LoginPage 
          key="login"
          onLogin={handleLogin}
          students={data.students}
        />
      );
    }

    if (view === 'editor' && editorConfig) {
      return (
        <FullEditor
          key="editor"
          {...editorConfig}
          onBack={handleBackFromEditor}
          onSubmit={handleSubmitFromEditor}
        />
      );
    }

    if (view === 'dashboard' && user) {
      if (user.role === 'teacher') {
        return (
          <TeacherDashboard
            key="teacher-dashboard"
            data={data}
            setData={setData}
            user={user}
            onOpenEditor={handleOpenEditor}
            onLogout={handleLogout}
          />
        );
      }

      if (user.role === 'student') {
        return (
          <StudentDashboard
            key="student-dashboard"
            user={user}
            data={data}
            onOpenEditor={handleOpenEditor}
            onLogout={handleLogout}
          />
        );
      }
    }

    // é»˜è®¤è¿”å›ç™»å½•é¡µ
    return (
      <LoginPage 
        key="login-default"
        onLogin={handleLogin}
        students={data.students}
      />
    );
  };

  return (
    <div style={{ minHeight: '100vh' }}>
      <style>{globalStyles}</style>
      {renderView()}
    </div>
  );
}

export default App;
